import * as vscode from 'vscode';
import axios from 'axios';
import * as fs from 'fs-extra';
import * as path from 'path';

export async function setApiKey(context: vscode.ExtensionContext) {
    const apiKey = await vscode.window.showInputBox({
        title: 'DesignSys API Key',
        prompt: 'Enter your DesignSys API Key (start with ds_live_)',
        password: true,
        ignoreFocusOut: true
    });

    if (!apiKey) {
        return;
    }

    try {
        await context.secrets.store('designsys.apiKey', apiKey);
        vscode.window.showInformationMessage('DesignSys API Key saved successfully!');
    } catch (error) {
        vscode.window.showErrorMessage('Failed to save API Key.');
    }
}

import { DesignToken } from '@designsys/shared';

// Helper to unflatten tokens
function unflattenTokens(tokens: DesignToken[]) {
    const root: any = {};
    tokens.forEach(token => {
        // Strip initial "ds." if present for cleaner hierarchy, or keep it.
        // Paths are like "colors.primary.500".
        const parts = token.path.split('.');
        let current = root;
        parts.forEach((part, index) => {
            if (index === parts.length - 1) {
                current[part] = token.value; // Leaf node
            } else {
                current[part] = current[part] || {};
                current = current[part];
            }
        });
    });
    return root;
}

export async function pullDesignSystem(context: vscode.ExtensionContext) {
    // 1. Check workspace
    if (!vscode.workspace.workspaceFolders || vscode.workspace.workspaceFolders.length === 0) {
        vscode.window.showErrorMessage('Please open a folder or workspace first.');
        return;
    }
    const rootPath = vscode.workspace.workspaceFolders[0].uri.fsPath;

    // 2. Get API Key
    const apiKey = await context.secrets.get('designsys.apiKey');
    if (!apiKey) {
        const result = await vscode.window.showErrorMessage('API Key not found. Set it now?', 'Yes', 'No');
        if (result === 'Yes') {
            await setApiKey(context);
        }
        return;
    }

    // 3. API Call
    let apiUrl = vscode.workspace.getConfiguration('designsys').get<string>('apiUrl');
    if (!apiUrl || apiUrl.includes('[YOUR_PROJECT_ID]')) {
        apiUrl = await vscode.window.showInputBox({
            prompt: 'Enter your DesignSys API URL (e.g., https://<project>.functions.supabase.co/v1-api/v1/tokens)',
            value: apiUrl,
            ignoreFocusOut: true
        });
        if (apiUrl) {
            await vscode.workspace.getConfiguration('designsys').update('apiUrl', apiUrl, vscode.ConfigurationTarget.Global);
        } else {
            return;
        }
    }

    if (!apiUrl) return;

    vscode.window.withProgress({
        location: vscode.ProgressLocation.Notification,
        title: "DesignSys: Pulling Tokens...",
        cancellable: false
    }, async (_progress) => {
        try {
            // Fix: remove /generate if present, ensure we hit /tokens
            // User might enter base URL. Let's try to be smart or just trust them.
            // Assumption: User provides the full endpoint or we construct it.
            // Let's assume standard endpoint for now.

            const response = await axios.get(apiUrl!, {
                headers: { 'x-api-key': apiKey }
            });

            if (response.data.data) {
                const tokens: DesignToken[] = response.data.data;
                const tokenTree = unflattenTokens(tokens);

                // 4. Save Tokens
                const tokensDir = path.join(rootPath, '.designsys');
                fs.ensureDirSync(tokensDir);
                fs.writeFileSync(path.join(tokensDir, 'tokens.json'), JSON.stringify(tokenTree, null, 2));

                vscode.window.showInformationMessage(`Successfully pulled ${tokens.length} tokens to .designsys/tokens.json`);
            } else {
                throw new Error('Invalid response format: data property missing');
            }
        } catch (apiError: any) {
            console.error(apiError);
            const msg = apiError.response?.data?.error || apiError.message;
            vscode.window.showErrorMessage(`Pull Failed: ${msg}`);
        }
    });

}

import * as vscode from 'vscode';
import { setApiKey, pullDesignSystem } from './commands';
import { DesignSysCompletionProvider } from './providers/completion';
import { DesignSysHoverProvider } from './providers/hover';

export function activate(context: vscode.ExtensionContext) {
    console.log('DesignSys Extension is now active!');

    // 1. Register Commands
    let setKeyCmd = vscode.commands.registerCommand('designsys.setApiKey', async () => {
        await setApiKey(context);
    });

    let pullCmd = vscode.commands.registerCommand('designsys.pull', async () => {
        await pullDesignSystem(context);
    });

    // 2. Register Providers
    const completionProvider = vscode.languages.registerCompletionItemProvider(
        ['typescript', 'javascript', 'javascriptreact', 'typescriptreact', 'css', 'scss', 'json'],
        new DesignSysCompletionProvider(),
        '.' // Trigger on dot
    );

    const hoverProvider = vscode.languages.registerHoverProvider(
        ['typescript', 'javascript', 'javascriptreact', 'typescriptreact', 'css', 'scss', 'json'],
        new DesignSysHoverProvider()
    );

    context.subscriptions.push(setKeyCmd);
    context.subscriptions.push(pullCmd);
    context.subscriptions.push(completionProvider);
    context.subscriptions.push(hoverProvider);
}

export function deactivate() { }

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';

export class DesignSysCompletionProvider implements vscode.CompletionItemProvider {
    provideCompletionItems(
        document: vscode.TextDocument,
        position: vscode.Position
    ): vscode.ProviderResult<vscode.CompletionItem[] | vscode.CompletionList> {

        const linePrefix = document.lineAt(position).text.substr(0, position.character);
        if (!linePrefix.includes('ds.')) {
            return undefined;
        }

        const workspaceFolder = vscode.workspace.getWorkspaceFolder(document.uri);
        if (!workspaceFolder) return undefined;

        const tokensPath = path.join(workspaceFolder.uri.fsPath, '.designsys', 'tokens.json');
        if (!fs.existsSync(tokensPath)) return undefined;

        try {
            const tokens = JSON.parse(fs.readFileSync(tokensPath, 'utf8'));

            // Extract the part after "ds."
            // e.g. "const c = ds.colors.prim" -> prefix match "ds.colors."
            const regex = /ds\.([\w\.]*)$/;
            const match = linePrefix.match(regex);

            if (!match) return undefined;

            const typedPath = match[1]; // e.g. "colors." or "colors.prim" or ""

            // Should valid suggestions require a dot at the end?
            // If I type "ds.co", I want "colors".
            // If I type "ds.colors.", I want "primary".

            // Algorithm:
            // 1. Split typedPath by dot.
            // 2. Traverse token tree.
            // 3. If last part is partial, Suggest keys of the parent matching partial.
            //    If last part is empty (dot ending), Suggest all keys of current node.

            const parts = typedPath.split('.');
            // parts:
            // "ds." -> [""] 
            // "ds.co" -> ["co"]
            // "ds.colors." -> ["colors", ""]
            // "ds.colors.pr" -> ["colors", "pr"]

            let current: any = tokens;
            // Traverse up to the second to last part
            for (let i = 0; i < parts.length - 1; i++) {
                const part = parts[i];
                if (current[part] && typeof current[part] === 'object') {
                    current = current[part];
                } else {
                    return undefined; // Path not found
                }
            }

            const lastPart = parts[parts.length - 1];

            // "current" is now the object we want to pick from.
            // "lastPart" is the filter.

            if (typeof current !== 'object' || current === null) return undefined;

            return Object.keys(current).map(key => {
                if (key.startsWith(lastPart)) {
                    const val = current[key];
                    const kind = typeof val === 'object' ? vscode.CompletionItemKind.Module : vscode.CompletionItemKind.Color;
                    const item = new vscode.CompletionItem(key, kind);

                    if (typeof val !== 'object') {
                        item.detail = String(val);
                        // Add color preview if it looks like a color
                        if (String(val).startsWith('#') || String(val).startsWith('rgb') || String(val).startsWith('hsl')) {
                            item.documentation = new vscode.MarkdownString(`Preview: <span style="color:${val};background-color:${val};width:20px;height:20px;display:inline-block;border:1px solid #777;"></span> \`${val}\``);
                        }
                    } else {
                        item.detail = "(Group)";
                    }
                    return item;
                }
                return undefined;
            }).filter((item): item is vscode.CompletionItem => item !== undefined);

        } catch (e) {
            console.error(e);
        }
        return undefined;
    }
}

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';

export class DesignSysHoverProvider implements vscode.HoverProvider {
    provideHover(document: vscode.TextDocument, position: vscode.Position): vscode.ProviderResult<vscode.Hover> {
        // Match any sequence starting with ds. and containing alphanumeric, dots, or underscores
        const range = document.getWordRangeAtPosition(position, /ds\.[\w\.]+/);
        if (!range) return undefined;

        const word = document.getText(range); // e.g., ds.colors.primary.500

        const workspaceFolder = vscode.workspace.getWorkspaceFolder(document.uri);
        if (!workspaceFolder) return undefined;

        const tokensPath = path.join(workspaceFolder.uri.fsPath, '.designsys', 'tokens.json');
        if (!fs.existsSync(tokensPath)) return undefined;

        try {
            const tokens = JSON.parse(fs.readFileSync(tokensPath, 'utf8'));
            const pathParts = word.replace('ds.', '').split('.'); // [colors, primary, 500]

            // Traverse safely
            let value = tokens;
            for (const part of pathParts) {
                if (value && typeof value === 'object') {
                    value = value[part];
                } else {
                    value = undefined;
                    break;
                }
            }

            if (value !== undefined && (typeof value === 'string' || typeof value === 'number')) {
                const markdown = new vscode.MarkdownString();
                markdown.appendMarkdown(`**${word}**\n\n`);
                markdown.appendMarkdown(`\`${value}\` `);

                // Create a color preview square if it looks like a color
                const strVal = String(value);
                if (strVal.startsWith('#') || strVal.startsWith('rgb') || strVal.startsWith('hsl')) {
                    markdown.appendMarkdown(`<span style="background-color:${strVal};color:${strVal};border:1px solid #777;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>`);
                    markdown.supportHtml = true;
                }

                return new vscode.Hover(markdown);
            }
        } catch (e) {
            console.error(e);
        }
        return undefined;
    }
}

export type TokenType = 'color' | 'dimension' | 'fontFamily' | 'fontWeight' | 'fontSize' | 'lineHeight' | 'letterSpacing' | 'paragraph' | 'textCase' | 'textDecoration' | 'number' | 'percentage' | 'duration' | 'cubicBezier' | 'spacing' | 'borderRadius' | 'borderWidth' | 'shadow' | 'gradient' | 'typography' | 'composition' | 'asset' | 'border';
export type TokenStatus = 'draft' | 'published' | 'deprecated' | 'archived';
export interface BaseToken {
    id?: string;
    name: string;
    path: string;
    type: TokenType;
    description?: string;
    ref?: string;
    extensions?: Record<string, any>;
    status?: TokenStatus;
    stagingValue?: any;
    publishedValue?: any;
    syncStatus?: 'synced' | 'changed';
}
export interface ColorToken extends BaseToken {
    type: 'color';
    value: string;
}
export interface GradientToken extends BaseToken {
    type: 'gradient';
    value: {
        type: 'linear' | 'radial' | 'conic';
        angle?: number;
        stops: Array<{
            color: string;
            position: number;
        }>;
    };
}
export interface DimensionToken extends BaseToken {
    type: 'dimension' | 'spacing' | 'borderRadius' | 'borderWidth' | 'fontSize' | 'lineHeight' | 'letterSpacing' | 'paragraph';
    value: string;
}
export interface NumberToken extends BaseToken {
    type: 'number' | 'fontWeight';
    value: number | string;
}
export interface PercentageToken extends BaseToken {
    type: 'percentage';
    value: string;
}
export interface TypographyToken extends BaseToken {
    type: 'typography';
    value: {
        fontFamily: string;
        fontSize: string;
        fontWeight: number | string;
        lineHeight: string | number;
        letterSpacing?: string;
        textCase?: 'none' | 'uppercase' | 'lowercase' | 'capitalize';
        textDecoration?: 'none' | 'underline' | 'line-through';
    };
}
export interface ShadowToken extends BaseToken {
    type: 'shadow';
    value: {
        color: string;
        x: string;
        y: string;
        blur: string;
        spread: string;
        type?: 'inner' | 'drop';
    } | Array<{
        color: string;
        x: string;
        y: string;
        blur: string;
        spread: string;
        type?: 'inner' | 'drop';
    }>;
}
export interface CubicBezierToken extends BaseToken {
    type: 'cubicBezier';
    value: [number, number, number, number];
}
export interface DurationToken extends BaseToken {
    type: 'duration';
    value: string;
}
export interface CompositionToken extends BaseToken {
    type: 'composition';
    value: Record<string, string>;
}
export interface AssetToken extends BaseToken {
    type: 'asset';
    value: string;
}
export interface BorderToken extends BaseToken {
    type: 'border';
    value: {
        color: string;
        width: string;
        style: 'solid' | 'dashed' | 'dotted';
    };
}
export type DesignToken = ColorToken | GradientToken | DimensionToken | NumberToken | PercentageToken | TypographyToken | ShadowToken | CubicBezierToken | DurationToken | CompositionToken | AssetToken | BorderToken;
export type TokenMap = Record<string, DesignToken>;
export interface TokenGroup {
    id: string;
    name: string;
    path: string;
    description?: string;
    parentId?: string;
    tokens: string[];
    groups: string[];
}
export type TokenCollectionType = 'foundation' | 'semantic' | 'component';
export interface TokenCollection {
    id: string;
    name: string;
    type: TokenCollectionType;
    description?: string;
    groups: string[];
}
export interface UnifiedTokenStore {
    collections: Record<string, TokenCollection>;
    groups: Record<string, TokenGroup>;
    tokens: Record<string, DesignToken>;
}

// Shared Token Types

export type TokenType =
    | 'color' | 'dimension' | 'fontFamily' | 'fontWeight'
    | 'fontSize' | 'lineHeight' | 'letterSpacing' | 'paragraph'
    | 'textCase' | 'textDecoration' | 'number' | 'percentage'
    | 'duration' | 'cubicBezier' | 'spacing' | 'borderRadius'
    | 'borderWidth' | 'shadow' | 'gradient' | 'typography'
    | 'composition' | 'asset' | 'border';

export type TokenStatus = 'draft' | 'published' | 'deprecated' | 'archived';

export interface BaseToken {
    id?: string;
    name: string;
    path: string;
    type: TokenType;
    description?: string;
    ref?: string; // Standard reference syntax: {namespace.path.to.token}
    extensions?: Record<string, any>;
    status?: TokenStatus;
    stagingValue?: any; // The draft value currently being edited
    publishedValue?: any; // The currently active/live value (if different from staging)
    syncStatus?: 'synced' | 'changed';
}

export interface ColorToken extends BaseToken {
    type: 'color';
    value: string; // HSL, RGB, or HEX
}

export interface GradientToken extends BaseToken {
    type: 'gradient';
    value: {
        type: 'linear' | 'radial' | 'conic';
        angle?: number;
        stops: Array<{ color: string; position: number }>;
    };
}

export interface DimensionToken extends BaseToken {
    type: 'dimension' | 'spacing' | 'borderRadius' | 'borderWidth' | 'fontSize' | 'lineHeight' | 'letterSpacing' | 'paragraph';
    value: string; // e.g., "16px", "1rem"
}

export interface NumberToken extends BaseToken {
    type: 'number' | 'fontWeight';
    value: number | string; // Allows for references like "{fontWeights.bold}"
}

export interface PercentageToken extends BaseToken {
    type: 'percentage';
    value: string; // e.g. "50%"
}

export interface TypographyToken extends BaseToken {
    type: 'typography';
    value: {
        fontFamily: string;
        fontSize: string;
        fontWeight: number | string;
        lineHeight: string | number;
        letterSpacing?: string;
        textCase?: 'none' | 'uppercase' | 'lowercase' | 'capitalize';
        textDecoration?: 'none' | 'underline' | 'line-through';
    };
}

export interface ShadowToken extends BaseToken {
    type: 'shadow';
    value: {
        color: string;
        x: string;
        y: string;
        blur: string;
        spread: string;
        type?: 'inner' | 'drop';
    } | Array<{
        color: string;
        x: string;
        y: string;
        blur: string;
        spread: string;
        type?: 'inner' | 'drop';
    }>;
}

export interface CubicBezierToken extends BaseToken {
    type: 'cubicBezier';
    value: [number, number, number, number];
}

export interface DurationToken extends BaseToken {
    type: 'duration';
    value: string; // e.g., "300ms"
}

export interface CompositionToken extends BaseToken {
    type: 'composition';
    value: Record<string, string>; // References to other tokens (paths)
}

export interface AssetToken extends BaseToken {
    type: 'asset';
    value: string; // URL or path to asset
}

export interface BorderToken extends BaseToken {
    type: 'border';
    value: {
        color: string;
        width: string;
        style: 'solid' | 'dashed' | 'dotted';
    };
}

export type DesignToken =
    | ColorToken
    | GradientToken
    | DimensionToken
    | NumberToken
    | PercentageToken
    | TypographyToken
    | ShadowToken
    | CubicBezierToken
    | DurationToken
    | CompositionToken
    | AssetToken
    | BorderToken;

export type TokenMap = Record<string, DesignToken>;

export interface TokenGroup {
    id: string;
    name: string;
    path: string;
    description?: string;
    parentId?: string;
    tokens: string[]; // List of token paths
    groups: string[]; // List of subgroup IDs
}

export type TokenCollectionType = 'foundation' | 'semantic' | 'component';

export interface TokenCollection {
    id: string;
    name: string;
    type: TokenCollectionType;
    description?: string;
    groups: string[]; // Root IDs of TokenGroups
}

export interface UnifiedTokenStore {
    collections: Record<string, TokenCollection>;
    groups: Record<string, TokenGroup>;
    tokens: Record<string, DesignToken>;
}

export interface DesignToken {
    id: string;
    name: string;
    value: string | number;
    type: string;
    description?: string;
    meta?: Record<string, any>;
}

export interface ColorPalette {
    primary: string;
    secondary: string;
    accent: string;
    background: string;
    surface: string;
    text: string;
    error: string;
    success: string;
    warning: string;
    info: string;
    [key: string]: string;
}

export interface TypographyScale {
    baseSize: number;
    ratio: number;
    sizes: Record<string, string>;
}

export interface FontFamily {
    heading: string;
    body: string;
    mono: string;
}

export interface DesignSystem {
    name: string;
    version: string;
    colors: ColorPalette;
    typography: {
        fonts: FontFamily;
        scale: TypographyScale;
    };
    spacing: Record<string, string>;
    breakpoints: Record<string, string>;
    tokens: DesignToken[];
    meta: {
        industry?: string;
        mood?: string[];
        createdAt: string;
        updatedAt: string;
    };
}

export interface ThemeConfig {
    mode: 'light' | 'dark' | 'system';
    overrides?: Partial<DesignSystem>;
}

/**
 * Base interface for all UI UX Pro Max patterns
 */
export interface Pattern<T = unknown> {
    /** Unique identifier for the pattern */
    id: string;

    /** Human-readable name */
    name: string;

    /** Pattern category */
    category: PatternCategory;

    /** Tags for searchability */
    tags: string[];

    /** Description of the pattern */
    description: string;

    /** Industries this pattern is suitable for */
    industries: string[];

    /** Mood keywords associated with this pattern */
    mood: string[];

    /** Validate if pattern matches given criteria */
    matches(criteria: PatternCriteria): boolean;

    /** Generate output based on input parameters */
    generate(input: PatternInput): T;

    /** Get rendered component representation (optional as it might be frontend specific) */
    render?(props: PatternProps): any;
}

/**
 * Pattern categories
 */
export type PatternCategory = 'style' | 'color' | 'typography' | 'layout' | 'interaction' | 'component';

/**
 * Pattern input criteria for matching
 */
export interface PatternCriteria {
    industry?: string;
    productType?: string;
    brandMood?: string[];
    targetPlatform?: 'web' | 'mobile' | 'desktop';
    accessibilityLevel?: 'A' | 'AA' | 'AAA';
    complexity?: 'low' | 'medium' | 'high';
}

/**
 * Generic input for pattern generation
 */
export interface PatternInput {
    [key: string]: any;
}

/**
 * Generic props for pattern rendering
 */
export interface PatternProps {
    [key: string]: any;
}

/**
 * Pattern output types
 */
export interface PatternOutput {
    /** Generated tokens */
    tokens: any[]; // refine type later

    /** CSS variables to define */
    cssVariables: Record<string, string>;

    /** Tailwind config additions */
    tailwindConfig: Record<string, unknown>;

    /** Generated color shades */
    shades?: Record<string, any[]>;

    /** Code snippets */
    codeSnippets: CodeSnippet[];

    /** Implementation checklist */
    checklist: string[];
}

/**
 * Code snippet for component implementation
 */
export interface CodeSnippet {
    language: 'tsx' | 'css' | 'scss' | 'tailwind';
    framework: 'react' | 'vue' | 'svelte' | 'vanilla';
    code: string;
}

/**
 * Base interface for all skills
 */
export interface Skill<TInput = unknown, TOutput = unknown> {
    /** Unique skill identifier */
    id: string;

    /** Human-readable name */
    name: string;

    /** Skill category */
    category: SkillCategory;

    /** Version of the skill */
    version: string;

    /** Description of what the skill does */
    description: string;

    /** Parameters this skill accepts */
    parameters: SkillParameter[];

    /** Output schema */
    outputSchema: SkillOutputSchema;

    /** Dependencies on other skills */
    dependencies: SkillDependency[];

    /** Whether this skill requires AI */
    requiresAI: boolean;

    /** Whether this skill can run offline */
    canRunOffline: boolean;

    /** Cache configuration */
    cacheConfig?: SkillCacheConfig;

    /** Validate input parameters */
    validate(input: TInput): ValidationResult;

    /** Execute the skill */
    execute(
        input: TInput,
        context: SkillContext
    ): Promise<TOutput>;

    /** Get estimated cost (for AI-based skills) */
    estimateCost(input: TInput): CostEstimate;
}

/**
 * Skill categories
 */
export type SkillCategory =
    | 'color'
    | 'typography'
    | 'layout'
    | 'style'
    | 'interaction'
    | 'accessibility'
    | 'animation'
    | 'component'
    | 'analysis';

/**
 * Skill parameter definition
 */
export interface SkillParameter {
    name: string;
    type: 'string' | 'number' | 'boolean' | 'array' | 'object';
    required: boolean;
    description: string;
    defaultValue?: unknown;
    validation?: ParameterValidation;
}

export interface ParameterValidation {
    min?: number;
    max?: number;
    options?: any[];
    regex?: string;
    custom?: (value: any) => boolean;
}

/**
 * Skill output schema
 */
/**
 * Skill output schema
 */
export interface SkillOutputSchema {
    type: 'object' | 'array' | 'string' | 'number' | 'boolean';
    properties?: Record<string, SkillOutputSchema>;
    items?: SkillOutputSchema;
    description?: string;
    format?: string;
    enum?: any[];
}

/**
 * Skill dependency definition
 */
export interface SkillDependency {
    skillId: string;
    minVersion: string;
    optional: boolean;
    reason: string;
}

export interface SkillContext {
    executionId?: string;
    timeout?: number;
    logger?: any;
    messageBus?: any;
    [key: string]: any;
}

export interface ValidationResult {
    valid: boolean;
    errors: string[];
}

export interface CostEstimate {
    tokens: number;
    usd: number;
}

export interface SkillResult<T = unknown> {
    skillId: string;
    executionId: string;
    status: 'success' | 'error' | 'cancelled';
    output?: T;
    error?: {
        message: string;
        code: string;
        recoverable: boolean;
        details?: unknown;
    };
    duration: number;
    timestamp: Date;
    cached?: boolean;
    metrics?: {
        tokens?: number;
        cost?: number;
    };
}

/**
 * Skill cache configuration
 */
export interface SkillCacheConfig {
    ttlMs: number;
    persist: 'memory' | 'localStorage';
}

import { SkillContext, SkillResult } from '@designsys/ui-ux-core';
import { SkillRegistry } from './skill-registry';
import { SkillStore } from '../state/skill-store';

export class SkillExecutor {
    private registry: SkillRegistry;
    private store: SkillStore;

    constructor(store: SkillStore) {
        this.store = store;
        this.registry = new SkillRegistry(store);
    }

    // Method to access registry for registering skills
    getRegistry(): SkillRegistry {
        return this.registry;
    }

    async execute<TInput, TOutput>(
        skillId: string,
        input: TInput,
        context: Partial<SkillContext> = {}
    ): Promise<SkillResult<TOutput>> {
        const startTime = Date.now();
        const executionId = context.executionId || `exec-${Math.random().toString(36).substr(2, 9)}`;

        const skill = this.registry.get(skillId);

        if (!skill) {
            return this.createErrorResult(skillId, executionId, 'SKILL_NOT_FOUND', `Skill ${skillId} not found`, startTime);
        }

        try {
            // 1. Validate Input
            const validation = skill.validate(input);
            if (!validation.valid) {
                return this.createErrorResult(
                    skillId,
                    executionId,
                    'INVALID_INPUT',
                    `Invalid input: ${validation.errors.join(', ')}`,
                    startTime
                );
            }

            // 2. Execute
            const fullContext: SkillContext = {
                executionId,
                timestamp: new Date(),
                ...context
            };

            // Check Cache
            if (skill.cacheConfig) {
                const cacheKey = this.generateCacheKey(skillId, input);
                const cachedOutput = this.store.getFromCache<TOutput>(cacheKey, skill.cacheConfig.persist);

                if (cachedOutput) {
                    const duration = Date.now() - startTime;
                    const result: SkillResult<TOutput> = {
                        skillId,
                        executionId,
                        status: 'success',
                        output: cachedOutput,
                        duration,
                        timestamp: new Date(),
                        cached: true
                    };
                    this.store.saveExecutionResult(result);
                    return result;
                }
            }

            const output = await skill.execute(input, fullContext) as TOutput;
            const duration = Date.now() - startTime;

            const result: SkillResult<TOutput> = {
                skillId,
                executionId,
                status: 'success',
                output,
                duration,
                timestamp: new Date()
            };

            // Save to Cache
            if (skill.cacheConfig) {
                const cacheKey = this.generateCacheKey(skillId, input);
                this.store.saveToCache(cacheKey, output, skill.cacheConfig.ttlMs, skill.cacheConfig.persist);
            }

            // 3. Store History
            this.store.saveExecutionResult(result);

            return result;

        } catch (error) {
            console.error(`Error executing skill ${skillId}:`, error);
            return this.createErrorResult(
                skillId,
                executionId,
                'EXECUTION_ERROR',
                error instanceof Error ? error.message : 'Unknown error',
                startTime
            );
        }
    }

    async executeParallel<TInput, TOutput>(
        requests: Array<{ skillId: string; input: TInput }>,
        context: Partial<SkillContext> = {}
    ): Promise<Map<string, SkillResult<TOutput>>> {
        const results = new Map<string, SkillResult<TOutput>>();

        const promises = requests.map(async (req) => {
            const result = await this.execute<TInput, TOutput>(req.skillId, req.input, context);
            results.set(req.skillId, result);
        });

        await Promise.all(promises);
        return results;
    }

    private createErrorResult(
        skillId: string,
        executionId: string,
        code: string,
        message: string,
        startTime: number
    ): SkillResult<any> {
        const result: SkillResult<any> = {
            skillId,
            executionId,
            status: 'error',
            error: {
                code,
                message,
                recoverable: false
            },
            duration: Date.now() - startTime,
            timestamp: new Date()
        };
        this.store.saveExecutionResult(result);
        return result;
    }

    private generateCacheKey(skillId: string, input: any): string {
        try {
            return `${skillId}:${JSON.stringify(input)}`;
        } catch (e) {
            return `${skillId}:unserializable-${Math.random()}`;
        }
    }
}

import { Skill, SkillCategory, SkillContext, SkillOutputSchema, SkillParameter, ValidationResult, CostEstimate, SkillDependency } from '@designsys/ui-ux-core';
