// Main documentation viewer component
export const DocViewer: React.FC<DocViewerProps> = ({
    component,
    className,
    showSidebar = true,
}) => {
    const [activeSection, setActiveSection] = React.useState('props');
    const [searchQuery, setSearchQuery] = React.useState('');
    const [isSidebarOpen, setIsSidebarOpen] = React.useState(true);

    const sections = [
        { id: 'props', label: 'Props', count: component.props.length },
        { id: 'methods', label: 'Methods', count: component.methods.length },
        { id: 'events', label: 'Events', count: component.events.length },
        { id: 'slots', label: 'Slots', count: component.slots.length },
        { id: 'examples', label: 'Examples', count: component.examples.length },
    ];

    const filteredProps = component.props.filter(
        (prop) =>
            prop.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            prop.type.toLowerCase().includes(searchQuery.toLowerCase()) ||
            prop.description.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const copyMarkdown = async () => {
        const markdown = generateMarkdownDoc(component);
        await navigator.clipboard.writeText(markdown);
    };

    const downloadHTML = () => {
        const html = generateHTMLDoc(component);
        const blob = new Blob([html], { type: 'text/html' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${component.name.toLowerCase()}.html`;
        a.click();
        URL.revokeObjectURL(url);
    };

    return (
        <div className={cn('flex gap-6', className)}>
            {/* Sidebar */}
            {showSidebar && (
                <AnimatePresence>
                    {isSidebarOpen && (
                        <motion.aside
                            initial={{ width: 0, opacity: 0 }}
                            animate={{ width: 240, opacity: 1 }}
                            exit={{ width: 0, opacity: 0 }}
                            className="hidden lg:block flex-shrink-0"
                        >
                            <Card className="h-full">
                                <CardHeader className="pb-3">
                                    <CardTitle className="text-sm font-medium">
                                        {component.category}
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="pt-0">
                                    <nav className="space-y-1">
                                        {sections.map((section) => (
                                            <button
                                                key={section.id}
                                                className={cn(
                                                    'w-full flex items-center justify-between px-3 py-2 text-sm rounded-md transition-colors',
                                                    activeSection === section.id
                                                        ? 'bg-primary text-primary-foreground'
                                                        : 'hover:bg-muted'
                                                )}
                                                onClick={() => setActiveSection(section.id)}
                                            >
                                                <span>{section.label}</span>
                                                <Badge
                                                    variant={
                                                        activeSection === section.id
                                                            ? 'secondary'
                                                            : 'outline'
                                                    }
                                                    className="text-xs"
                                                >
                                                    {section.count}
                                                </Badge>
                                            </button>
                                        ))}
                                    </nav>
                                </CardContent>
                            </Card>
                        </motion.aside>
                    )}
                </AnimatePresence>
            )}

            {/* Main content */}
            <div className="flex-1 min-w-0">
                <Card>
                    <CardHeader>
                        <div className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <Button
                                    variant="ghost"
                                    size="icon"
                                    className="lg:hidden"
                                    onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                                >
                                    {isSidebarOpen ? (
                                        <X className="h-4 w-4" />
                                    ) : (
                                        <Menu className="h-4 w-4" />
                                    )}
                                </Button>
                                <div>
                                    <CardTitle className="flex items-center gap-2">
                                        <BookOpen className="h-5 w-5" />
                                        {component.name}
                                    </CardTitle>
                                    {component.isDeprecated && (
                                        <Badge variant="destructive" className="mt-2">
                                            Deprecated
                                        </Badge>
                                    )}
                                </div>
                            </div>

                            <div className="flex items-center gap-2">
                                <Button variant="ghost" size="icon" onClick={copyMarkdown}>
                                    <Copy className="h-4 w-4" />
                                </Button>
                                <Button variant="ghost" size="icon" onClick={downloadHTML}>
                                    <Download className="h-4 w-4" />
                                </Button>
                            </div>
                        </div>

                        <p className="text-sm text-muted-foreground mt-2">
                            {component.description}
                        </p>

                        <div className="flex items-center gap-4 mt-3 text-xs text-muted-foreground">
                            {component.version && (
                                <span>v{component.version}</span>
                            )}
                            {component.since && (
                                <span>Since {component.since}</span>
                            )}
                            <code className="px-2 py-0.5 bg-muted rounded">
                                {component.filePath}
                            </code>
                        </div>
                    </CardHeader>

                    <CardContent>
                        {/* Search */}
                        <div className="relative mb-6">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                            <input
                                type="text"
                                placeholder="Search props..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="w-full pl-10 pr-4 py-2 text-sm border rounded-lg bg-background"
                            />
                        </div>

                        {/* Tabs for sections */}
                        <Tabs value={activeSection} onValueChange={setActiveSection}>
                            <TabsList className="mb-4">
                                {sections.map((section) => (
                                    <TabsTrigger key={section.id} value={section.id} className="relative">
                                        {section.label}
                                        {section.count > 0 && (
                                            <Badge
                                                variant="secondary"
                                                className="ml-2 text-xs h-5"
                                            >
                                                {section.count}
                                            </Badge>
                                        )}
                                    </TabsTrigger>
                                ))}
                            </TabsList>

                            <TabsContent value="props">
                                <ScrollArea className="h-[400px] border rounded-lg">
                                    {filteredProps.length > 0 ? (
                                        filteredProps.map((prop) => (
                                            <PropRow key={prop.name} prop={prop} />
                                        ))
                                    ) : (
                                        <p className="p-4 text-sm text-muted-foreground">
                                            No props found matching your search.
                                        </p>
                                    )}
                                </ScrollArea>
                            </TabsContent>

                            <TabsContent value="methods">
                                <div className="space-y-4">
                                    {component.methods.length > 0 ? (
                                        component.methods.map((method) => (
                                            <MethodRow key={method.name} method={method} />
                                        ))
                                    ) : (
                                        <p className="p-4 text-sm text-muted-foreground">
                                            No methods available.
                                        </p>
                                    )}
                                </div>
                            </TabsContent>

                            <TabsContent value="events">
                                {component.events.length > 0 ? (
                                    <div className="space-y-3">
                                        {component.events.map((event) => (
                                            <div key={event.name} className="p-4 border rounded-lg">
                                                <div className="flex items-center gap-2 mb-2">
                                                    <code className="font-mono text-sm">{event.name}</code>
                                                </div>
                                                <p className="text-sm text-muted-foreground">
                                                    {event.description}
                                                </p>
                                                {event.payload && (
                                                    <div className="mt-2 text-xs text-muted-foreground">
                                                        Payload: <code>{event.payload.type}</code> ΓÇö{' '}
                                                        {event.payload.description}
                                                    </div>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                ) : (
                                    <p className="p-4 text-sm text-muted-foreground">
                                        No events available.
                                    </p>
                                )}
                            </TabsContent>

                            <TabsContent value="slots">
                                {component.slots.length > 0 ? (
                                    <div className="space-y-3">
                                        {component.slots.map((slot) => (
                                            <div key={slot.name} className="p-4 border rounded-lg">
                                                <div className="flex items-center gap-2 mb-2">
                                                    <code className="font-mono text-sm">
                                                        {'<'}{slot.name}{'>'}
                                                    </code>
                                                </div>
                                                <p className="text-sm text-muted-foreground">
                                                    {slot.description}
                                                </p>
                                                {slot.fallback && (
                                                    <div className="mt-2 text-xs">
                                                        <span className="text-muted-foreground">
                                                            Fallback:
                                                        </span>{' '}
                                                        <code className="bg-muted px-1 py-0.5 rounded">
                                                            {slot.fallback}
                                                        </code>
                                                    </div>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                ) : (
                                    <p className="p-4 text-sm text-muted-foreground">
                                        No slots available.
                                    </p>
                                )}
                            </TabsContent>

                            <TabsContent value="examples">
                                {component.examples.length > 0 ? (
                                    <div className="space-y-4">
                                        {component.examples.map((example, index) => (
                                            <div key={index} className="border rounded-lg overflow-hidden">
                                                <div className="bg-muted px-4 py-2 text-sm font-medium flex items-center gap-2">
                                                    <Code className="h-4 w-4" />
                                                    Example {index + 1}
                                                </div>
                                                <pre className="p-4 overflow-auto text-sm">
                                                    <code>{example}</code>
                                                </pre>
                                            </div>
                                        ))}
                                    </div>
                                ) : (
                                    <p className="p-4 text-sm text-muted-foreground">
                                        No examples available.
                                    </p>
                                )}
                            </TabsContent>
                        </Tabs>
                    </CardContent>
                </Card>
            </div>
        </div>
    );
};

// Generate component documentation from file
export async function generateDocFromFile(
    filePath: string
): Promise<ComponentDoc | null> {
    try {
        const response = await fetch(filePath);
        const content = await response.text();

        // Parse component metadata
        const nameMatch = content.match(/export\s+interface\s+(\w+)Props/);
        const descriptionMatch = content.match(/\/\*\*\s*\n\s*\*\s*(.+?)\s*\n/);

        // This is a simplified parser - in production you'd use TypeScript compiler API
        const component: ComponentDoc = {
            name: nameMatch ? nameMatch[1] : 'Unknown',
            description: descriptionMatch
                ? descriptionMatch[1]
                : 'No description available.',
            category: 'Components',
            version: '1.0.0',
            props: parseInterfaceToProps(content),
            methods: [],
            events: [],
            slots: [],
            examples: [],
            filePath,
        };

        return component;
    } catch {
        console.error('Failed to generate documentation:', filePath);
        return null;
    }
}

// Hook for managing documentation state
export function useDocumentation() {
    const [components, setComponents] = React.useState<ComponentDoc[]>([]);
    const [selectedComponent, setSelectedComponent] = React.useState<
        ComponentDoc | null
    >(null);
    const [isLoading, setIsLoading] = React.useState(false);

    const addComponent = (component: ComponentDoc) => {
        setComponents((prev) => [...prev, component]);
    };

    const removeComponent = (name: string) => {
        setComponents((prev) => prev.filter((c) => c.name !== name));
        if (selectedComponent?.name === name) {
            setSelectedComponent(null);
        }
    };

    const generateAllDocs = async (files: string[]) => {
        setIsLoading(true);
        const docs: ComponentDoc[] = [];

        for (const file of files) {
            const doc = await generateDocFromFile(file);
            if (doc) {
                docs.push(doc);
            }
        }

        setComponents(docs);
        setIsLoading(false);
    };

    return {
        components,
        selectedComponent,
        setSelectedComponent,
        addComponent,
        removeComponent,
        generateAllDocs,
        isLoading,
    };
}

import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ColorPalette, DarkModeColors } from "@/types/designSystem";
import { getContrastRatio, getWCAGCompliance, WCAGLevel } from "@/lib/colorUtils";
import { CheckCircle, XCircle, AlertCircle, Eye, Sparkles } from "lucide-react";
import { fixContrast } from "@/lib/colorUtils";

interface AccessibilityCheckerProps {
  colors: ColorPalette;
  darkColors?: DarkModeColors;
  isDarkMode?: boolean;
  onUpdate?: (colors: ColorPalette, darkColors?: DarkModeColors) => void;
}

interface ContrastCheck {
  name: string;
  foreground: string;
  background: string;
  ratio: number;
  normalText: WCAGLevel;
  largeText: WCAGLevel;
}

function ComplianceBadge({ level, size = "normal" }: { level: WCAGLevel; size?: "normal" | "large" }) {
  const colors: Record<WCAGLevel, string> = {
    AAA: "bg-emerald-500/20 text-emerald-600 border-emerald-500/30",
    AA: "bg-amber-500/20 text-amber-600 border-amber-500/30",
    Fail: "bg-red-500/20 text-red-600 border-red-500/30"
  };

  const icons: Record<WCAGLevel, React.ReactNode> = {
    AAA: <CheckCircle className="h-3 w-3" />,
    AA: <AlertCircle className="h-3 w-3" />,
    Fail: <XCircle className="h-3 w-3" />
  };

  return (
    <Badge variant="outline" className={`${colors[level]} gap-1 text-xs`}>
      {icons[level]}
      {level} {size === "large" ? "(lg)" : "(sm)"}
    </Badge>
  );
}

export function AccessibilityChecker({ colors, darkColors, isDarkMode = false, onUpdate }: AccessibilityCheckerProps) {
  const activeColors = isDarkMode && darkColors ? darkColors : colors;

  const handleAutoFix = (check: ContrastCheck) => {
    if (!onUpdate) return;

    // Determine the key in ColorPalette
    const colorKey = Object.keys(activeColors).find(key => (activeColors as any)[key] === check.foreground);
    if (!colorKey) return;

    const fixedColor = fixContrast(check.foreground, check.background, "AA", "normal");

    if (isDarkMode && darkColors) {
      const newDarkColors = { ...darkColors, [colorKey]: fixedColor };
      onUpdate(colors, newDarkColors);
    } else {
      const newColors = { ...colors, [colorKey]: fixedColor };
      onUpdate(newColors, darkColors);
    }
  };

  const checks: ContrastCheck[] = [
    {
      name: "Primary on Background",
      foreground: activeColors.primary,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Text on Background",
      foreground: activeColors.text,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Text Secondary on Background",
      foreground: activeColors.textSecondary,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Text on Surface",
      foreground: activeColors.text,
      background: activeColors.surface,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Primary on Surface",
      foreground: activeColors.primary,
      background: activeColors.surface,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Secondary on Background",
      foreground: activeColors.secondary,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Accent on Background",
      foreground: activeColors.accent,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Success on Background",
      foreground: activeColors.success,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Warning on Background",
      foreground: activeColors.warning,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    },
    {
      name: "Error on Background",
      foreground: activeColors.error,
      background: activeColors.background,
      ratio: 0,
      normalText: "Fail",
      largeText: "Fail"
    }
  ].map(check => {
    const ratio = getContrastRatio(check.foreground, check.background);
    return {
      ...check,
      ratio,
      normalText: getWCAGCompliance(ratio, "normal"),
      largeText: getWCAGCompliance(ratio, "large")
    };
  });

  const passCount = checks.filter(c => c.normalText !== "Fail").length;
  const aaaCount = checks.filter(c => c.normalText === "AAA").length;
  const overallScore = Math.round((passCount / checks.length) * 100);

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2">
              <Eye className="h-5 w-5" />
              Accessibility Checker
            </CardTitle>
            <CardDescription>WCAG 2.1 color contrast validation</CardDescription>
          </div>
          <div className="text-right">
            <div className="text-2xl font-bold">{overallScore}%</div>
            <div className="text-xs text-muted-foreground">
              {passCount}/{checks.length} pass ΓÇó {aaaCount} AAA
            </div>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {checks.map((check, index) => (
            <div
              key={index}
              className="flex items-center justify-between p-3 rounded-lg border border-border/50 bg-muted/20"
            >
              <div className="flex items-center gap-3">
                <div className="flex gap-1">
                  <div
                    className="w-6 h-6 rounded border border-border"
                    style={{ background: check.foreground }}
                    title={`Foreground: ${check.foreground}`}
                  />
                  <div
                    className="w-6 h-6 rounded border border-border"
                    style={{ background: check.background }}
                    title={`Background: ${check.background}`}
                  />
                </div>
                <div>
                  <p className="text-sm font-medium">{check.name}</p>
                  <p className="text-xs text-muted-foreground">
                    Ratio: {check.ratio.toFixed(2)}:1
                  </p>
                </div>
              </div>
              <div className="flex gap-2 items-center">
                {check.normalText === "Fail" && (
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-8 px-2 text-primary hover:bg-primary/10 gap-1 animate-pulse-soft"
                    onClick={() => handleAutoFix(check)}
                  >
                    <Sparkles className="h-3 w-3" />
                    Magic Fix
                  </Button>
                )}
                <ComplianceBadge level={check.normalText} size="normal" />
                <ComplianceBadge level={check.largeText} size="large" />
              </div>
            </div>
          ))}
        </div>

        <div className="mt-6 p-4 rounded-lg bg-muted/30 border border-border/50">
          <h4 className="font-medium text-sm mb-2">WCAG 2.1 Guidelines</h4>
          <ul className="text-xs text-muted-foreground space-y-1">
            <li>ΓÇó <strong>AAA (Enhanced):</strong> 7:1 for normal text, 4.5:1 for large text</li>
            <li>ΓÇó <strong>AA (Minimum):</strong> 4.5:1 for normal text, 3:1 for large text</li>
            <li>ΓÇó Large text is defined as 18pt+ or 14pt+ bold</li>
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}

import { useState, useEffect } from "react";
import { monitor } from "@/lib/monitoring";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from "recharts";
import { Activity, Download, MousePointer2, BarChart3 } from "lucide-react";

interface AnalyticsDashboardProps {
    designSystemId: string;
}

export function AnalyticsDashboard({ designSystemId }: AnalyticsDashboardProps) {
    const [data, setData] = useState<any[]>([]);
    const [stats, setStats] = useState({
        totalExports: 0,
        totalEdits: 0,
        activeUsers: 0
    });

    useEffect(() => {
        fetchAnalytics();
    }, [designSystemId]);

    const fetchAnalytics = async () => {
        if (!designSystemId) return;
        try {
            const { data: events, error } = await (supabase as any)
                .from("analytics_events")
                .select("*")
                .eq("design_system_id", designSystemId);

            if (error) throw error;

            // Group by event type for a bar chart
            const grouped = (events as any[]).reduce((acc: any, event) => {
                const type = event.event_type;
                acc[type] = (acc[type] || 0) + 1;
                return acc;
            }, {});

            const chartData = Object.keys(grouped).map(key => ({
                name: key.replace(/_/g, " ").toUpperCase(),
                value: grouped[key]
            }));

            setData(chartData);

            // Aggregate stats
            setStats({
                totalExports: (events as any[]).filter(e => e.event_type.startsWith("exported")).length,
                totalEdits: (events as any[]).filter(e => e.event_type === "token_updated").length,
                activeUsers: new Set((events as any[]).map(e => e.metadata?.user_id)).size || 1
            });
        } catch (err) {
            monitor.error("Error fetching analytics", err as Error);
        }
    };

    const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#0088FE', '#00C49F'];

    return (
        <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6">
                        <div className="flex items-center gap-4">
                            <div className="h-10 w-10 rounded-full bg-primary/10 flex items-center justify-center">
                                <Download className="h-5 w-5 text-primary" />
                            </div>
                            <div>
                                <p className="text-xs text-muted-foreground font-medium uppercase tracking-wider">Total Exports</p>
                                <h3 className="text-2xl font-bold">{stats.totalExports}</h3>
                            </div>
                        </div>
                    </CardContent>
                </Card>
                <Card className="bg-emerald-500/5 border-emerald-500/20">
                    <CardContent className="pt-6">
                        <div className="flex items-center gap-4">
                            <div className="h-10 w-10 rounded-full bg-emerald-500/10 flex items-center justify-center">
                                <Activity className="h-5 w-5 text-emerald-500" />
                            </div>
                            <div>
                                <p className="text-xs text-muted-foreground font-medium uppercase tracking-wider">Token Updates</p>
                                <h3 className="text-2xl font-bold">{stats.totalEdits}</h3>
                            </div>
                        </div>
                    </CardContent>
                </Card>
                <Card className="bg-blue-500/5 border-blue-500/20">
                    <CardContent className="pt-6">
                        <div className="flex items-center gap-4">
                            <div className="h-10 w-10 rounded-full bg-blue-500/10 flex items-center justify-center">
                                <MousePointer2 className="h-5 w-5 text-blue-500" />
                            </div>
                            <div>
                                <p className="text-xs text-muted-foreground font-medium uppercase tracking-wider">Active Collaborators</p>
                                <h3 className="text-2xl font-bold">{stats.activeUsers}</h3>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            </div>

            <Card className="border-border/50 bg-card/50 backdrop-blur-sm">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                        <BarChart3 className="h-5 w-5 text-primary" />
                        Event Distribution
                    </CardTitle>
                    <CardDescription>Telemetry across all design system interactions</CardDescription>
                </CardHeader>
                <CardContent>
                    <div className="h-[300px] w-full">
                        <ResponsiveContainer width="100%" height="100%">
                            <BarChart data={data}>
                                <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#888888" opacity={0.1} />
                                <XAxis
                                    dataKey="name"
                                    fontSize={10}
                                    tickLine={false}
                                    axisLine={false}
                                    tick={{ fill: 'currentColor', opacity: 0.5 }}
                                />
                                <YAxis
                                    fontSize={10}
                                    tickLine={false}
                                    axisLine={false}
                                    tick={{ fill: 'currentColor', opacity: 0.5 }}
                                />
                                <Tooltip
                                    contentStyle={{ borderRadius: '12px', border: '1px solid rgba(0,0,0,0.1)', boxShadow: '0 10px 15px -3px rgba(0,0,0,0.1)' }}
                                    cursor={{ fill: 'rgba(0,0,0,0.05)' }}
                                />
                                <Bar dataKey="value" radius={[4, 4, 0, 0]}>
                                    {data.map((_, index) => (
                                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                    ))}
                                </Bar>
                            </BarChart>
                        </ResponsiveContainer>
                    </div>
                </CardContent>
            </Card>
        </div>
    );
}

import { useState } from "react";
import { monitor } from "@/lib/monitoring";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import {
    Download,
    FileType,
    FileText,
    Image as ImageIcon,
    Loader2,
    AlertCircle,
    ExternalLink,
    Settings2,
    Package,
    ArrowRight
} from "lucide-react";
import { GeneratedDesignSystem } from "@/types/designSystem";
import { DesignToken } from "@/types/tokens";
import { generateBulkBundle } from "@/lib/exporters/bulk-exporter";
import { exportToPDF, exportToWord } from "@/lib/exporters/asset-exporters";
import { toast } from "sonner";
import { motion, AnimatePresence } from "framer-motion";

interface AssetHubProps {
    designSystem: GeneratedDesignSystem;
    tokens: DesignToken[];
}

export function AssetHub({ designSystem, tokens }: AssetHubProps) {
    const [isGenerating, setIsGenerating] = useState(false);
    const [progress, setProgress] = useState(0);
    const [error, setError] = useState<string | null>(null);

    const handleBulkExport = async () => {
        setIsGenerating(true);
        setProgress(0);
        setError(null);

        try {
            const blob = await generateBulkBundle(designSystem, tokens, {
                onProgress: (p) => setProgress(p)
            });

            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = `${designSystem.name}-assets-bundle.zip`;
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            toast.success("Design bundle generated successfully!");
        } catch (err: any) {
            monitor.error("Bulk export failed", err as Error);
            setError(err.message || "Export failed. Please try again.");
            toast.error("Bulk export failed");
        } finally {
            setIsGenerating(false);
        }
    };

    const downloadIndividual = async (type: 'pdf' | 'docx') => {
        const toastId = toast.loading(`Generating ${type.toUpperCase()}...`);
        try {
            let blob: Blob;
            if (type === 'pdf') blob = await exportToPDF(designSystem, tokens);
            else blob = await exportToWord(designSystem, tokens);

            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = `${designSystem.name}-spec.${type}`;
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            toast.success(`${type.toUpperCase()} ready!`, { id: toastId });
        } catch (err) {
            toast.error(`Failed to generate ${type.toUpperCase()}`, { id: toastId });
        }
    };

    const assetCategories: Array<{
        title: string;
        description: string;
        icon: any;
        color: string;
        bg: string;
        formats: Array<{ name: string; type?: 'pdf' | 'docx' | 'css' | 'json'; format?: string; size: string }>;
    }> = [
            {
                title: "Documentation",
                description: "Full design specifications and guidelines",
                icon: FileText,
                color: "text-blue-500",
                bg: "bg-blue-500/10",
                formats: [
                    { name: "Specification (PDF)", type: 'pdf', size: "2.4 MB" },
                    { name: "Spec Document (DOCX)", type: 'docx', size: "1.2 MB" },
                ]
            },
            {
                title: "Code Tokens",
                description: "Raw variables for multiple platforms",
                icon: FileType,
                color: "text-purple-500",
                bg: "bg-purple-500/10",
                formats: [
                    { name: "JSON Tokens", format: 'json', size: "45 KB" },
                    { name: "Tailwind Config", format: 'js', size: "12 KB" },
                ]
            },
            {
                title: "Asset Package",
                description: "SVG and high-res image representations",
                icon: ImageIcon,
                color: "text-orange-500",
                bg: "bg-orange-500/10",
                formats: [
                    { name: "Color Swatches (PNG)", format: 'png', size: "850 KB" },
                    { name: "Logo Icons (SVG)", format: 'svg', size: "120 KB" },
                ]
            }
        ];

    return (
        <div className="max-w-6xl mx-auto space-y-8 p-6">
            {/* Hero Section */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 bg-card p-8 rounded-[2.5rem] border border-primary/10 relative overflow-hidden">
                <div className="relative z-10 space-y-2">
                    <h2 className="text-3xl font-black tracking-tight">Unified Asset Delivery</h2>
                    <p className="text-muted-foreground">Package and deliver your design system foundations in professional formats.</p>
                </div>

                <div className="relative z-10">
                    <Button
                        size="lg"
                        onClick={handleBulkExport}
                        disabled={isGenerating}
                        className="h-14 px-8 rounded-2xl bg-primary hover:bg-primary/90 shadow-xl shadow-primary/20 gap-3 group"
                    >
                        {isGenerating ? (
                            <Loader2 className="h-5 w-5 animate-spin" />
                        ) : (
                            <Package className="h-5 w-5 group-hover:scale-110 transition-transform" />
                        )}
                        {isGenerating ? "Packaging Assets..." : "Download Full Bundle"}
                        <ArrowRight className="h-4 w-4 opacity-50" />
                    </Button>
                </div>

                {/* Background Decor */}
                <div className="absolute top-0 right-0 w-64 h-64 bg-primary/5 rounded-full blur-3xl -mr-20 -mt-20" />
                <div className="absolute bottom-0 left-0 w-32 h-32 bg-primary/5 rounded-full blur-2xl -ml-10 -mb-10" />
            </div>

            {/* Progress Bar (Visible during generation) */}
            <AnimatePresence>
                {isGenerating && (
                    <motion.div
                        initial={{ opacity: 0, height: 0 }}
                        animate={{ opacity: 1, height: 'auto' }}
                        exit={{ opacity: 0, height: 0 }}
                        className="space-y-4 bg-muted/30 p-6 rounded-2xl border overflow-hidden"
                    >
                        <div className="flex justify-between text-sm font-bold">
                            <span className="flex items-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin text-primary" />
                                Processing Hierarchical Packaging...
                            </span>
                            <span>{progress}%</span>
                        </div>
                        <Progress value={progress} className="h-2" />
                        <p className="text-[10px] text-muted-foreground uppercase tracking-widest font-bold">
                            Bundling specification, code tokens, and visual assets
                        </p>
                    </motion.div>
                )}
            </AnimatePresence>

            {error && (
                <div className="p-4 rounded-xl bg-destructive/10 border border-destructive/20 flex items-center gap-3 text-sm text-destructive font-medium">
                    <AlertCircle className="h-5 w-5" />
                    {error}
                </div>
            )}

            {/* Grid of format categories */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {assetCategories.map((category, idx) => (
                    <Card key={idx} className="border-border/50 hover:border-primary/20 transition-all duration-300 group">
                        <CardHeader>
                            <div className={`w-12 h-12 rounded-2xl ${category.bg} flex items-center justify-center mb-2 group-hover:scale-110 transition-transform duration-500`}>
                                <category.icon className={`h-6 w-6 ${category.color}`} />
                            </div>
                            <CardTitle className="text-xl">{category.title}</CardTitle>
                            <CardDescription>{category.description}</CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-3">
                            {category.formats.map((format, fIdx) => (
                                <div key={fIdx} className="flex items-center justify-between p-3 rounded-xl bg-muted/50 hover:bg-muted transition-colors">
                                    <div className="space-y-0.5">
                                        <p className="text-sm font-bold">{format.name}</p>
                                        <p className="text-[10px] text-muted-foreground uppercase">{format.size}</p>
                                    </div>
                                    <Button
                                        variant="ghost"
                                        size="icon"
                                        className="h-8 w-8 rounded-full"
                                        onClick={() => format.type ? downloadIndividual(format.type as any) : toast.info("Download available in bundle")}
                                    >
                                        <Download className="h-4 w-4" />
                                    </Button>
                                </div>
                            ))}
                        </CardContent>
                    </Card>
                ))}
            </div>

            {/* Quick Actions / Settings */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <Card className="bg-primary/5 border-primary/10">
                    <CardHeader>
                        <CardTitle className="text-lg flex items-center gap-2">
                            <Settings2 className="h-5 w-5" /> Export Preferences
                        </CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                        <div className="flex items-center justify-between">
                            <span className="text-sm font-medium">Default Format</span>
                            <span className="text-xs font-bold text-primary">ZIP Archive</span>
                        </div>
                        <div className="flex items-center justify-between">
                            <span className="text-sm font-medium">Include Private Tokens</span>
                            <span className="text-xs text-muted-foreground">Off</span>
                        </div>
                    </CardContent>
                </Card>

                <Card className="border-dashed">
                    <CardHeader>
                        <CardTitle className="text-lg flex items-center gap-2">
                            <ExternalLink className="h-5 w-5" /> Cloud Sync
                        </CardTitle>
                    </CardHeader>
                    <CardContent className="flex items-center justify-center p-8 opacity-40">
                        <div className="text-center space-y-2">
                            <Download className="h-8 w-8 mx-auto mb-2" />
                            <p className="text-xs font-bold">AWS S3 / Google Cloud Storage</p>
                            <p className="text-[10px]">Premium Feature Coming Soon</p>
                        </div>
                    </CardContent>
                </Card>
            </div>
        </div>
    );
}

import { useState, useEffect } from "react";
