    <section style={{ marginTop: '3rem' }}>
      <h2 style={{ fontSize: '${ds.typography.sizes.xl}', borderBottom: '1px solid #eee', paddingBottom: '0.5rem' }}>Typography</h2>
      <div style={{ marginTop: '1rem' }}>
        ${Object.entries(ds.typography.sizes)
      .map(([size, value]) => `
