}

export const FigmaSync = ({ designSystemId }: FigmaSyncProps) => {
    const { user } = useAuth();
    const [copied, setCopied] = useState(false);
    const [connection, setConnection] = useState<FigmaConnection | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [isRotating, setIsRotating] = useState(false);

    const figmaPluginUrl = "https://designforge.me/figma-plugin";
    const bridgeUrl = `${window.location.origin}/api/tokens/${designSystemId || "default"}`;

    const fetchConnection = async () => {
        if (!designSystemId) return;
        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from("figma_connections" as any)
                .select("*")
                .eq("design_system_id", designSystemId)
                .maybeSingle();

            if (error) throw error;
            if (data) {
                setConnection(data as unknown as FigmaConnection);
            } else if (user) {
                // Initialize a token if none exists
                const newToken = `df_${Math.random().toString(36).substring(2, 15)}`;
                const { data: created, error: createError } = await supabase
                    .from("figma_connections" as any)
                    .insert({
                        design_system_id: designSystemId,
                        user_id: user.id,
                        access_token: newToken,
                        sync_status: 'idle'
                    })
                    .select()
                    .single();

                if (createError) throw createError;
                setConnection(created as unknown as FigmaConnection);
            }
        } catch (error) {
            monitor.error("Error fetching figma connection", error as Error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleRotateToken = async () => {
        if (!designSystemId || !user || !connection) return;
        setIsRotating(true);
        const newToken = `df_${Math.random().toString(36).substring(2, 15)}`;

        try {
            const { error } = await supabase
                .from("figma_connections" as any)
                .update({ access_token: newToken })
                .eq("design_system_id", designSystemId);

            if (error) throw error;
            setConnection({ ...connection, access_token: newToken });
            toast.success("Token rotated successfully");
        } catch (error: any) {
            toast.error("Failed to rotate token: " + error.message);
        } finally {
            setIsRotating(false);
        }
    };

    useEffect(() => {
        fetchConnection();
    }, [designSystemId]);

    const copyToClipboard = (text: string) => {
        navigator.clipboard.writeText(text);
        setCopied(true);
        toast.success("Copied to clipboard");
        setTimeout(() => setCopied(false), 2000);
    };

    if (isLoading) {
        return <div className="p-8 text-center text-muted-foreground animate-pulse">Initializing Figma bridge...</div>;
    }

    return (
        <div className="space-y-6 animate-in fade-in duration-700">
            <Card className="glass-card">
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <div className="space-y-1">
                            <CardTitle className="text-2xl font-bold flex items-center gap-2">
                                <Layout className="h-6 w-6 text-primary" />
                                Figma Sync Bridge
                            </CardTitle>
                            <CardDescription>
                                Connect your design system to Figma for live token syncing.
                            </CardDescription>
                        </div>
                        <Button variant="outline" size="sm" asChild>
                            <a href={figmaPluginUrl} target="_blank" rel="noopener noreferrer">
                                Get Figma Plugin
                                <ExternalLink className="ml-2 h-4 w-4" />
                            </a>
                        </Button>
                    </div>
                </CardHeader>
                <CardContent className="space-y-6">
                    <div className="grid gap-6">
                        <div className="space-y-2">
                            <Label htmlFor="bridge-url">Bridge API URL</Label>
                            <div className="flex gap-2">
                                <Input
                                    id="bridge-url"
                                    value={bridgeUrl}
                                    readOnly
                                    className="bg-muted/30 font-mono text-xs"
                                />
                                <Button variant="ghost" size="icon" onClick={() => copyToClipboard(bridgeUrl)} aria-label="Copy Bridge URL">
                                    <Copy className="h-4 w-4" />
                                </Button>
                            </div>
                            <div className="text-[10px] text-muted-foreground flex items-center gap-1">
                                <LinkIcon className="h-3 w-3" />
                                Use this URL in the DesignForge Figma Plugin.
                            </div>
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="sync-token">Access Token</Label>
                            <div className="flex gap-2">
                                <Input
                                    id="sync-token"
                                    value={connection?.access_token || ""}
                                    readOnly
                                    type="password"
                                    className="bg-muted/30 font-mono text-xs"
                                />
                                <Button variant="ghost" size="icon" onClick={() => copyToClipboard(connection?.access_token || "")} aria-label="Copy Access Token">
                                    {copied ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
                                </Button>
                            </div>
                            <div className="text-[10px] text-muted-foreground flex items-center gap-1">
                                <ShieldCheck className="h-3 w-3 text-primary" />
                                This token grants read-only access to your design system tokens.
                            </div>
                        </div>
                    </div>

                    <div className="pt-4 border-t flex items-center justify-between">
                        <div className="flex items-center gap-4">
                            <div className="flex items-center gap-2">
                                <div className={cn("h-2 w-2 rounded-full", connection?.sync_status === 'idle' ? 'bg-green-500 animate-pulse' : 'bg-blue-500')} />
                                <span className="text-xs font-medium">Bridge {connection?.sync_status === 'idle' ? 'Active' : 'Connected'}</span>
                            </div>
                            {connection?.last_sync_at && (
                                <div className="text-[10px] text-muted-foreground italic">
                                    Last synced: {new Date(connection.last_sync_at).toLocaleString()}
                                </div>
                            )}
                        </div>
                        <Button variant="ghost" size="sm" className="text-xs gap-2" onClick={handleRotateToken} disabled={isRotating}>
                            <RefreshCw className={cn("h-3 w-3", isRotating && "animate-spin")} />
                            Rotate Token
                        </Button>
                    </div>
                </CardContent>
            </Card>

            <div className="grid md:grid-cols-2 gap-4">
                <Card className="bg-primary/5 border-primary/10">
                    <CardContent className="pt-6 space-y-2">
                        <h5 className="font-semibold text-sm">How it works</h5>
                        <ol className="text-xs text-muted-foreground space-y-2 list-decimal list-inside">
                            <li>Install the DesignForge plugin in Figma</li>
                            <li>Enter your Bridge API URL and Token</li>
                            <li>Whenever you update tokens here, click "Sync" in Figma</li>
                        </ol>
                    </CardContent>
                </Card>

                <Card className="bg-muted/20 border-border/50">
                    <CardContent className="pt-6 space-y-2">
                        <h5 className="font-semibold text-sm">Upcoming: Auto-Push</h5>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            We are working on a background worker that will automatically push changes to Figma whenever you save a version in DesignForge.
                        </p>
                    </CardContent>
                </Card>
            </div>
            <Card className="glass-card mt-6">
                <CardHeader>
                    <CardTitle className="text-xl font-bold flex items-center gap-2">
                        <ShieldCheck className="h-5 w-5 text-primary" />
                        Figma Account Connection
                    </CardTitle>
                    <CardDescription>
                        Connect your Figma account to import variables directly.
                    </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                    <div className="space-y-2">
                        <Label htmlFor="figma-pat">Personal Access Token</Label>
                        <Input
                            id="figma-pat"
                            type="password"
                            placeholder="figd_..."
                            value={connection?.figma_token || ""}
                            onChange={(e) => setConnection(prev => prev ? ({ ...prev, figma_token: e.target.value }) : null)}
                        />
                        <p className="text-[10px] text-muted-foreground">
                            Generate this in Figma Settings {'>'} Security {'>'} Personal Access Tokens
                        </p>
                    </div>
                    <div className="space-y-2">
                        <Label htmlFor="file-key">Figma File Key</Label>
                        <Input
                            id="file-key"
                            placeholder="e.g. 8Kj9..."
                            value={connection?.figma_file_key || ""}
                            onChange={(e) => setConnection(prev => prev ? ({ ...prev, figma_file_key: e.target.value }) : null)}
                        />
                        <p className="text-[10px] text-muted-foreground">
                            Found in the URL of your Figma file: figma.com/file/KEY/...
                        </p>
                    </div>
                    <Button
                        onClick={async () => {
                            if (!connection) return;
                            setIsLoading(true);
                            try {
                                const { error } = await supabase.functions.invoke('save-connection', {
                                    body: {
                                        type: 'figma',
                                        designSystemId,
                                        token: connection.figma_token,
                                        figmaFileKey: connection.figma_file_key
                                    }
                                });

                                if (error) throw error;
                                toast.success("Credentials saved securely!");
                                fetchConnection(); // Refresh
                            } catch (error: any) {
                                toast.error("Failed to save credentials: " + error.message);
                            } finally {
                                setIsLoading(false);
                            }
                        }}
                        className="w-full"
                        disabled={isLoading}
                    >
                        {isLoading ? "Encrypting & Saving..." : "Save Secure Connection"}
                    </Button>
                </CardContent>
            </Card>
        </div>
    );
};

import { useState, useEffect } from "react";
import { monitor } from "@/lib/monitoring";
import { supabase } from "@/integrations/supabase/client";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
    Github,
    GitBranch,
    Settings2,
    RefreshCw,
    ExternalLink,
    CheckCircle2,
    AlertCircle,
    Lock,
    Globe,
    Zap,
    Key
} from "lucide-react";
import { toast } from "sonner";
import { useAuth } from "@/contexts/AuthContext";
import { exportToGitHub } from "@/lib/git/sync";
import { SkeletonCard } from "@/components/ui/skeleton-loader";
import ErrorBoundary from "@/components/ErrorBoundary";

// Define GitConnection type locally since table was just created
interface GitConnection {
    id: string;
    design_system_id: string;
    user_id: string;
    provider: string;
    repo_full_name: string;
    default_branch: string;
    sync_status: string;
    last_sync_at: string | null;
    created_at: string;
    updated_at: string;
}


export function GitSettings({ designSystemId }: { designSystemId: string }) {
    const { user } = useAuth();
    const [connection, setConnection] = useState<GitConnection | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [isLinking, setIsLinking] = useState(false);
    const [repoName, setRepoName] = useState("");
    const [branch, setBranch] = useState("main");
    const [webhookUrl, setWebhookUrl] = useState("");
    const [isSavingWebhook, setIsSavingWebhook] = useState(false);

    const fetchConnection = async () => {
        if (!designSystemId) return;
        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from("git_connections" as any)
                .select("*")
                .eq("design_system_id", designSystemId)
                .single();

            if (error && error.code !== "PGRST116") throw error;
            setConnection((data as unknown as GitConnection) || null);
            if (data) {
                const conn = data as unknown as GitConnection;
                setRepoName(conn.repo_full_name);
                setBranch(conn.default_branch);
            }
        } catch (error) {
            monitor.error("Error fetching git connection", error as Error);
        } finally {
            setIsLoading(false);
        }
    };

    const fetchWebhook = async () => {
        if (!designSystemId) return;
        const { data } = await supabase
            .from("design_system_webhooks" as any)
            .select("*")
            .eq("design_system_id", designSystemId)
            .maybeSingle();

        if (data) {
            setWebhookUrl((data as any).url);
        }
    };

    const handleConnect = async () => {
        if (!designSystemId || !repoName || !user) {
            toast.error("Please enter a repository name");
            return;
        }
        setIsLinking(true);
        try {
            const { error } = await supabase
                .from("git_connections" as any)
                .upsert({
                    design_system_id: designSystemId,
                    user_id: user.id,
                    repo_full_name: repoName,
                    default_branch: branch,
                    provider: 'github',
                    sync_status: 'idle'
                }, { onConflict: 'design_system_id' });

            if (error) throw error;
            toast.success("Repository connected successfully!");
            fetchConnection();
        } catch (error: any) {
            toast.error("Failed to connect repository: " + error.message);
        } finally {
            setIsLinking(false);
        }
    };

    const handleSaveWebhook = async () => {
        if (!designSystemId || !webhookUrl || !user) {
            toast.error("Please enter a valid webhook URL");
            return;
        }
        setIsSavingWebhook(true);
        try {
            const { error } = await supabase
                .from("design_system_webhooks" as any)
                .upsert({
                    design_system_id: designSystemId,
                    user_id: user.id,
                    url: webhookUrl,
                    event_types: ['tokens.updated']
                }, { onConflict: 'design_system_id' });

            if (error) throw error;
            toast.success("Webhook configured successfully!");
        } catch (error: any) {
            toast.error("Failed to save webhook: " + error.message);
        } finally {
            setIsSavingWebhook(false);
        }
    };

    const [isSyncing, setIsSyncing] = useState(false);

    const handleSyncNow = async () => {
        if (!connection || !designSystemId) return;
        setIsSyncing(true);
        const toastId = toast.loading("Syncing with GitHub...");

        try {
            // 1. Fetch current design system data which contains tokens
            const { data: designSystemData } = await supabase
                .from("design_systems")
                .select("design_system_data")
                .eq("id", designSystemId)
                .single();

            // Extract tokens from design_system_data JSON
            const dsData = designSystemData?.design_system_data as Record<string, unknown> | null;
            const tokens = dsData?.tokens || [];

            // 2. Perform export
            const result = await exportToGitHub(
                designSystemId,
                tokens as any,
                connection.repo_full_name,
                connection.default_branch
            );

            if (result.success) {
                toast.success(result.message, { id: toastId });
            } else {
                toast.error(result.message, { id: toastId });
            }

            fetchConnection();
        } catch (error: any) {
            toast.error("Sync failed: " + error.message, { id: toastId });
        } finally {
            setIsSyncing(false);
        }
    };

    useEffect(() => {
        fetchConnection();
        fetchWebhook();
    }, [designSystemId]);

    if (isLoading) {
        return (
            <div className="space-y-6 max-w-4xl mx-auto">
                <div className="flex items-center gap-3">
                    <div className="h-10 w-10 rounded-lg bg-muted animate-pulse" />
                    <div className="space-y-2">
                        <div className="h-6 w-48 bg-muted animate-pulse rounded" />
                        <div className="h-3 w-32 bg-muted animate-pulse rounded" />
                    </div>
                </div>
                <div className="grid md:grid-cols-3 gap-6">
                    <div className="md:col-span-2">
                        <SkeletonCard />
                    </div>
                    <div>
                        <SkeletonCard className="h-full" />
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="space-y-6 max-w-4xl mx-auto">
            <ErrorBoundary variant="component">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-foreground rounded-lg">
                        <Github className="h-6 w-6 text-background" />
                    </div>
                    <div>
                        <h2 className="text-xl font-bold">Git Code Connect</h2>
                        <p className="text-xs text-muted-foreground font-medium">Auto-sync design tokens to your codebase</p>
                    </div>
                </div>

                <div className="grid md:grid-cols-3 gap-6">
                    <div className="md:col-span-2 space-y-6">
                        <Card className="border-primary/10 bg-card/50 backdrop-blur-sm">
                            <CardHeader>
                                <CardTitle className="text-sm font-semibold flex items-center gap-2">
                                    <Settings2 className="h-4 w-4" />
                                    Repository Configuration
                                </CardTitle>
                                <CardDescription>
                                    Link this design system to a GitHub repository to enable automated PRs.
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-4">
                                <div className="space-y-2">
                                    <label className="text-xs font-bold uppercase tracking-wider text-muted-foreground">GitHub Repository</label>
                                    <div className="relative">
                                        <Input
                                            placeholder="org/repo-name"
                                            value={repoName}
                                            onChange={(e) => setRepoName(e.target.value)}
                                            className="pl-9"
                                        />
                                        <Github className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                                    </div>
                                </div>
                                <div className="space-y-2">
                                    <label className="text-xs font-bold uppercase tracking-wider text-muted-foreground">Default Branch</label>
                                    <div className="relative">
                                        <Input
                                            placeholder="main"
                                            value={branch}
                                            onChange={(e) => setBranch(e.target.value)}
                                            className="pl-9"
                                        />
                                        <GitBranch className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                                    </div>
                                </div>
                                <Button
                                    onClick={handleConnect}
                                    disabled={isLinking}
                                    className="w-full gap-2 font-bold"
                                >
                                    {connection ? "Update Connection" : "Connect Repository"}
                                    <ExternalLink className="h-4 w-4" />
                                </Button>
                            </CardContent>
                        </Card>

                        {connection && (
                            <Card className="border-accent/10 bg-accent/5">
                                <CardContent className="p-6 flex items-center justify-between">
                                    <div className="flex items-center gap-4">
                                        <div className="h-10 w-10 rounded-full bg-accent/10 flex items-center justify-center">
                                            <CheckCircle2 className="h-5 w-5 text-accent-foreground" />
                                        </div>
                                        <div>
                                            <div className="flex items-center gap-2">
                                                <span className="font-bold">Sync Active</span>
                                                <Badge variant="outline" className="text-[10px] bg-accent/10 text-accent-foreground border-accent/20 uppercase">Live</Badge>
                                            </div>
                                            <p className="text-xs text-muted-foreground">Linked to <strong>{connection.repo_full_name}</strong> on <strong>{connection.default_branch}</strong></p>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <p className="text-[10px] font-bold uppercase text-muted-foreground mb-1">Last Sync</p>
                                        <p className="text-xs font-medium">{connection.last_sync_at ? new Date(connection.last_sync_at).toLocaleString() : "Never"}</p>
                                    </div>
                                </CardContent>
                            </Card>
                        )}

                        <Card className="border-primary/10 bg-card/50 backdrop-blur-sm">
                            <CardHeader>
                                <CardTitle className="text-sm font-semibold flex items-center gap-2">
                                    <Globe className="h-4 w-4" />
                                    Webhook Delivery
                                </CardTitle>
                                <CardDescription>
                                    Notify external services (like CI/CD or Slack) when tokens change.
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-4">
                                <div className="space-y-2">
                                    <label className="text-xs font-bold uppercase tracking-wider text-muted-foreground">Payload URL</label>
                                    <div className="relative">
                                        <Input
                                            placeholder="https://api.yourdomain.com/webhooks/design-tokens"
                                            value={webhookUrl}
                                            onChange={(e) => setWebhookUrl(e.target.value)}
                                            className="pl-9"
                                        />
                                        <Globe className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                                    </div>
                                </div>
                                <div className="flex items-center justify-between p-3 bg-muted/30 rounded-lg border border-border/50">
                                    <div className="flex items-center gap-3">
                                        <Zap className="h-4 w-4 text-primary" />
                                        <div>
                                            <p className="text-[10px] font-bold uppercase">Event Type</p>
                                            <p className="text-xs font-medium">tokens.updated</p>
                                        </div>
                                    </div>
                                    <Badge variant="outline" className="text-[9px]">JSON POST</Badge>
                                </div>
                                <Button
                                    variant="secondary"
                                    onClick={handleSaveWebhook}
                                    disabled={isSavingWebhook}
                                    className="w-full gap-2 font-bold"
                                >
                                    {webhookUrl ? "Update Webhook" : "Configure Webhook"}
                                    <Key className="h-4 w-4" />
                                </Button>
                            </CardContent>
                        </Card>
                    </div>

                    <div className="space-y-6">
                        <Card className="bg-primary/5 border-primary/10">
                            <CardHeader className="pb-2">
                                <CardTitle className="text-xs font-bold uppercase tracking-widest text-primary flex items-center gap-2">
                                    <Lock className="h-3 w-3" /> Security
                                </CardTitle>
                            </CardHeader>
                            <CardContent className="space-y-4">
                                <p className="text-xs text-muted-foreground leading-relaxed">
                                    DesignForge uses <strong>GitHub Apps</strong> for secure, granular access. We never store your personal credentials.
                                </p>
                                <div className="p-3 bg-background rounded-lg border border-border/50">
                                    <div className="flex items-center gap-2 mb-2">
                                        <AlertCircle className="h-3 w-3 text-destructive" />
                                        <span className="text-[10px] font-bold">Token Safety</span>
                                    </div>
                                    <p className="text-[10px] text-muted-foreground">
                                        Access tokens are encrypted at rest using industry-standard AES-256 GCM.
                                    </p>
                                </div>
                            </CardContent>
                        </Card>

                        <Card className="border-dashed">
                            <CardContent className="p-6 flex flex-col items-center text-center">
                                <RefreshCw className={cn("h-8 w-8 text-muted-foreground mb-4 opacity-20", isSyncing && "animate-spin opacity-100 text-primary")} />
                                <h4 className="text-sm font-bold mb-1">Manual Trigger</h4>
                                <p className="text-[10px] text-muted-foreground mb-4">Force a synchronization for debugging</p>
                                <Button
                                    variant="outline"
                                    size="sm"
                                    className="w-full text-[10px] h-8"
                                    disabled={!connection || isSyncing}
                                    onClick={handleSyncNow}
                                >
                                    {isSyncing ? "Syncing..." : "Sync Now"}
                                </Button>
                            </CardContent>
                        </Card>
                    </div>
                </div>
            </ErrorBoundary>
        </div>
    );
}

import React, { useMemo } from 'react';
import ReactFlow, {
    Background,
    Controls,
    Edge,
    Node,
    Handle,
    Position,
    MarkerType
} from 'reactflow';
import 'reactflow/dist/style.css';
import { GeneratedDesignSystem } from '@/types/designSystem';

// Custom Node Components
const PrimitiveNode = ({ data }: any) => (
    <div className="px-4 py-2 rounded-xl bg-card border-2 border-primary/20 shadow-lg min-w-[150px]">
        <Handle type="target" position={Position.Left} className="w-2 h-2 bg-primary" />
        <div className="flex items-center gap-3">
            {data.color && (
                <div className="w-4 h-4 rounded-full border border-border" style={{ backgroundColor: data.color }} />
            )}
            <div className="text-[10px] font-black uppercase tracking-widest text-muted-foreground">{data.label}</div>
        </div>
        <div className="text-xs font-mono mt-1 text-primary">{data.value}</div>
        <Handle type="source" position={Position.Right} className="w-2 h-2 bg-primary" />
    </div>
);

const SemanticNode = ({ data }: any) => (
    <div className="px-4 py-2 rounded-xl bg-primary shadow-xl shadow-primary/20 min-w-[180px] border-2 border-white/10">
        <Handle type="target" position={Position.Left} className="w-2 h-2 bg-white" />
        <div className="text-[9px] font-black uppercase tracking-widest text-white/60 mb-1">Semantic Token</div>
        <div className="text-xs font-bold text-white">{data.label}</div>
        <Handle type="source" position={Position.Right} className="w-2 h-2 bg-white" />
    </div>
);

const ComponentNode = ({ data }: any) => (
    <div className="px-6 py-4 rounded-2xl bg-white/5 backdrop-blur-xl border border-white/10 shadow-2xl min-w-[200px]">
        <Handle type="target" position={Position.Left} className="w-2 h-2 bg-primary" />
        <div className="text-[10px] font-black uppercase tracking-widest text-primary mb-3">Target Component</div>
        <div className="p-3 rounded-lg border border-white/5 bg-black/20 flex items-center justify-center">
            {data.preview}
        </div>
        <div className="text-[10px] text-center mt-2 font-bold text-muted-foreground">{data.label}</div>
    </div>
);

const nodeTypes = {
    primitive: PrimitiveNode,
    semantic: SemanticNode,
    component: ComponentNode,
};

interface TokenGraphProps {
    designSystem: GeneratedDesignSystem;
}

export const TokenGraph: React.FC<TokenGraphProps> = ({ designSystem }) => {
    const { nodes, edges } = useMemo(() => {
        const dsNodes: Node[] = [];
        const dsEdges: Edge[] = [];

        if (!designSystem.tokenStore) {
            // Fallback to basic visualization if store is missing
            const primitives = [
                { id: 'p-primary', label: 'Primary', value: designSystem.colors.primary, color: designSystem.colors.primary },
                { id: 'p-secondary', label: 'Secondary', value: designSystem.colors.secondary, color: designSystem.colors.secondary },
                { id: 'p-text', label: 'Text', value: designSystem.colors.text, color: designSystem.colors.text },
            ];

            primitives.forEach((p, i) => {
                dsNodes.push({
                    id: p.id,
                    type: 'primitive',
                    data: { label: p.label, value: p.value, color: p.color },
                    position: { x: 50, y: i * 100 + 50 },
                });
            });
            return { nodes: dsNodes, edges: dsEdges };
        }

        const { tokens } = designSystem.tokenStore;
        const REF_REGEX = /\{([^}]+)\}/g;

        // Position tracking
        let foundationCount = 0;
        let semanticCount = 0;
        let componentCount = 0;

        Object.values(tokens).forEach((token) => {

            const isSemantic = token.path.startsWith('semantic.');
            const isComponent = token.path.startsWith('components.');

            let type = 'primitive';
            let x = 50;
            let y = 0;

            if (isSemantic) {
                type = 'semantic';
                x = 350;
                y = semanticCount * 100 + 50;
                semanticCount++;
            } else if (isComponent) {
                type = 'component';
                x = 700;
                y = componentCount * 150 + 75;
                componentCount++;
            } else {
                y = foundationCount * 100 + 50;
                foundationCount++;
            }

            // Preview for colors
            const colorValue = typeof token.value === 'string' && token.value.startsWith('hsl') ? token.value : undefined;

            dsNodes.push({
                id: token.path,
                type: type as any,
                data: {
                    label: token.name || token.path.split('.').pop(),
                    value: typeof token.value === 'string' ? token.value : JSON.stringify(token.value),
                    color: colorValue
                },
                position: { x, y },
            });

            // Extract references for edges
            const valueStr = typeof token.value === 'string' ? token.value : '';
            const refs = [...valueStr.matchAll(REF_REGEX)].map(m => m[1]);

            refs.forEach(refPath => {
                if (tokens[refPath]) {
                    dsEdges.push({
                        id: `e-${refPath}-${token.path}`,
                        source: refPath,
                        target: token.path,
                        animated: true,
                        style: { stroke: 'var(--primary)', strokeWidth: 2, opacity: 0.6 },
                        markerEnd: { type: MarkerType.ArrowClosed, color: 'var(--primary)' },
                    });
                }
            });
        });

        return { nodes: dsNodes, edges: dsEdges };
    }, [designSystem]);

    return (
        <div className="w-full h-[500px] rounded-3xl border-2 border-primary/10 bg-black/40 backdrop-blur-xl overflow-hidden relative group">
            <div className="absolute top-6 left-6 z-10">
                <div className="px-4 py-2 rounded-full bg-primary/20 border border-primary/30 backdrop-blur-md">
                    <span className="text-[10px] font-black uppercase tracking-widest text-primary">Semantic Token Graph</span>
                </div>
            </div>

            <ReactFlow
                nodes={nodes}
                edges={edges}
                nodeTypes={nodeTypes}
                fitView
                className="bg-transparent"
                proOptions={{ hideAttribution: true }}
            >
                <Background color="rgba(255, 255, 255, 0.03)" gap={20} />
                <Controls className="fill-primary" />
            </ReactFlow>

            <div className="absolute bottom-6 right-6 z-10 pointer-events-none">
                <p className="text-[9px] font-medium text-muted-foreground/60 uppercase tracking-widest text-right">
                    Interactive Node Mapping<br />Visual Semantic Layer
                </p>
            </div>
        </div>
    );
};

import { DesignToken } from "@/types/tokens";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { GitCompare, ArrowRight, Check, X, Info } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface VersionDiffProps {
    currentTokens: DesignToken[];
    previousTokens: DesignToken[];
}

export const VersionDiff = ({ currentTokens, previousTokens }: VersionDiffProps) => {
    if (!previousTokens || previousTokens.length === 0) {
        return (
            <Card className="glass-card border-dashed">
                <CardContent className="flex flex-col items-center justify-center py-12 text-center space-y-4">
                    <div className="h-12 w-12 rounded-full bg-muted/30 flex items-center justify-center">
                        <GitCompare className="h-6 w-6 text-muted-foreground" />
                    </div>
                    <div className="space-y-1">
                        <h3 className="font-semibold">No version to compare</h3>
                        <p className="text-sm text-muted-foreground max-w-sm">
                            Publish a version first to enable side-by-side visual diffing.
                        </p>
                    </div>
                </CardContent>
            </Card>
        );
    }

    const findDiffs = () => {
        const diffs: { path: string; old: any; new: any; action: 'ADDED' | 'MODIFIED' | 'DELETED' }[] = [];

        const currentMap = new Map(currentTokens.map(t => [t.path, t]));
        const previousMap = new Map(previousTokens.map(t => [t.path, t]));

        // Check for modified and deleted
        previousTokens.forEach(oldToken => {
            const newToken = currentMap.get(oldToken.path);
            if (!newToken) {
                diffs.push({
                    path: oldToken.path,
                    old: oldToken.ref || oldToken.value,
                    new: null,
                    action: 'DELETED'
                });
            } else if (JSON.stringify(newToken.value) !== JSON.stringify(oldToken.value) || newToken.ref !== oldToken.ref) {
                diffs.push({
                    path: oldToken.path,
                    old: oldToken.ref || oldToken.value,
                    new: newToken.ref || newToken.value,
                    action: 'MODIFIED'
                });
            }
        });

        // Check for added
        currentTokens.forEach(newToken => {
            if (!previousMap.has(newToken.path)) {
                diffs.push({
                    path: newToken.path,
                    old: null,
                    new: newToken.ref || newToken.value,
                    action: 'ADDED'
                });
            }
        });

        return diffs;
    };

    const diffs = findDiffs();

    return (
        <div className="space-y-6 animate-in fade-in duration-700">
            <Card className="glass-card">
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <div className="space-y-1">
                            <CardTitle className="text-2xl font-bold flex items-center gap-2">
                                <GitCompare className="h-6 w-6 text-primary" />
                                Version Comparison
                            </CardTitle>
                            <CardDescription>
                                Comparing <Badge variant="outline" className="ml-1">Current Draft</Badge> vs <Badge variant="secondary" className="ml-1">Previous Version</Badge>
                            </CardDescription>
                        </div>
                        <Badge variant="outline" className="text-xs">
                            {diffs.length} Change{diffs.length !== 1 ? 's' : ''} Detected
                        </Badge>
                    </div>
                </CardHeader>
                <CardContent>
                    <div className="rounded-xl border bg-muted/10 overflow-hidden">
                        <table className="w-full text-sm">
                            <thead className="bg-muted/20 text-left border-b font-medium">
                                <tr>
                                    <th className="px-6 py-3">Property</th>
                                    <th className="px-6 py-3">Previous</th>
                                    <th className="px-6 py-3"></th>
                                    <th className="px-6 py-3">Current</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y">
                                {diffs.length === 0 ? (
                                    <tr>
                                        <td colSpan={4} className="px-6 py-12 text-center text-muted-foreground italic">
                                            No changes detected between these versions.
                                        </td>
                                    </tr>
                                ) : (
                                    diffs.map((diff, i) => (
                                        <tr key={i} className="hover:bg-muted/5 transition-colors group">
                                            <td className="px-6 py-4">
                                                <div className="flex flex-col gap-1">
                                                    <span className="font-mono text-[10px] font-bold text-muted-foreground group-hover:text-foreground transition-colors uppercase tracking-wider truncate max-w-[200px]" title={diff.path}>
                                                        {diff.path}
                                                    </span>
                                                    <Badge
                                                        variant={diff.action === 'ADDED' ? 'secondary' : diff.action === 'DELETED' ? 'destructive' : 'outline'}
                                                        className="text-[8px] h-3 px-1 w-fit font-bold"
                                                    >
                                                        {diff.action}
                                                    </Badge>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-2">
                                                    {diff.old && typeof diff.old === 'string' && (diff.old.startsWith("#") || diff.old.startsWith("hsl")) && (
                                                        <div className="h-4 w-4 rounded-full border border-white/10 shadow-sm" style={{ backgroundColor: diff.old }} />
                                                    )}
                                                    <span className="font-mono text-[10px] tabular-nums truncate max-w-[100px]">
                                                        {diff.old === null ? "ΓÇö" : String(diff.old)}
                                                    </span>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 text-muted-foreground text-center">
                                                <ArrowRight className="h-3 w-3 inline-block" />
                                            </td>
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-2">
                                                    {diff.new && typeof diff.new === 'string' && (diff.new.startsWith("#") || diff.new.startsWith("hsl")) && (
                                                        <div className="h-4 w-4 rounded-full border border-white/10 shadow-sm" style={{ backgroundColor: diff.new }} />
                                                    )}
                                                    <span className="font-mono text-[10px] text-primary font-bold tabular-nums truncate max-w-[100px]">
                                                        {diff.new === null ? "ΓÇö" : String(diff.new)}
                                                    </span>
                                                </div>
                                            </td>
                                        </tr>
                                    ))
                                )}
                            </tbody>
                        </table>
                    </div>

                    <div className="mt-6 flex gap-4">
                        <div className="flex-1 p-4 rounded-lg bg-red-500/5 border border-red-500/10 flex items-start gap-3">
                            <X className="h-4 w-4 text-red-500 mt-0.5" />
                            <div className="space-y-1">
                                <p className="text-xs font-semibold text-red-300">Previous</p>
                                <p className="text-[10px] text-red-200/60 leading-relaxed uppercase tracking-wider">Older tokens are being replaced</p>
                            </div>
                        </div>
                        <div className="flex-1 p-4 rounded-lg bg-green-500/5 border border-green-500/10 flex items-start gap-3">
                            <Check className="h-4 w-4 text-green-500 mt-0.5" />
                            <div className="space-y-1">
                                <p className="text-xs font-semibold text-green-300">Current</p>
                                <p className="text-[10px] text-green-200/60 leading-relaxed uppercase tracking-wider">New tokens are live in current draft</p>
                            </div>
                        </div>
                    </div>
                </CardContent>
            </Card>

            <Card className="bg-primary/5 border-primary/10">
                <CardContent className="pt-6 flex gap-4 items-start">
                    <Info className="h-4 w-4 text-primary mt-1 flex-shrink-0" />
                    <div className="space-y-1">
                        <h5 className="font-semibold text-sm">Automated Governance</h5>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            DesignForge automatically maps semantic contrasts between versions. If a background changes from light to dark, all "On" tokens are recalculated to maintain compliance.
                        </p>
                    </div>
                </CardContent>
            </Card>
        </div>
    );
};

import { useState, useEffect } from "react";
import { monitor } from "@/lib/monitoring";
import { supabase } from "@/integrations/supabase/client";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
    History,
    GitCommit,
    Plus,
    Clock,
    ChevronRight
} from "lucide-react";
import { VersionDiff } from "./VersionDiff";
import { toast } from "sonner";
import { ScrollArea } from "@/components/ui/scroll-area";
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
    DialogFooter
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

interface DesignVersion {
    id: string;
    version_number: string;
    name: string;
    description: string;
    is_published: boolean;
    published_at: string;
    snapshot: any;
    created_at: string;
}

export function VersionManager({ designSystemId }: { designSystemId: string }) {
    const [versions, setVersions] = useState<DesignVersion[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isPublishing, setIsPublishing] = useState(false);
    const [selectedVersion, setSelectedVersion] = useState<DesignVersion | null>(null);
    const [currentTokens, setCurrentTokens] = useState<any[]>([]);
    const [newVersion, setNewVersion] = useState({
        number: "1.0.0",
        name: "",
        description: ""
    });

    const fetchCurrentTokens = async () => {
        const { data } = await supabase
            .from("design_tokens" as any)
            .select("*")
            .eq("design_system_id", designSystemId);
        setCurrentTokens(data || []);
    };

    const fetchVersions = async () => {
        if (!designSystemId) return;
        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from("design_system_versions" as any)
                .select("*")
                .eq("design_system_id", designSystemId)
                .order("created_at", { ascending: false });

            if (error) throw error;
            setVersions((data as any) || []);
        } catch (error) {
            monitor.error("Error fetching versions", error as Error);
        } finally {
            setIsLoading(false);
        }
    };

    const handlePublish = async () => {
        if (!designSystemId) return;
        setIsPublishing(true);
        try {
            // 1. Fetch current tokens for the snapshot
            const { data: tokens, error: tokenError } = await supabase
                .from("design_tokens" as any)
                .select("*")
                .eq("design_system_id", designSystemId);

            if (tokenError) throw tokenError;

            // 2. Create version record
            const { error: versionError } = await supabase
                .from("design_system_versions" as any)
                .insert({
                    design_system_id: designSystemId,
                    version_number: newVersion.number,
                    name: newVersion.name,
                    description: newVersion.description,
                    is_published: true,
                    published_at: new Date().toISOString(),
                    snapshot: tokens, // Store the tokens array as the snapshot
                });

            if (versionError) throw versionError;

            toast.success(`Version ${newVersion.number} published!`);
            fetchVersions();
        } catch (error: any) {
            toast.error("Failed to publish version: " + error.message);
        } finally {
            setIsPublishing(false);
        }
    };

    useEffect(() => {
        fetchVersions();
        fetchCurrentTokens();
    }, [designSystemId]);

    return (
        <div className="space-y-6">
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-primary/10 rounded-lg">
                        <History className="h-6 w-6 text-primary" />
                    </div>
                    <div>
                        <h2 className="text-xl font-bold">Version History</h2>
                        <p className="text-xs text-muted-foreground font-medium">Track and restore previous design system states</p>
                    </div>
                </div>

                <div className="flex gap-2">
                    {selectedVersion && (
                        <Button variant="outline" onClick={() => setSelectedVersion(null)}>
                            Back to List
                        </Button>
                    )}
                    <Dialog>
                        <DialogTrigger asChild>
                            <Button className="gap-2 shadow-lg shadow-primary/20">
                                <Plus className="h-4 w-4" />
                                Publish New Version
                            </Button>
                        </DialogTrigger>
                        <DialogContent className="sm:max-w-[425px] bg-card/95 backdrop-blur-xl border-primary/20">
                            <DialogHeader>
                                <DialogTitle>Publish New Version</DialogTitle>
                            </DialogHeader>
                            <div className="grid gap-4 py-4">
                                <div className="grid gap-2">
                                    <label className="text-sm font-medium">Version Number (SemVer)</label>
                                    <Input
                                        value={newVersion.number}
                                        onChange={(e) => setNewVersion({ ...newVersion, number: e.target.value })}
                                        placeholder="e.g. 1.1.0"
                                    />
                                </div>
                                <div className="grid gap-2">
                                    <label className="text-sm font-medium">Version Name</label>
                                    <Input
                                        value={newVersion.name}
                                        onChange={(e) => setNewVersion({ ...newVersion, name: e.target.value })}
                                        placeholder="e.g. Q1 Design Update"
                                    />
                                </div>
                                <div className="grid gap-2">
                                    <label className="text-sm font-medium">Release Notes</label>
                                    <Textarea
                                        value={newVersion.description}
                                        onChange={(e) => setNewVersion({ ...newVersion, description: e.target.value })}
                                        placeholder="What changed in this version?"
                                        className="min-h-[100px]"
                                    />
                                </div>
                            </div>
                            <DialogFooter>
                                <Button
                                    onClick={handlePublish}
                                    disabled={isPublishing}
                                    className="w-full"
                                >
                                    {isPublishing ? "Publishing..." : "Confirm Release"}
                                </Button>
                            </DialogFooter>
                        </DialogContent>
                    </Dialog>
                </div>
            </div>

            {selectedVersion ? (
                <div className="animate-in fade-in slide-in-from-bottom-4 duration-500">
                    <VersionDiff
                        currentTokens={currentTokens}
                        previousTokens={selectedVersion.snapshot}
                    />
                </div>
            ) : (
                <ScrollArea className="h-[500px] rounded-xl border border-border/50 bg-card/50">
                    <div className="p-4 space-y-4">
                        {isLoading ? (
                            <div className="text-center py-12 text-muted-foreground">Loading versions...</div>
                        ) : versions.length === 0 ? (
                            <div className="text-center py-12 text-muted-foreground">No versions published yet.</div>
                        ) : (
                            versions.map((version) => (
                                <Card
                                    key={version.id}
                                    className="relative overflow-hidden group hover:border-primary/50 transition-all cursor-pointer"
                                    onClick={() => setSelectedVersion(version)}
                                >
                                    <div className="absolute top-0 left-0 w-1 h-full bg-primary opacity-0 group-hover:opacity-100 transition-opacity" />
                                    <CardContent className="p-4">
                                        <div className="flex items-center justify-between">
                                            <div className="flex items-center gap-4">
                                                <div className="p-3 bg-muted rounded-full group-hover:bg-primary/10 transition-colors">
                                                    <GitCommit className="h-5 w-5 text-muted-foreground group-hover:text-primary" />
                                                </div>
                                                <div>
                                                    <div className="flex items-center gap-2">
                                                        <h3 className="font-bold">{version.name || `Version ${version.version_number}`}</h3>
                                                        <Badge variant="secondary" className="font-mono text-[10px]">{version.version_number}</Badge>
                                                    </div>
                                                    <p className="text-xs text-muted-foreground flex items-center gap-2 mt-1">
                                                        <Clock className="h-3 w-3" />
                                                        {new Date(version.published_at).toLocaleDateString()} at {new Date(version.published_at).toLocaleTimeString()}
                                                    </p>
                                                </div>
                                            </div>
                                            <Button variant="ghost" size="icon" className="group-hover:translate-x-1 transition-transform" aria-label="View version details">
                                                <ChevronRight className="h-5 w-5" />
                                            </Button>
                                        </div>
                                        {version.description && (
                                            <p className="text-sm text-muted-foreground mt-4 pl-12 line-clamp-2 italic">
                                                "{version.description}"
                                            </p>
                                        )}
                                    </CardContent>
                                </Card>
                            ))
                        )}
                    </div>
                </ScrollArea>
            )}
        </div>
    );
}

import { motion, HTMLMotionProps } from "framer-motion";
import { useReducedMotion } from "@/hooks/useReducedMotion";

import { ReactNode, forwardRef } from "react";
import { cn } from "@/lib/utils";

// ============================================
// INTERACTIVE BUTTON
// ============================================
interface InteractiveButtonProps extends HTMLMotionProps<"button"> {
  children: ReactNode;
  variant?: "default" | "lift" | "bounce" | "glow";
}

export const InteractiveButton = forwardRef<HTMLButtonElement, InteractiveButtonProps>(
  ({ children, variant = "default", className, ...props }, ref) => {
    const prefersReducedMotion = useReducedMotion();

    const variants = {
      default: {
        whileHover: prefersReducedMotion ? {} : { scale: 1.02 },
        whileTap: prefersReducedMotion ? {} : { scale: 0.98 }
      },
      lift: {
        whileHover: prefersReducedMotion ? {} : { y: -2, boxShadow: "0 4px 12px rgba(0,0,0,0.15)" },
        whileTap: prefersReducedMotion ? {} : { y: 0, boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }
      },
      bounce: {
        whileHover: prefersReducedMotion ? {} : { scale: 1.05 },
        whileTap: prefersReducedMotion ? {} : { scale: 0.9 }
      },
      glow: {
        whileHover: prefersReducedMotion ? {} : { boxShadow: "0 0 20px rgba(var(--primary), 0.4)" },
        whileTap: prefersReducedMotion ? {} : { scale: 0.98 }
      }
    };

    return (
      <motion.button
        ref={ref}
        className={cn("transition-colors", className)}
        {...variants[variant]}
        transition={{ duration: 0.15 }}
        {...props}
      >
        {children}
      </motion.button>
    );
  }
);
InteractiveButton.displayName = "InteractiveButton";

// ============================================
// INTERACTIVE CARD
// ============================================
interface InteractiveCardProps extends HTMLMotionProps<"div"> {
  children: ReactNode;
  interactive?: boolean;
}

export function InteractiveCard({
  children,
  interactive = true,
  className,
  ...props
}: InteractiveCardProps) {
  const prefersReducedMotion = useReducedMotion();

  return (
    <motion.div
      className={cn(
        "rounded-lg border bg-card text-card-foreground shadow-sm",
        interactive && "cursor-pointer",
        className
      )}
      whileHover={interactive && !prefersReducedMotion ? {
        y: -4,
        boxShadow: "0 10px 30px -10px rgba(0,0,0,0.2)"
      } : {}}
      whileTap={interactive && !prefersReducedMotion ? { scale: 0.99 } : {}}
      transition={{ duration: 0.2 }}
      {...props}
    >
      {children}
    </motion.div>
  );
}

// ============================================
// RIPPLE EFFECT
// ============================================
import { useState, useCallback } from "react";

interface RippleProps {
  x: number;
  y: number;
  size: number;
}

export function useRipple() {
  const [ripples, setRipples] = useState<RippleProps[]>([]);

  const addRipple = useCallback((event: React.MouseEvent<HTMLElement>) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const size = Math.max(rect.width, rect.height);
    const x = event.clientX - rect.left - size / 2;
    const y = event.clientY - rect.top - size / 2;

    const newRipple = { x, y, size };
    setRipples(prev => [...prev, newRipple]);

    setTimeout(() => {
      setRipples(prev => prev.slice(1));
    }, 600);
  }, []);

  const RippleContainer = () => (
    <>
      {ripples.map((ripple, i) => (
        <motion.span
          key={i}
          className="absolute rounded-full bg-white/30 pointer-events-none"
          style={{
            left: ripple.x,
            top: ripple.y,
            width: ripple.size,
            height: ripple.size,
          }}
          initial={{ scale: 0, opacity: 0.5 }}
          animate={{ scale: 2, opacity: 0 }}
          transition={{ duration: 0.6, ease: "easeOut" }}
        />
      ))}
    </>
  );

  return { addRipple, RippleContainer };
}

// ============================================
// SUCCESS/ERROR FEEDBACK
// ============================================
interface FeedbackIconProps {
  type: "success" | "error" | "warning" | "info";
  size?: "sm" | "md" | "lg";
}

export function FeedbackIcon({ type, size = "md" }: FeedbackIconProps) {
  const prefersReducedMotion = useReducedMotion();

  const sizeClasses = {
    sm: "w-4 h-4",
    md: "w-6 h-6",
    lg: "w-8 h-8"
  };

  const colors = {
    success: "text-green-500",
    error: "text-red-500",
    warning: "text-amber-500",
    info: "text-blue-500"
  };

  const icons = {
    success: (
      <motion.svg
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth={2}
        className={cn(sizeClasses[size], colors[type])}
      >
        <motion.path
          d="M20 6L9 17L4 12"
          strokeLinecap="round"
          strokeLinejoin="round"
          initial={{ pathLength: 0 }}
          animate={{ pathLength: 1 }}
          transition={prefersReducedMotion ? { duration: 0 } : { duration: 0.4, delay: 0.2 }}
        />
      </motion.svg>
    ),
    error: (
      <motion.svg
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth={2}
        className={cn(sizeClasses[size], colors[type])}
      >
        <motion.path
          d="M18 6L6 18M6 6l12 12"
          strokeLinecap="round"
          strokeLinejoin="round"
          initial={{ pathLength: 0 }}
          animate={{ pathLength: 1 }}
          transition={prefersReducedMotion ? { duration: 0 } : { duration: 0.3 }}
        />
      </motion.svg>
    ),
    warning: (
      <motion.svg
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth={2}
        className={cn(sizeClasses[size], colors[type])}
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={prefersReducedMotion ? { duration: 0 } : { type: "spring", stiffness: 400, damping: 15 }}
      >
        <path d="M12 9v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" strokeLinecap="round" strokeLinejoin="round" />
      </motion.svg>
    ),
    info: (
      <motion.svg
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth={2}
        className={cn(sizeClasses[size], colors[type])}
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={prefersReducedMotion ? { duration: 0 } : { type: "spring", stiffness: 400, damping: 15 }}
      >
        <path d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" strokeLinecap="round" strokeLinejoin="round" />
      </motion.svg>
    )
  };

  return (
    <motion.div
      initial={{ scale: 0, rotate: -180 }}
      animate={{ scale: 1, rotate: 0 }}
      transition={prefersReducedMotion ? { duration: 0 } : { type: "spring", stiffness: 300, damping: 20 }}
    >
      {icons[type]}
    </motion.div>
  );
}

// ============================================
// NUMBER COUNTER
// ============================================
import { useMotionValue, useTransform, animate } from "framer-motion";
import { useEffect } from "react";

interface CounterProps {
  from?: number;
  to: number;
  duration?: number;
  className?: string;
  formatter?: (value: number) => string;
}

export function Counter({
  from = 0,
  to,
  duration = 1.5,
  className,
  formatter = (v) => Math.round(v).toString()
}: CounterProps) {
  const prefersReducedMotion = useReducedMotion();
  const count = useMotionValue(prefersReducedMotion ? to : from);
  const rounded = useTransform(count, (v) => formatter(v));

  useEffect(() => {
    if (!prefersReducedMotion) {
      const controls = animate(count, to, { duration, ease: "easeOut" });
      return controls.stop;
    }
  }, [to, duration, prefersReducedMotion, count]);

  return (
    <motion.span className={className}>
      {rounded}
    </motion.span>
  );
}

// ============================================
// TYPEWRITER EFFECT
// ============================================
interface TypewriterProps {
  text: string;
  delay?: number;
  className?: string;
}

export function Typewriter({ text, delay = 0.05, className }: TypewriterProps) {
  const prefersReducedMotion = useReducedMotion();

  if (prefersReducedMotion) {
    return <span className={className}>{text}</span>;
  }

  return (
    <span className={className}>
      {text.split("").map((char, i) => (
        <motion.span
          key={i}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: i * delay }}
        >
          {char}
        </motion.span>
      ))}
    </span>
  );
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import {
    GripVertical,
    Trash2,
    Type,
    Palette,
    Image as ImageIcon,
    Save,
    Monitor,
    Eye
} from "lucide-react";
import { toast } from "sonner";
import { supabase } from "@/integrations/supabase/client";

type BlockType = 'text' | 'token' | 'image' | 'heading';

interface DocBlock {
    id: string;
    type: BlockType;
    content: any;
}

export function DocEditor({ designSystemId }: { designSystemId: string }) {
    const [title, setTitle] = useState("Untitled Page");
    const [blocks, setBlocks] = useState<DocBlock[]>([]);
    const [isSaving, setIsSaving] = useState(false);

    const addBlock = (type: BlockType) => {
        const newBlock: DocBlock = {
            id: Math.random().toString(36).substr(2, 9),
            type,
            content: type === 'text' ? "" : type === 'heading' ? "Heading" : {}
        };
        setBlocks([...blocks, newBlock]);
    };

    const updateBlock = (id: string, content: any) => {
        setBlocks(blocks.map(b => b.id === id ? { ...b, content } : b));
    };

    const removeBlock = (id: string) => {
        setBlocks(blocks.filter(b => b.id !== id));
    };

    const handleSave = async () => {
        if (!designSystemId) return;
        setIsSaving(true);
        try {
            const { error } = await supabase
                .from("documentation_pages" as any)
                .upsert({
                    design_system_id: designSystemId,
                    title,
                    slug: title.toLowerCase().replace(/ /g, '-'),
                    content: blocks,
                    is_published: true
                });

            if (error) throw error;
            toast.success("Documentation saved successfully");
        } catch (error: any) {
            toast.error("Error saving: " + error.message);
        } finally {
            setIsSaving(false);
        }
    };

    return (
        <div className="flex flex-col h-full bg-background rounded-xl border border-border overflow-hidden">
            <div className="p-4 border-b bg-card/30 flex items-center justify-between">
                <div className="flex-1 max-w-md">
                    <Input
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="text-xl font-bold bg-transparent border-none focus-visible:ring-0 px-0 h-auto"
                    />
                </div>
                <div className="flex items-center gap-2">
                    <Button variant="outline" size="sm" className="gap-2">
                        <Eye className="h-4 w-4" /> Preview
                    </Button>
                    <Button
                        size="sm"
                        className="gap-2"
                        onClick={handleSave}
                        disabled={isSaving}
                    >
                        <Save className="h-4 w-4" />
                        {isSaving ? "Saving..." : "Save Page"}
                    </Button>
                </div>
            </div>

            <div className="flex-1 overflow-y-auto p-8 max-w-4xl mx-auto w-full space-y-6">
                {blocks.map((block) => (
                    <div key={block.id} className="relative group">
                        <div className="absolute -left-10 top-2 opacity-0 group-hover:opacity-100 transition-opacity flex flex-col gap-1">
                            <Button variant="ghost" size="icon" className="h-6 w-6 cursor-grab" aria-label="Drag block">
                                <GripVertical className="h-3 w-3" />
                            </Button>
                            <Button
                                variant="ghost"
                                size="icon"
                                className="h-6 w-6 text-destructive"
                                onClick={() => removeBlock(block.id)}
                                aria-label="Remove block"
                            >
                                <Trash2 className="h-3 w-3" />
                            </Button>
                        </div>

                        <Card className="border-border/50 group-hover:border-primary/30 transition-colors">
                            <CardContent className="p-4">
                                {block.type === 'heading' && (
                                    <Input
                                        value={block.content}
                                        onChange={(e) => updateBlock(block.id, e.target.value)}
                                        className="text-2xl font-bold border-none focus-visible:ring-0 p-0"
                                        placeholder="Enter heading..."
                                    />
                                )}
                                {block.type === 'text' && (
                                    <textarea
                                        value={block.content}
                                        onChange={(e) => updateBlock(block.id, e.target.value)}
                                        className="w-full min-h-[100px] bg-transparent border-none focus:ring-0 p-0 resize-none text-muted-foreground leading-relaxed"
                                        placeholder="Start typing..."
                                    />
                                )}
                                {block.type === 'token' && (
                                    <div className="p-12 border-2 border-dashed rounded-lg text-center bg-muted/20">
                                        <Palette className="h-8 w-8 mx-auto mb-2 text-primary opacity-50" />
                                        <p className="text-sm font-medium">Token Swatch Area</p>
                                        <p className="text-xs text-muted-foreground">Token picker coming soon</p>
                                    </div>
                                )}
                            </CardContent>
                        </Card>
                    </div>
                ))}

                {blocks.length === 0 && (
                    <div className="text-center py-20 bg-muted/10 rounded-2xl border-2 border-dashed border-border/50">
                        <h3 className="font-bold text-lg mb-2">Build your documentation</h3>
                        <p className="text-muted-foreground text-sm max-w-xs mx-auto mb-6">
                            Add blocks below to document your design tokens, components, and guidelines.
                        </p>
                    </div>
                )}

                <div className="flex justify-center gap-4 pt-8 border-t">
                    <Button variant="outline" size="sm" onClick={() => addBlock('heading')} className="gap-2">
                        <Type className="h-4 w-4" /> Heading
                    </Button>
                    <Button variant="outline" size="sm" onClick={() => addBlock('text')} className="gap-2">
                        <Monitor className="h-4 w-4" /> Text
                    </Button>
                    <Button variant="outline" size="sm" onClick={() => addBlock('token')} className="gap-2">
                        <Palette className="h-4 w-4" /> Token
                    </Button>
                    <Button variant="outline" size="sm" onClick={() => addBlock('image')} className="gap-2">
                        <ImageIcon className="h-4 w-4" /> Image
                    </Button>
                </div>
            </div>
        </div>
    );
}

