    Check,
    ArrowRight,
    Star,
    ShieldCheck,
    Users,
    Download,
    TrendingUp
} from "lucide-react";
import { supabase } from "@/integrations/supabase/client";
import { GeneratedDesignSystem } from "@/types/designSystem";
import { toast } from "sonner";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Skeleton } from "@/components/ui/skeleton";

interface MarketplacePreset {
    id: string;
    name: string;
    description: string;
    author: string;
    category: string;
    icon: string;
    is_premium: boolean;
    price_cents: number;
    preset_data: any;
}

interface MarketplaceProps {
    onImport: (preset: GeneratedDesignSystem) => void;
    currentSystemId?: string;
}

const IconMap: Record<string, any> = {
    Apple: Apple,
    Palette: Palette,
    Wind: Wind,
    ShoppingBag: ShoppingBag
};

export function Marketplace({ onImport, currentSystemId }: MarketplaceProps) {
    const [presets, setPresets] = useState<MarketplacePreset[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [importingId, setImportingId] = useState<string | null>(null);

    useEffect(() => {
        const fetchPresets = async () => {
            const { data, error } = await supabase
                .from("marketplace_presets")
                .select("*")
                .order("is_premium", { ascending: false });

            if (data) setPresets(data);
            setIsLoading(false);
        };

        fetchPresets();
    }, []);

    const handleImport = async (preset: MarketplacePreset) => {
        if (importingId) return;

        setImportingId(preset.id);
        const toastId = toast.loading(`Importing ${preset.name}...`);

        try {
            // If we have a current design system, we update it in Supabase as well
            if (currentSystemId) {
                const { error } = await supabase
                    .from("design_systems")
                    .update({
                        design_system_data: preset.preset_data,
                        name: preset.name
                    } as any)
                    .eq("id", currentSystemId);

                if (error) throw error;
            }

            // Update local state
            onImport(preset.preset_data as GeneratedDesignSystem);

            toast.success(`${preset.name} imported successfully!`, { id: toastId });
        } catch (error: any) {
            toast.error(`Import failed: ${error.message}`, { id: toastId });
        } finally {
            setImportingId(null);
        }
    };

    if (isLoading) {
        return (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[1, 2, 3].map(i => (
                    <Card key={i} className="bg-card/50 border-dashed">
                        <CardHeader>
                            <Skeleton className="h-12 w-12 rounded-xl mb-4" />
                            <Skeleton className="h-6 w-1/2 mb-2" />
                            <Skeleton className="h-4 w-full" />
                        </CardHeader>
                        <CardFooter>
                            <Skeleton className="h-10 w-full" />
                        </CardFooter>
                    </Card>
                ))}
            </div>
        );
    }

    return (
        <div className="space-y-8 max-w-7xl mx-auto">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h2 className="text-3xl font-black flex items-center gap-3">
                        <ShoppingBag className="h-8 w-8 text-primary" />
                        Design Marketplace
                    </h2>
                    <p className="text-muted-foreground mt-1">Professional foundations to accelerate your workflow.</p>
                </div>
                <div className="flex items-center gap-4 p-4 rounded-2xl bg-primary/5 border border-primary/10">
                    <TrendingUp className="h-5 w-5 text-primary" />
                    <div>
                        <p className="text-xs font-bold uppercase tracking-widest text-primary">Pro Tip</p>
                        <p className="text-[10px] text-muted-foreground leading-tight">Presets include full token sets, typography scales, and grid systems.</p>
                    </div>
                </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {presets.map(preset => {
                    const Icon = IconMap[preset.icon] || ShoppingBag;
                    return (
                        <Card key={preset.id} className="group relative overflow-hidden transition-all duration-500 hover:shadow-2xl hover:shadow-primary/5 border-primary/10 hover:border-primary/30 min-h-[300px] flex flex-col">
                            {preset.is_premium && (
                                <div className="absolute top-4 right-4 z-10">
                                    <Badge className="bg-gradient-to-r from-amber-500 to-orange-600 border-none">
                                        <Star className="h-3 w-3 mr-1 fill-white" /> Premium
                                    </Badge>
                                </div>
                            )}

                            <CardHeader className="relative z-10 flex-1">
                                <div className="p-3 rounded-2xl bg-primary/10 w-fit mb-4 group-hover:scale-110 group-hover:rotate-6 transition-transform duration-500">
                                    <Icon className="h-8 w-8 text-primary" />
                                </div>
                                <CardTitle className="text-xl font-bold group-hover:text-primary transition-colors">{preset.name}</CardTitle>
                                <CardDescription className="mt-2 text-sm line-clamp-3 leading-relaxed">
                                    {preset.description}
                                </CardDescription>

                                <div className="mt-6 flex flex-wrap gap-2">
                                    <div className="flex items-center gap-1.5 px-2.5 py-1 rounded-full bg-muted/50 text-[10px] font-bold">
                                        <Users className="h-3 w-3" /> {preset.author}
                                    </div>
                                    <div className="flex items-center gap-1.5 px-2.5 py-1 rounded-full bg-muted/50 text-[10px] font-bold">
                                        <ShieldCheck className="h-3 w-3 text-green-500" /> Verified
                                    </div>
                                </div>
                            </CardHeader>

                            <CardFooter className="relative z-10 pt-4 border-t border-border/40">
                                <Button
                                    className="w-full group/btn relative overflow-hidden h-11"
                                    variant={preset.is_premium ? "default" : "secondary"}
                                    onClick={() => handleImport(preset)}
                                    disabled={importingId !== null}
                                >
                                    <span className="relative z-10 flex items-center justify-center gap-2">
                                        {importingId === preset.id ? (
                                            "Importing..."
                                        ) : (
                                            <>
                                                {preset.is_premium ? `Unlock for $${(preset.price_cents / 100).toFixed(2)}` : "Import Preset"}
                                                <ArrowRight className="h-4 w-4 group-hover/btn:translate-x-1 transition-transform" />
                                            </>
                                        )}
                                    </span>
                                </Button>
                            </CardFooter>

                            {/* Decorative Background */}
                            <div className="absolute -bottom-12 -right-12 h-32 w-32 bg-primary/5 rounded-full blur-3xl group-hover:bg-primary/10 transition-colors" />
                        </Card>
                    );
                })}
            </div>

            <section className="mt-12 p-12 rounded-3xl border border-dashed border-primary/20 bg-muted/30 text-center space-y-4">
                <div className="p-4 rounded-full bg-primary/10 w-fit mx-auto">
                    <Download className="h-8 w-8 text-primary" />
                </div>
                <h3 className="text-xl font-bold">Contribute to the Marketplace</h3>
                <p className="max-w-md mx-auto text-sm text-muted-foreground">
                    Are you a Design System lead? Soon you will be able to publish your own foundations and themes to the DesignForge community.
                </p>
                <Button variant="outline" className="rounded-full">Get Notified</Button>
            </section>
        </div>
    );
}

import { useState, useMemo } from "react";
import { DesignToken } from "@/types/tokens";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
    ShieldCheck,
    AlertTriangle,
    Activity,
    Link as LinkIcon,
    FileWarning,
    RefreshCw,
    CheckCircle2,
    Sparkles,
    Check,
    FileDiff,
    ClipboardList
} from "lucide-react";
import { ApprovalQueue } from "./governance/ApprovalQueue";
import { ApprovalRequest } from "./governance/ApprovalRequest";
import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { runAICopilot, AISuggestion } from "@/lib/ai";
import { toast } from "sonner";
import { Skeleton } from "@/components/ui/skeleton";
import { AuditLogViewer } from "../AuditLogViewer";

// Helper function to check if a value is a valid color
function isValidColor(value: unknown): boolean {
    if (typeof value !== 'string') return false;
    // Check for hex, rgb, rgba, hsl, hsla, or named colors
    const colorPatterns = [
        /^#([0-9A-Fa-f]{3}){1,2}$/,
        /^#([0-9A-Fa-f]{4}){1,2}$/,
        /^rgb\(\s*\d+\s*,\s*\d+\s*,\s*\d+\s*\)$/,
        /^rgba\(\s*\d+\s*,\s*\d+\s*,\s*\d+\s*,\s*[\d.]+\s*\)$/,
        /^hsl\(\s*\d+\s*,\s*[\d.]+%\s*,\s*[\d.]+%\s*\)$/,
        /^hsla\(\s*\d+\s*,\s*[\d.]+%\s*,\s*[\d.]+%\s*,\s*[\d.]+\s*\)$/
    ];
    return colorPatterns.some(pattern => pattern.test(value));
}

interface GovernanceDashboardProps {
    tokens: DesignToken[];
    designSystemId: string;
    onTokenClick: (path: string) => void;
    onRestore: (path: string) => void;
    onPermanentDelete: (path: string) => void;
    onApplyAISuggestion?: (suggestion: AISuggestion) => void;
    onApplyAllSuggestions?: (suggestions: AISuggestion[]) => void;
}

export function GovernanceDashboard({
    tokens,
    designSystemId,
    onTokenClick,
    onRestore,
    onPermanentDelete,
    onApplyAISuggestion,
    onApplyAllSuggestions
}: GovernanceDashboardProps) {
    const [isAuditing, setIsAuditing] = useState(false);
    const [tokenToDelete, setTokenToDelete] = useState<string | null>(null);
    const [aiSuggestions, setAiSuggestions] = useState<AISuggestion[]>([]);
    const [isApprovalRequestOpen, setIsApprovalRequestOpen] = useState(false);

    // Identify Changed Tokens (Drafts)
    const changedTokens = useMemo(() => {
        return tokens.filter(t => t.syncStatus === 'changed' || (t.publishedValue === undefined && t.value !== undefined));
    }, [tokens]);

    // 1. Identify Unused Tokens (Tokens not referenced by any other token)
    // Note: In a real app, we'd also check if they are used in components, but here we check internal refs.
    const unusedTokens = useMemo(() => {
        const referencedPaths = new Set<string>();
        tokens.forEach(t => {
            if (t.ref) {
                referencedPaths.add(t.ref.replace(/[{}]/g, ''));
            }
        });

        return tokens.filter(t => !referencedPaths.has(t.path));
    }, [tokens]);

    // 2. Identify Duplicate Values
    const duplicates = useMemo(() => {
        const valueMap = new Map<string, string[]>();
        tokens.forEach(t => {
            if (t.ref) return; // Skip aliases
            const valStr = JSON.stringify(t.value);
            if (!valueMap.has(valStr)) {
                valueMap.set(valStr, []);
            }
            valueMap.get(valStr)!.push(t.path);
        });

        const result: { value: any, paths: string[] }[] = [];
        valueMap.forEach((paths, value) => {
            if (paths.length > 1) {
                result.push({ value: JSON.parse(value), paths });
            }
        });
        return result;
    }, [tokens]);

    // 3. Health Score Calculation
    const healthScore = useMemo(() => {
        if (tokens.length === 0) return 100;
        const duplicatePenalty = duplicates.length * 5;
        const unusedPenalty = (unusedTokens.length / tokens.length) * 20;
        return Math.max(0, Math.min(100, 100 - duplicatePenalty - unusedPenalty)).toFixed(0);
    }, [tokens, duplicates, unusedTokens]);

    const handleAIAudit = async () => {
        setIsAuditing(true);
        try {
            // Only send relevant data for audit to save tokens/tokens
            const auditData = tokens.map(t => ({
                path: t.path,
                value: t.value,
                type: t.type,
                name: t.name
            }));

            const result = await runAICopilot('audit', auditData);
            if (result && result.suggestions) {
                setAiSuggestions(result.suggestions);
                toast.success("AI Audit complete!");
            }
        } catch (error) {
            toast.error("Failed to run AI Audit");
        } finally {
            setIsAuditing(false);
        }
    };

    return (
        <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {/* Health Score Card */}
                <Card className="bg-primary/5 border-primary/10">
                    <CardHeader className="pb-2">
                        <CardTitle className="text-sm font-bold uppercase tracking-widest text-primary flex items-center gap-2">
                            <Activity className="h-4 w-4" /> Health Score
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="flex items-end gap-2">
                            <span className="text-5xl font-black">{healthScore}%</span>
                            <Badge variant="outline" className="mb-2 bg-background border-primary/20">
                                {Number(healthScore) > 80 ? 'EXCELLENT' : Number(healthScore) > 50 ? 'GOOD' : 'POOR'}
                            </Badge>
                        </div>
                        <p className="text-xs text-muted-foreground mt-2 font-medium">
                            Based on duplicate values and internal referencing
                        </p>
                    </CardContent>
                </Card>

                {/* Unpublished Changes Status */}
                <Card className={changedTokens.length > 0 ? "border-primary/50 bg-primary/5 shadow-sm" : ""}>
                    <CardHeader className="pb-2">
                        <CardTitle className="text-sm font-bold uppercase tracking-widest text-primary flex items-center gap-2">
                            <FileDiff className="h-4 w-4" /> Unpublished Changes
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="flex items-end gap-2">
                            <span className="text-5xl font-black">{changedTokens.length}</span>
                            <span className="text-xs text-muted-foreground mb-2 font-medium bg-background px-2 py-0.5 rounded border">
                                DRAFT STATE
                            </span>
                        </div>
                        <Button
                            className="w-full mt-4 gap-2 font-bold"
                            disabled={changedTokens.length === 0}
                            onClick={() => setIsApprovalRequestOpen(true)}
                        >
                            <ClipboardList className="h-4 w-4" />
                            Request Approval
                        </Button>
                    </CardContent>
                </Card>

                {/* Duplicates Status */}
                <Card className={duplicates.length > 0 ? "border-yellow-500/20 bg-yellow-500/5" : ""}>
                    <CardHeader className="pb-2">
                        <CardTitle className="text-sm font-bold uppercase tracking-widest flex items-center gap-2">
                            <AlertTriangle className={`h-4 w-4 ${duplicates.length > 0 ? "text-yellow-500" : "text-green-500"}`} />
                            Duplicates
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="text-3xl font-bold">{duplicates.length}</div>
                        <p className="text-xs text-muted-foreground mt-1 font-medium">
                            Value groups that should be aliased
                        </p>
                    </CardContent>
                </Card>

                {/* Unused Tokens Status */}
                <Card>
                    <CardHeader className="pb-2">
                        <CardTitle className="text-sm font-bold uppercase tracking-widest flex items-center gap-2">
                            <ShieldCheck className="h-4 w-4 text-primary" />
                            Orphan Tokens
                        </CardTitle>
                    </CardHeader>
                    <CardContent className="flex items-center justify-between">
                        <div>
                            <div className="text-3xl font-bold">{unusedTokens.length}</div>
                            <p className="text-xs text-muted-foreground mt-1 font-medium">
                                Tokens not referenced
                            </p>
                        </div>
                        <Button
                            variant="secondary"
                            size="sm"
                            className="gap-2 font-bold h-9"
                            onClick={handleAIAudit}
                            disabled={isAuditing}
                        >
                            {isAuditing ? <RefreshCw className="h-4 w-4 animate-spin" /> : <Sparkles className="h-4 w-4" />}
                            AI Audit
                        </Button>
                    </CardContent>
                </Card>
            </div>

            {/* AI Suggestions Section */}
            {(isAuditing || aiSuggestions.length > 0) && (
                <Card className="border-primary/20 bg-primary/5 animate-in slide-in-from-top duration-500">
                    <CardHeader className="pb-4">
                        <div className="flex items-center justify-between">
                            <div>
                                <CardTitle className="text-base font-bold flex items-center gap-2">
                                    <Sparkles className="h-4 w-4 text-primary" />
                                    AI Architect Suggestions
                                </CardTitle>
                                <CardDescription>Intelligent recommendations for token consolidation and naming.</CardDescription>
                            </div>
                            <div className="flex gap-2">
                                <Button
                                    size="sm"
                                    variant="secondary"
                                    className="h-8 font-bold gap-2"
                                    onClick={() => onApplyAllSuggestions?.(aiSuggestions)}
                                    disabled={aiSuggestions.length === 0}
                                >
                                    <CheckCircle2 className="h-3.5 w-3.5" />
                                    Apply All
                                </Button>
                                <Badge variant="secondary" className="bg-primary text-primary-foreground hidden sm:flex">BETA</Badge>
                            </div>
                        </div>
                    </CardHeader>
                    <CardContent>
                        {isAuditing ? (
                            <div className="space-y-4">
                                <Skeleton className="h-16 w-full" />
                                <Skeleton className="h-16 w-full" />
                            </div>
                        ) : (
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                {aiSuggestions.map((s, i) => (
                                    <div key={i} className="p-4 rounded-xl bg-background border border-primary/10 flex flex-col gap-3 shadow-sm">
                                        <div className="flex justify-between items-start">
                                            <div>
                                                <Badge className="text-[10px] uppercase mb-1">{s.type}</Badge>
                                                <p className="text-sm font-bold leading-tight">{s.newName}</p>
                                            </div>
                                            <Button
                                                variant="outline"
                                                size="sm"
                                                className="h-7 text-[10px] font-bold gap-1 bg-primary/5 border-primary/20 hover:bg-primary/10"
                                                onClick={() => onApplyAISuggestion?.(s)}
                                            >
                                                <Check className="h-3 w-3" /> Apply
                                            </Button>
                                        </div>
                                        <p className="text-xs text-muted-foreground italic">"{s.reason}"</p>
                                        {s.tokensToAlias && (
                                            <div className="flex flex-wrap gap-1 mt-1">
                                                {s.tokensToAlias.map(t => (
                                                    <span key={t} className="text-[9px] px-1.5 py-0.5 rounded bg-muted border font-mono">{t}</span>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                        )}
                    </CardContent>
                </Card>
            )}

            <ApprovalQueue designSystemId={designSystemId} />
            <ApprovalRequest
                designSystemId={designSystemId}
                changedTokens={changedTokens}
                open={isApprovalRequestOpen}
                onOpenChange={setIsApprovalRequestOpen}
            />

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Detailed Duplicate Report */}
                <Card className="flex flex-col max-h-[500px]">
                    <CardHeader>
                        <CardTitle className="text-base font-bold flex items-center gap-2">
                            <FileWarning className="h-4 w-4 text-yellow-500" />
                            Redundancy Report
                        </CardTitle>
                        <CardDescription>Tokens sharing identical values (candidates for aliasing)</CardDescription>
                    </CardHeader>
                    <CardContent className="p-0 overflow-hidden">
                        <ScrollArea className="h-[350px] px-6">
                            <div className="space-y-4 pb-6">
                                {duplicates.length === 0 ? (
                                    <div className="py-12 text-center text-muted-foreground">
                                        <CheckCircle2 className="h-12 w-12 mx-auto mb-4 opacity-20 text-green-500" />
                                        <p>No duplicate values found!</p>
                                    </div>
                                ) : (
                                    duplicates.map((dup, i) => (
                                        <div key={i} className="p-3 rounded-lg border bg-muted/30 space-y-2">
                                            <div className="flex items-center justify-between">
                                                <div className="flex items-center gap-2">
                                                    <div className="w-3 h-3 rounded border" style={{ backgroundColor: isValidColor(dup.value) ? dup.value : 'transparent' }} />
                                                    <code className="text-xs font-mono">{JSON.stringify(dup.value)}</code>
                                                </div>
                                                <div className="flex items-center gap-2">
                                                    <Button
                                                        variant="ghost"
                                                        size="sm"
                                                        className="h-6 text-[10px] font-bold gap-1 text-primary hover:text-primary hover:bg-primary/10"
                                                        onClick={() => {
                                                            const newName = dup.paths[0].split('.').slice(0, -1).join('.') + '.consolidated';
                                                            onApplyAISuggestion?.({
                                                                type: 'consolidate',
                                                                newName: newName,
                                                                tokensToAlias: dup.paths,
                                                                reason: "Consolidated identical values into a single source",
                                                                value: dup.value
                                                            });
                                                        }}
                                                    >
                                                        <Sparkles className="h-3 w-3" /> Consolidate
                                                    </Button>
                                                    <Badge variant="outline" className="text-[10px]">{dup.paths.length} tokens</Badge>
                                                </div>
                                            </div>
                                            <div className="flex flex-wrap gap-1">
                                                {dup.paths.map(path => (
                                                    <button
                                                        key={path}
                                                        onClick={() => onTokenClick(path)}
                                                        className="text-[9px] px-1.5 py-0.5 rounded bg-background border hover:border-primary transition-colors font-mono"
                                                    >
                                                        {path}
                                                    </button>
                                                ))}
                                            </div>
                                        </div>
                                    ))
                                )}
                            </div>
                        </ScrollArea>
                    </CardContent>
                </Card>

                {/* Internal Usage Tree / Reachability */}
                <Card className="flex flex-col max-h-[500px]">
                    <CardHeader>
                        <CardTitle className="text-base font-bold flex items-center gap-2">
                            <LinkIcon className="h-4 w-4 text-primary" />
                            Token Hierarchy Depth
                        </CardTitle>
                        <CardDescription>Deeply nested alias chains and dependency levels</CardDescription>
                    </CardHeader>
                    <CardContent className="p-0 overflow-hidden">
                        <ScrollArea className="h-[350px] px-6">
                            <div className="space-y-3 pb-6">
                                {tokens.filter(t => t.ref).sort((a, b) => (b.ref?.length || 0) - (a.ref?.length || 0)).slice(0, 10).map(t => (
                                    <div key={t.path} className="flex items-center justify-between p-2 rounded border bg-card/50">
                                        <div className="flex-1 min-w-0">
                                            <p className="text-xs font-bold truncate">{t.name}</p>
                                            <p className="text-[10px] text-muted-foreground truncate font-mono">{t.path}</p>
                                        </div>
                                        <div className="flex items-center gap-2 shrink-0">
                                            <span className="text-[10px] text-muted-foreground">ΓåÆ</span>
                                            <code className="text-[10px] bg-primary/10 text-primary px-1.5 py-0.5 rounded border border-primary/20 font-mono">
                                                {t.ref?.replace(/[{}]/g, '')}
                                            </code>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </ScrollArea>
                    </CardContent>
                </Card>
            </div>

            {/* Trash / Archived Tokens Section */}
            <Card className="border-destructive/20 bg-destructive/5">
                <CardHeader>
                    <CardTitle className="text-base font-bold flex items-center gap-2">
                        <FileWarning className="h-4 w-4 text-destructive" />
                        Archived Tokens (Trash)
                    </CardTitle>
                    <CardDescription>Soft-deleted tokens that can be restored or permanently removed</CardDescription>
                </CardHeader>
                <CardContent>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {tokens.filter(t => t.status === 'archived').length === 0 ? (
                            <div className="col-span-full py-8 text-center text-muted-foreground italic text-xs">
                                Trash is empty.
                            </div>
                        ) : (
                            tokens.filter(t => t.status === 'archived').map(t => (
                                <div key={t.path} className="p-3 rounded-lg border bg-background flex flex-col gap-3">
                                    <div className="flex justify-between items-start">
                                        <div className="min-w-0">
                                            <p className="text-xs font-bold truncate">{t.name}</p>
                                            <p className="text-[10px] text-muted-foreground truncate font-mono">{t.path}</p>
                                        </div>
                                        <Badge variant="outline" className="text-[8px] uppercase">{t.type}</Badge>
                                    </div>
                                    <div className="flex gap-2 mt-auto">
                                        <Button
                                            variant="outline"
                                            size="sm"
                                            className="h-7 text-[10px] flex-1 font-bold"
                                            onClick={() => onRestore(t.path)}
                                        >
                                            <RefreshCw className="h-3 w-3 mr-1" /> Restore
                                        </Button>
                                        <Button
                                            variant="destructive"
                                            size="sm"
                                            className="h-7 text-[10px] flex-1 font-bold"
                                            onClick={() => setTokenToDelete(t.path)}
                                        >
                                            Delete
                                        </Button>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </CardContent>
            </Card>

            <AuditLogViewer designSystemId={designSystemId} />

            <AlertDialog open={!!tokenToDelete} onOpenChange={(open) => !open && setTokenToDelete(null)}>
                <AlertDialogContent>
                    <AlertDialogHeader>
                        <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
                        <AlertDialogDescription>
                            This action cannot be undone. This will permanently delete the token
                            <span className="font-mono font-bold mx-1">{tokenToDelete}</span>
                            from the design system.
                        </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                        <AlertDialogAction
                            onClick={() => {
                                if (tokenToDelete) onPermanentDelete(tokenToDelete);
                                setTokenToDelete(null);
                            }}
                            className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                        >
                            Delete Permanently
                        </AlertDialogAction>
                    </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>
        </div>
    );
}

import { useState } from "react";
import { DesignToken } from "@/types/tokens";
import { BrandTheme } from "@/hooks/useBrands";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";


import {
    Layers,
    MousePointer2,
    Split,
    Eye,
    Info,
    AlertCircle,
    ChevronRight,
    Search
} from "lucide-react";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue
} from "@/components/ui/select";
import { motion } from "framer-motion";

interface TokenCompareSandboxProps {
    allTokens: DesignToken[];
    brands: BrandTheme[];
    onTokenClick?: (path: string) => void;
}

export function TokenCompareSandbox({ allTokens, brands, onTokenClick }: TokenCompareSandboxProps) {
    const [primaryBrandId, setPrimaryBrandId] = useState<string | null>(brands[0]?.id || null);
    const [comparisonBrandId, setComparisonBrandId] = useState<string | null>(brands[1]?.id || null);
    const [hoveredTokenPath, setHoveredTokenPath] = useState<string | null>(null);

    const primaryBrand = brands.find(b => b.id === primaryBrandId);
    const comparisonBrand = brands.find(b => b.id === comparisonBrandId);

    // Helper to resolve token value with brand override
    const resolveTokenValue = (path: string, brand?: BrandTheme | null): any => {
        if (!brand) {
            const token = allTokens.find(t => t.path === path);
            return token?.value || null;
        }

        // Check for override first
        if (brand.tokens_override && brand.tokens_override[path]) {
            return brand.tokens_override[path];
        }

        const token = allTokens.find(t => t.path === path);
        if (!token) return null;

        // If it's an alias, we need to resolve it recursively, 
        // but we also need to check for overrides at each step of the alias chain.
        if (token.ref) {
            const refPath = token.ref.replace(/[{}]/g, '');
            return resolveTokenValue(refPath, brand);
        }

        return token.value;
    };

    const SandboxPreview = ({ brand, label }: { brand?: BrandTheme | null, label: string }) => {
        const bg = resolveTokenValue('background.primary', brand) || 'transparent';
        const text = resolveTokenValue('text.primary', brand) || 'inherit';
        const surface = resolveTokenValue('surface.primary', brand) || 'rgba(0,0,0,0.05)';
        const border = resolveTokenValue('border.primary', brand) || 'rgba(0,0,0,0.1)';
        const primary = resolveTokenValue('color.primary', brand) || '#6366f1';
        const primaryText = resolveTokenValue('text.onPrimary', brand) || '#ffffff';
        const buttonRadius = resolveTokenValue('radius.button', brand) || '0.5rem';
        const cardRadius = resolveTokenValue('radius.card', brand) || '1rem';

        return (
            <div className="flex-1 min-w-[300px] flex flex-col gap-4">
                <div className="flex items-center justify-between px-2">
                    <Badge variant="outline" className="font-bold text-[10px] uppercase tracking-widest px-2 py-0.5">
                        {label}: {brand?.name || "Base tokens"}
                    </Badge>
                    <span className="text-[10px] text-muted-foreground uppercase font-medium">
                        Mode: {brand?.mode || "standard"}
                    </span>
                </div>

                <div
                    className="p-8 rounded-2xl border transition-all duration-500 shadow-sm min-h-[400px]"
                    style={{
                        backgroundColor: bg,
                        color: text,
                        borderColor: border,
                    }}
                >
                    <div className="space-y-8">
                        {/* 1. Header Section */}
                        <div className="space-y-2">
                            <h3 className="text-2xl font-bold tracking-tight">Main Heading</h3>
                            <p className="opacity-70 text-sm leading-relaxed max-w-sm">
                                This component reacts to your semantic tokens. Change them in the list and watch the transformation.
                            </p>
                        </div>

                        {/* 2. Interactive Area */}
                        <div
                            className="p-6 border-2 border-dashed rounded-xl flex flex-col gap-4 transition-colors"
                            style={{
                                backgroundColor: surface,
                                borderColor: border
                            }}
                        >
                            <div className="flex flex-wrap gap-3">
                                <button
                                    className="px-6 py-2.5 font-semibold text-sm transition-all hover:scale-105 active:scale-95 shadow-lg shadow-primary/10"
                                    style={{
                                        backgroundColor: primary,
                                        color: primaryText,
                                        borderRadius: buttonRadius
                                    }}
                                    onMouseEnter={() => setHoveredTokenPath('color.primary')}
                                    onMouseLeave={() => setHoveredTokenPath(null)}
                                >
                                    Primary Action
                                </button>
                                <button
                                    className="px-6 py-2.5 font-semibold text-sm border-2 transition-all hover:bg-muted/10 active:scale-95"
                                    style={{
                                        borderColor: primary,
                                        color: primary,
                                        borderRadius: buttonRadius
                                    }}
                                    onMouseEnter={() => setHoveredTokenPath('radius.button')}
                                    onMouseLeave={() => setHoveredTokenPath(null)}
                                >
                                    Secondary
                                </button>
                            </div>

                            <div className="flex items-center gap-4 mt-2">
                                <div className="flex -space-x-2">
                                    {[1, 2, 3].map(i => (
                                        <div
                                            key={i}
                                            className="w-8 h-8 rounded-full border-2 border-background bg-muted flex items-center justify-center text-[10px] font-bold"
                                            style={{ backgroundColor: surface, borderColor: bg }}
                                        >
                                            {i}
                                        </div>
                                    ))}
                                </div>
                                <span className="text-xs font-medium opacity-60">Collaborators</span>
                            </div>
                        </div>

                        {/* 3. Card Preview */}
                        <div
                            className="p-6 border transition-all duration-300 hover:shadow-xl group"
                            style={{
                                backgroundColor: bg,
                                borderColor: border,
                                borderRadius: cardRadius
                            }}
                        >
                            <div className="flex items-start justify-between mb-4">
                                <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center">
                                    <Layers className="h-5 w-5 text-primary" />
                                </div>
                                <Badge variant="secondary" className="text-[9px]">ACTIVE</Badge>
                            </div>
                            <h4 className="font-bold mb-2">Semantic Structure</h4>
                            <p className="text-xs opacity-60 leading-normal">
                                Cards are a great way to test your elevation and surface token contrasts.
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <div className="flex flex-col h-full bg-muted/20 rounded-xl border border-border/50 overflow-hidden">
            {/* Top Bar */}
            <div className="p-4 border-b bg-background/50 backdrop-blur-md flex items-center justify-between flex-wrap gap-4">
                <div className="flex items-center gap-4">
                    <div className="flex items-center gap-2">
                        <div className="p-1.5 bg-primary/10 rounded-lg">
                            <Split className="h-4 w-4 text-primary" />
                        </div>
                        <h2 className="text-sm font-bold uppercase tracking-tight">Visual Interrogator</h2>
                    </div>

                    <div className="h-4 w-px bg-border mx-2 hidden sm:block" />

                    <div className="flex items-center gap-3">
                        <div className="flex items-center gap-2">
                            <label className="text-[10px] font-bold text-muted-foreground uppercase whitespace-nowrap">Primary Brand</label>
                            <Select value={primaryBrandId || "none"} onValueChange={setPrimaryBrandId}>
                                <SelectTrigger className="h-8 w-[140px] text-xs font-medium border-primary/20 bg-primary/5">
                                    <SelectValue placeholder="Base System" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="none">Base System</SelectItem>
                                    {brands.map(b => (
                                        <SelectItem key={b.id} value={b.id}>{b.name}</SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>

                        <div className="flex items-center gap-2">
                            <label className="text-[10px] font-bold text-muted-foreground uppercase whitespace-nowrap">Compare With</label>
                            <Select value={comparisonBrandId || "none"} onValueChange={setComparisonBrandId}>
                                <SelectTrigger className="h-8 w-[140px] text-xs font-medium">
                                    <SelectValue placeholder="Comparison" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="none">None (Single View)</SelectItem>
                                    {brands.filter(b => b.id !== primaryBrandId).map(b => (
                                        <SelectItem key={b.id} value={b.id}>{b.name}</SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>
                    </div>
                </div>

                <div className="flex items-center gap-2">
                    <Button variant="ghost" size="sm" className="h-8 text-[10px] font-bold uppercase tracking-tighter gap-2">
                        <Eye className="h-3.5 w-3.5" />
                        Live Update
                    </Button>
                </div>
            </div>

            {/* Sandbox Content */}
            <div className="flex-1 flex flex-col md:flex-row gap-6 p-6 overflow-auto scrollbar-thin">
                <SandboxPreview brand={primaryBrand} label="Primary View" />

                {comparisonBrandId !== "none" && (
                    <motion.div
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="flex flex-1"
                    >
                        <SandboxPreview brand={comparisonBrand} label="Comparison View" />
                    </motion.div>
                )}

                {/* Interrogator Sidebar */}
                <div className="w-full md:w-64 flex flex-col gap-4">
                    <Card className="border-primary/20 bg-primary/5 shadow-none h-fit">
                        <CardHeader className="p-4 pb-2">
                            <CardTitle className="text-xs flex items-center gap-2 text-primary">
                                <Info className="h-3 w-3" />
                                Token Inspector
                            </CardTitle>
                        </CardHeader>
                        <CardContent className="p-4 pt-0">
                            {hoveredTokenPath ? (
                                <div className="space-y-3 animate-in fade-in slide-in-from-right-2 duration-300">
                                    <div>
                                        <label className="text-[9px] font-bold text-muted-foreground uppercase">Path</label>
                                        <div className="font-mono text-[10px] bg-primary/10 text-primary p-1 rounded mt-1 border border-primary/20 truncate">
                                            {hoveredTokenPath}
                                        </div>
                                    </div>
                                    <div className="grid grid-cols-2 gap-2">
                                        <div>
                                            <label className="text-[9px] font-bold text-muted-foreground uppercase">Brand A</label>
                                            <div className="text-[10px] font-bold truncate">
                                                {resolveTokenValue(hoveredTokenPath, primaryBrand)}
                                            </div>
                                        </div>
                                        {comparisonBrandId !== "none" && (
                                            <div>
                                                <label className="text-[9px] font-bold text-muted-foreground uppercase">Brand B</label>
                                                <div className="text-[10px] font-bold truncate">
                                                    {resolveTokenValue(hoveredTokenPath, comparisonBrand)}
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                    <Button
                                        variant="link"
                                        size="sm"
                                        className="h-auto p-0 text-[10px] text-primary"
                                        onClick={() => onTokenClick?.(hoveredTokenPath)}
                                    >
                                        Edit Token <ChevronRight className="h-2.5 w-2.5 ml-1" />
                                    </Button>
                                </div>
                            ) : (
                                <div className="flex flex-col items-center justify-center p-8 text-center gap-2 opacity-40">
                                    <MousePointer2 className="h-8 w-8" />
                                    <p className="text-[10px] font-medium leading-tight">
                                        Hover over elements in the preview to inspect their tokens
                                    </p>
                                </div>
                            )}
                        </CardContent>
                    </Card>

                    <Card className="bg-muted/40 shadow-none border-dashed">
                        <CardHeader className="p-4 pb-2">
                            <CardTitle className="text-xs flex items-center gap-2">
                                <Search className="h-3 w-3" />
                                Quick Verify
                            </CardTitle>
                        </CardHeader>
                        <CardContent className="p-4 pt-0 space-y-2">
                            <p className="text-[9px] text-muted-foreground">Verify contrast ratios and semantic mappings automatically.</p>
                            <Button variant="outline" size="sm" className="w-full text-[10px] h-7 gap-2">
                                <AlertCircle className="h-3 w-3" />
                                Audit Contrast
                            </Button>
                        </CardContent>
                    </Card>
                </div>
            </div>
        </div>
    );
}

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormLabel,
    FormMessage,
    FormDescription
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue
} from "@/components/ui/select";
import { DesignToken, TokenType } from "@/types/tokens";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

import { Badge } from "@/components/ui/badge";
import { Palette, Type, Square, Timer, Box, Layers, Image as ImageIcon } from "lucide-react";
import { ColorPicker } from "@/components/ui/color-picker";

const tokenSchema = z.object({
    name: z.string().min(1, "Name is required"),
    path: z.string().min(1, "Path is required"),
    type: z.string(),
    description: z.string().optional(),
    ref: z.string().optional(),
    value: z.any(), // Flexible based on type
    status: z.enum(['draft', 'published', 'deprecated']).default('draft'),
});

interface TokenEditorProps {
    token?: DesignToken;
    allTokens?: DesignToken[];
    onSave: (token: DesignToken) => void;
    onCancel: () => void;
}

export function TokenEditor({ token, allTokens = [], onSave, onCancel }: TokenEditorProps) {
    const [selectedType, setSelectedType] = useState<TokenType>(token?.type || 'color');

    const form = useForm<any>({
        resolver: zodResolver(tokenSchema),
        defaultValues: token || {
            name: "",
            path: "",
            type: "color",
            description: "",
            value: "#000000",
            status: "draft",
        },
    });

    const onSubmit = (data: any) => {
        // Clean up data before saving
        const submissionData = { ...data };
        if (submissionData.ref) {
            // If it's a reference, ensure it has the {} syntax if needed, 
            // but DTCG standard is just the path or {path}. We'll support both.
            if (!submissionData.ref.startsWith('{')) {
                submissionData.ref = `{${submissionData.ref}}`;
            }
        }
        onSave(submissionData as DesignToken);
    };

    const renderValueEditor = () => {
        switch (selectedType) {
            case 'color':
                return (
                    <div className="space-y-4">
                        <FormField
                            control={form.control}
                            name="value"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Color Value</FormLabel>
                                    <FormControl>
                                        <ColorPicker color={field.value} onChange={field.onChange} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                    </div>
                );

            case 'dimension':
            case 'spacing':
            case 'borderRadius':
            case 'borderWidth':
                return (
                    <FormField
                        control={form.control}
                        name="value"
                        render={({ field }) => (
                            <FormItem>
                                <FormLabel>Value</FormLabel>
                                <FormControl>
                                    <Input {...field} placeholder="e.g. 16px, 1rem, 4" />
                                </FormControl>
                                <FormDescription>Include units (px, rem, %, etc.)</FormDescription>
                                <FormMessage />
                            </FormItem>
                        )}
                    />
                );

            case 'typography':
                return (
                    <div className="grid grid-cols-2 gap-4">
                        {/* Simplified for now, will expand to use full TypographyToken structure */}
                        <FormField
                            control={form.control}
                            name={"value.fontFamily" as any}
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Font Family</FormLabel>
                                    <FormControl>
                                        <Input {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name={"value.fontSize" as any}
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Size</FormLabel>
                                    <FormControl>
                                        <Input {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />
                    </div>
                );

            default:
                return (
                    <div className="p-4 border border-dashed rounded-lg bg-muted/50 text-center">
                        Editor for <strong>{selectedType}</strong> coming soon.
                    </div>
                );
        }
    };

    const getTokenIcon = (type: TokenType) => {
        switch (type) {
            case 'color': return <Palette className="h-4 w-4" />;
            case 'typography': return <Type className="h-4 w-4" />;
            case 'dimension': return <Square className="h-4 w-4" />;
            case 'duration': return <Timer className="h-4 w-4" />;
            case 'shadow': return <Layers className="h-4 w-4" />;
            case 'asset': return <ImageIcon className="h-4 w-4" />;
            default: return <Box className="h-4 w-4" />;
        }
    };

    return (
        <Card className="w-full max-w-2xl mx-auto shadow-lg border-primary/20 backdrop-blur-sm bg-card/95">
            <CardHeader>
                <div className="flex items-center justify-between">
                    <CardTitle className="text-xl font-bold flex items-center gap-2">
                        {token ? "Edit Token" : "Create New Token"}
                        <Badge variant="outline" className="ml-2 capitalize">
                            {selectedType}
                        </Badge>
                        {token?.syncStatus === 'changed' && (
                            <Badge variant="outline" className="ml-2 text-orange-500 border-orange-500/20 bg-orange-500/5">
                                Unsaved Changes
                            </Badge>
                        )}
                    </CardTitle>
                    {getTokenIcon(selectedType)}
                </div>
            </CardHeader>
            <CardContent>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                        <div className="grid grid-cols-2 gap-4">
                            <FormField
                                control={form.control}
                                name="name"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Token Name</FormLabel>
                                        <FormControl>
                                            <Input placeholder="Primary Color" {...field} />
                                        </FormControl>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                            <FormField
                                control={form.control}
                                name="path"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Token Path</FormLabel>
                                        <FormControl>
                                            <Input placeholder="color.brand.primary" {...field} />
                                        </FormControl>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />
                        </div>

                        <FormField
                            control={form.control}
                            name="type"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Token Type</FormLabel>
                                    <Select
                                        onValueChange={(val) => {
                                            field.onChange(val);
                                            setSelectedType(val as TokenType);
                                        }}
                                        defaultValue={field.value}
                                    >
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a type" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            <SelectItem value="color">Color</SelectItem>
                                            <SelectItem value="dimension">Dimension</SelectItem>
                                            <SelectItem value="typography">Typography</SelectItem>
                                            <SelectItem value="spacing">Spacing</SelectItem>
                                            <SelectItem value="borderRadius">Border Radius</SelectItem>
                                            <SelectItem value="shadow">Shadow</SelectItem>
                                            <SelectItem value="gradient">Gradient</SelectItem>
                                            <SelectItem value="duration">Duration</SelectItem>
                                            <SelectItem value="composition">Composition</SelectItem>
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="description"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Description (Optional)</FormLabel>
                                    <FormControl>
                                        <Input {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="status"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Token Status</FormLabel>
                                    <Select
                                        onValueChange={field.onChange}
                                        defaultValue={field.value}
                                    >
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Select a status" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            <SelectItem value="draft">Draft (Experimental)</SelectItem>
                                            <SelectItem value="published">Published (Stable)</SelectItem>
                                            <SelectItem value="deprecated">Deprecated (Do not use)</SelectItem>
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        {/* Token Aliasing / Reference Picker */}
                        <div className="pt-4 border-t space-y-4">
                            <div className="flex items-center justify-between">
                                <h3 className="text-sm font-semibold text-muted-foreground uppercase tracking-wider">
                                    Token Aliasing (Reference)
                                </h3>
                                <Badge variant="outline" className="text-[10px]">DTCG COMPLIANT</Badge>
                            </div>

                            <FormField
                                control={form.control}
                                name="ref"
                                render={({ field }) => (
                                    <FormItem>
                                        <FormLabel>Reference another token</FormLabel>
                                        <Select
                                            onValueChange={(val) => field.onChange(val === "none" ? "" : val)}
                                            value={field.value?.replace(/[{}]/g, '') || "none"}
                                        >
                                            <FormControl>
                                                <SelectTrigger className="bg-muted/30">
                                                    <SelectValue placeholder="Select a token to alias" />
                                                </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                                <SelectItem value="none">Literal Value (No Alias)</SelectItem>
                                                {allTokens
                                                    .filter(t => t.path !== token?.path) // Prevent self-reference
                                                    .map(t => (
                                                        <SelectItem key={t.path} value={t.path}>
                                                            <div className="flex items-center gap-2">
                                                                <div className="w-2 h-2 rounded-full bg-primary" />
                                                                <span>{t.path}</span>
                                                                <span className="text-[10px] text-muted-foreground uppercase">({t.type})</span>
                                                            </div>
                                                        </SelectItem>
                                                    ))
                                                }
                                            </SelectContent>
                                        </Select>
                                        <FormDescription>
                                            Aliasing allows you to create semantic references (e.g., button.primary ΓåÆ brand.blue).
                                        </FormDescription>
                                        <FormMessage />
                                    </FormItem>
                                )}
                            />

                            {form.watch("ref") && (
                                <div className="p-3 rounded-xl bg-primary/5 border border-primary/20 animate-in fade-in slide-in-from-top-2 duration-300">
                                    <p className="text-xs font-medium text-primary flex items-center gap-2">
                                        <Layers className="h-3 w-3" />
                                        Alias Chain:
                                    </p>
                                    <div className="mt-2 flex items-center gap-2 text-[10px] font-mono text-muted-foreground overflow-x-auto whitespace-nowrap pb-1">
                                        <span className="text-foreground font-bold">{form.watch("path") || "new-token"}</span>
                                        <span>ΓåÆ</span>
                                        <span className="bg-primary/10 text-primary px-1.5 py-0.5 rounded border border-primary/20">
                                            {form.watch("ref").replace(/[{}]/g, '')}
                                        </span>
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="pt-4 border-t">
                            <h3 className="text-sm font-semibold mb-4 text-muted-foreground uppercase tracking-wider">
                                Value Configuration
                            </h3>
                            {renderValueEditor()}
                        </div>

                        <div className="flex justify-end gap-3 pt-6">
                            <Button type="button" variant="outline" onClick={onCancel}>
                                Cancel
                            </Button>
                            <Button type="submit" className="bg-primary hover:bg-primary/90">
                                {token ? "Update Token" : "Create Token"}
                            </Button>
                        </div>
                    </form>
                </Form>
            </CardContent>
        </Card>
    );
}

