import { useState } from "react";
import { DesignToken, TokenType } from "@/types/tokens";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
    Search,
    Filter,
    Plus,
    MoreVertical,
    Edit2,
    Trash2,
    Copy,
    GripVertical
} from "lucide-react";
import { Button } from "@/components/ui/button";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Reorder } from "framer-motion";

interface TokenListProps {
    tokens: DesignToken[];
    onEdit: (token: DesignToken) => void;
    onDelete: (tokenPath: string) => void;
    onAdd: () => void;
    onReorder?: (newTokens: DesignToken[]) => void;
}

import { VectorTokenSearch } from "@/lib/ai/VectorTokenSearch";

// ...

export function TokenList({ tokens, onEdit, onDelete, onAdd, onReorder }: TokenListProps) {
    const [search, setSearch] = useState("");
    const [filterType, setFilterType] = useState<TokenType | "all">("all");

    // Use Vector Search for smarter filtering
    const searchEngine = new VectorTokenSearch(tokens);
    let filteredTokens = tokens;

    if (search) {
        const results = searchEngine.search(search);
        // Map back to original tokens
        filteredTokens = results
            .map(r => tokens.find(t => t.path === r.path)!)
            .filter(Boolean);
    }

    // Apply Type Filter on top
    filteredTokens = filteredTokens.filter(t => {
        const matchesType = filterType === "all" || t.type === filterType;
        return matchesType;
    });

    const resolveValue = (token: DesignToken): any => {
        if (token.ref) {
            const refPath = token.ref.replace(/[{}]/g, '');
            const target = tokens.find(t => t.path === refPath);
            return target ? resolveValue(target) : "Invalid Ref";
        }
        return token.value;
    };

    const renderTokenValue = (token: DesignToken) => {
        const value = resolveValue(token);
        const isRef = !!token.ref;

        switch (token.type) {
            case 'color':
                return (
                    <div className="flex items-center gap-2">
                        <div
                            className="w-4 h-4 rounded border border-border shrink-0"
                            style={{ backgroundColor: typeof value === 'string' ? value : 'transparent' }}
                        />
                        <code className="text-[10px] uppercase font-mono bg-muted px-1 rounded truncate max-w-[80px]">{value}</code>
                        {isRef && (
                            <span className="text-[9px] text-muted-foreground italic truncate max-w-[60px]">
                                via {token.ref}
                            </span>
                        )}
                    </div>
                );
            case 'typography':
                const typo = value as any;
                if (!typo || typeof typo !== 'object') return null;
                return (
                    <div className="flex flex-col gap-1 max-w-[200px]">
                        <span
                            className="text-sm truncate"
                            style={{
                                fontFamily: typo.fontFamily,
                                fontSize: typo.fontSize,
                                fontWeight: typo.fontWeight,
                                lineHeight: typo.lineHeight,
                                letterSpacing: typo.letterSpacing,
                                textTransform: typo.textCase as any,
                                textDecoration: typo.textDecoration
                            }}
                        >
                            Ag The quick brown fox
                        </span>
                        <div className="flex gap-2 text-[9px] text-muted-foreground font-mono">
                            <span>{typo.fontSize}</span>
                            <span>{typo.fontWeight}</span>
                        </div>
                    </div>
                );
            case 'spacing':
            case 'dimension':
                return (
                    <div className="flex items-center gap-2">
                        <div
                            className="h-2 bg-primary/20 rounded-full border border-primary/30"
                            style={{ width: typeof value === 'string' ? value : '0px', maxWidth: '60px' }}
                        />
                        <code className="text-[10px] font-mono bg-muted px-1 rounded shrink-0">{value}</code>
                    </div>
                );
            case 'border':
                const border = value as any;
                if (!border || typeof border !== 'object') return null;
                return (
                    <div className="flex items-center gap-2">
                        <div
                            className="w-8 h-4 rounded border"
                            style={{
                                borderColor: border.color,
                                borderStyle: border.style,
                                borderWidth: border.width
                            }}
                        />
                        <code className="text-[10px] font-mono bg-muted px-1 rounded shrink-0">{border.width} {border.style}</code>
                    </div>
                );
            case 'borderRadius':
                return (
                    <div className="flex items-center gap-2">
                        <div
                            className="w-4 h-4 border border-dashed border-muted-foreground/50"
                            style={{ borderRadius: typeof value === 'string' ? value : '0px' }}
                        />
                        <code className="text-[10px] font-mono bg-muted px-1 rounded shrink-0">{value}</code>
                    </div>
                );
            default:
                return (
                    <div className="flex items-center gap-2">
                        <span className="text-[10px] text-muted-foreground italic truncate max-w-[100px]">
                            {typeof value === 'object' ? 'Structured' : value}
                        </span>
                        {isRef && (
                            <span className="text-[9px] text-muted-foreground italic truncate max-w-[60px]">
                                via {token.ref}
                            </span>
                        )}
                    </div>
                );
        }
    };

    return (
        <div className="flex flex-col h-full bg-card/50 rounded-xl border border-border overflow-hidden">
            <div className="p-4 border-b space-y-4">
                <div className="flex items-center justify-between">
                    <h2 className="font-bold text-lg flex items-center gap-2">
                        Design Tokens
                        <Badge variant="secondary" className="font-mono">
                            {tokens.length}
                        </Badge>
                    </h2>
                    <Button size="sm" onClick={onAdd} className="gap-2">
                        <Plus className="h-4 w-4" />
                        Add Token
                    </Button>
                </div>

                <div className="flex gap-2">
                    <div className="relative flex-1">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                        <Input
                            placeholder="Search tokens..."
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                            className="pl-9 bg-background/50 border-border/50"
                        />
                    </div>
                    <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                            <Button variant="outline" size="icon" className="shrink-0" aria-label="Filter tokens">
                                <Filter className="h-4 w-4" />
                            </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => setFilterType("all")}>All Types</DropdownMenuItem>
                            <DropdownMenuItem onClick={() => setFilterType("color")}>Colors</DropdownMenuItem>
                            <DropdownMenuItem onClick={() => setFilterType("dimension")}>Dimensions</DropdownMenuItem>
                            <DropdownMenuItem onClick={() => setFilterType("typography")}>Typography</DropdownMenuItem>
                            {/* Add more types as needed */}
                        </DropdownMenuContent>
                    </DropdownMenu>
                </div>
            </div>

            <ScrollArea className="flex-1">
                <div className="p-0">
                    {filteredTokens.length === 0 ? (
                        <div className="p-8 text-center text-muted-foreground">
                            No tokens found matching your criteria.
                        </div>
                    ) : (
                        <Reorder.Group
                            axis="y"
                            values={filteredTokens}
                            onReorder={onReorder || (() => { })}
                            className="divide-y"
                        >
                            {filteredTokens.map((token) => (
                                <Reorder.Item
                                    key={token.path}
                                    value={token}
                                    className="p-4 hover:bg-primary/5 transition-colors group flex items-center justify-between bg-card"
                                >
                                    <div className="flex items-center gap-4 flex-1">
                                        <GripVertical className="h-4 w-4 text-muted-foreground/30 group-hover:text-muted-foreground cursor-grab active:cursor-grabbing shrink-0" />
                                        <div className="space-y-1 flex-1">
                                            <div className="flex items-center gap-2">
                                                <span className="font-semibold text-sm tracking-tight">{token.name}</span>
                                                <Badge variant="outline" className="text-[9px] h-3.5 px-1 capitalize font-bold">
                                                    {token.type}
                                                </Badge>
                                                {token.ref && (
                                                    <Badge variant="secondary" className="text-[9px] h-3.5 px-1 bg-primary/10 text-primary border-primary/20">
                                                        ALIAS
                                                    </Badge>
                                                )}
                                                {token.status && (
                                                    <Badge
                                                        variant="outline"
                                                        className={`text-[9px] h-3.5 px-1 font-bold uppercase ${token.status === 'published' ? 'bg-green-500/10 text-green-500 border-green-500/20' :
                                                            token.status === 'deprecated' ? 'bg-yellow-500/10 text-yellow-500 border-yellow-500/20' :
                                                                'bg-gray-500/10 text-gray-500 border-gray-500/20'
                                                            }`}
                                                    >
                                                        {token.status}
                                                    </Badge>
                                                )}
                                                {token.syncStatus === 'changed' && (
                                                    <Badge
                                                        variant="outline"
                                                        className="text-[9px] h-3.5 px-1 font-bold uppercase bg-orange-500/10 text-orange-500 border-orange-500/20"
                                                    >
                                                        Modified
                                                    </Badge>
                                                )}
                                            </div>
                                            <div className="flex items-center gap-4">
                                                <code className="text-[11px] text-muted-foreground">{token.path}</code>
                                                {renderTokenValue(token)}
                                            </div>
                                        </div>
                                    </div>

                                    <DropdownMenu>
                                        <DropdownMenuTrigger asChild>
                                            <Button variant="ghost" size="icon" className="h-8 w-8 opacity-0 group-hover:opacity-100 transition-opacity" aria-label="Token options">
                                                <MoreVertical className="h-4 w-4" />
                                            </Button>
                                        </DropdownMenuTrigger>
                                        <DropdownMenuContent align="end">
                                            <DropdownMenuItem onClick={() => onEdit(token)} className="gap-2">
                                                <Edit2 className="h-3 w-3" /> Edit
                                            </DropdownMenuItem>
                                            <DropdownMenuItem className="gap-2">
                                                <Copy className="h-3 w-3" /> Duplicate
                                            </DropdownMenuItem>
                                            <DropdownMenuItem
                                                onClick={() => onEdit({ ...token, status: 'deprecated' })}
                                                className="gap-2 text-yellow-600 focus:text-yellow-600"
                                            >
                                                <Filter className="h-3 w-3" /> Deprecate
                                            </DropdownMenuItem>
                                            <DropdownMenuItem
                                                onClick={() => onDelete(token.path)}
                                                className="gap-2 text-destructive focus:text-destructive"
                                            >
                                                <Trash2 className="h-3 w-3" /> Delete
                                            </DropdownMenuItem>
                                        </DropdownMenuContent>
                                    </DropdownMenu>
                                </Reorder.Item>
                            ))}
                        </Reorder.Group>
                    )}
                </div>
            </ScrollArea>
        </div>
    );
}

import { TokenList } from "./TokenList";
import { TokenEditor } from "./TokenEditor";
import { BrandSwitcher } from "../BrandSwitcher";
import { VersionManager } from "../VersionManager";
import { TokenCompareSandbox } from "./TokenCompareSandbox";
import { GovernanceDashboard } from "./GovernanceDashboard";
import { ExportButton } from "../ExportButton";
import { SemanticCopilot } from "./SemanticCopilot";
import { SpacingGrid } from "./SpacingGrid";
import { DashboardSkeleton } from "../skeletons/DashboardSkeleton";
import { TokenListSkeleton } from "../skeletons/TokenListSkeleton";
import { SandboxSkeleton } from "../skeletons/SandboxSkeleton";
import { DesignToken } from "@/types/tokens";
import { GeneratedDesignSystem } from "@/types/designSystem";
import { Button } from "@/components/ui/button";
import ErrorBoundary from "@/components/ErrorBoundary";
import {
    History,
    LayoutDashboard,
    Split,
    Shield,
    Sparkles,
    Layers
} from "lucide-react";
import { toast } from "sonner";
import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";

import { useTokens } from "@/hooks/useTokens";
import { useBrands } from "@/hooks/useBrands";
import { useSearchParams } from "react-router-dom";
import { AISuggestion } from "@/lib/ai";
import { flattenDesignSystemToTokens } from "@/lib/token-utils";

interface TokenManagementDashboardProps {
    designSystem?: GeneratedDesignSystem | null;
    designSystemId?: string;
}

export function TokenManagementDashboard({
    designSystem: propDesignSystem,
    designSystemId: propDesignSystemId
}: TokenManagementDashboardProps = {}) {
    const [searchParams] = useSearchParams();
    const urlDesignSystemId = searchParams.get("id");
    const designSystemId = propDesignSystemId || urlDesignSystemId;

    const {
        tokens: dbTokens,
        loading: isLoadingTokens,
        saveToken,
        batchSaveTokens,
        deleteToken,
        restoreToken,
        permanentlyDeleteToken
    } = useTokens(designSystemId || undefined);

    // If no DB tokens but we have a design system prop, generate tokens locally
    const tokens = dbTokens.length > 0 ? dbTokens : (propDesignSystem ? flattenDesignSystemToTokens(propDesignSystem) : []);

    const { brands } = useBrands(designSystemId || undefined);

    const [designSystem, setDesignSystem] = useState<GeneratedDesignSystem | null>(propDesignSystem || null);
    const [isLoadingDS, setIsLoadingDS] = useState(false);
    const [editingToken, setEditingToken] = useState<DesignToken | null>(null);
    const [isCreating, setIsCreating] = useState(false);
    const [activeTab, setActiveTab] = useState<"tokens" | "history" | "sandbox" | "governance" | "copilot">("tokens");
    const [localTokens, setLocalTokens] = useState<DesignToken[]>([]);
    const [showSpacingGrid, setShowSpacingGrid] = useState(false);

    useEffect(() => {
        if (designSystemId && !propDesignSystem) {
            fetchDesignSystem();
        } else if (propDesignSystem) {
            setDesignSystem(propDesignSystem);
        }
    }, [designSystemId, propDesignSystem]);

    useEffect(() => {
        if (tokens.length > 0) {
            setLocalTokens(tokens.filter(t => t.status !== 'archived'));
        }
    }, [tokens]);

    const fetchDesignSystem = async () => {
        setIsLoadingDS(true);
        const { data, error } = await supabase
            .from("design_systems")
            .select("*")
            .eq("id", designSystemId as string)
            .single();

        if (data && !error) {
            setDesignSystem(data.design_system_data as unknown as GeneratedDesignSystem);
        }
        setIsLoadingDS(false);
    };

    const handleSave = async (token: DesignToken) => {
        if (!designSystemId) {
            toast.error("Please save the design system first before modifying tokens.");
            return;
        }
        await saveToken(token);
        setEditingToken(null);
        setIsCreating(false);
    };

    const handleDelete = async (path: string) => {
        if (!designSystemId) {
            toast.error("Please save the design system first.");
            return;
        }
        await deleteToken(path);
    };

    const handleReorder = (newTokens: DesignToken[]) => {
        setLocalTokens(newTokens);
    };

    const handleApplyAISuggestion = async (suggestion: AISuggestion) => {
        try {
            if (suggestion.type === 'consolidate' || suggestion.type === 'new-token') {
                const tokensToBatch: DesignToken[] = [];
                let baseTokenValue = suggestion.value;

                if (!baseTokenValue && suggestion.tokensToAlias && suggestion.tokensToAlias.length > 0) {
                    const firstToken = tokens.find(t => t.path === suggestion.tokensToAlias![0]);
                    baseTokenValue = firstToken?.value;
                }

                if (baseTokenValue) {
                    tokensToBatch.push({
                        name: suggestion.newName.split('.').pop() || suggestion.newName,
                        path: suggestion.newName,
                        type: 'color',
                        value: baseTokenValue,
                        status: 'published'
                    });
                }

                if (suggestion.tokensToAlias) {
                    for (const path of suggestion.tokensToAlias) {
                        const token = tokens.find(t => t.path === path);
                        if (token) {
                            tokensToBatch.push({
                                ...token,
                                ref: `{${suggestion.newName}}`
                            });
                        }
                    }
                }

                if (tokensToBatch.length > 0) {
                    await batchSaveTokens(tokensToBatch);
                    toast.success(`Successfully applied AI suggestion: ${suggestion.newName}`);
                }
            }
        } catch (error) {
            toast.error("Failed to apply AI suggestion");
        }
    };

    const handleApplyAllSuggestions = async (suggestions: AISuggestion[]) => {
        if (suggestions.length === 0) return;
        toast.loading(`Applying ${suggestions.length} suggestions...`);
        try {
            const allTokensToUpdate: DesignToken[] = [];

            for (const suggestion of suggestions) {
                if (suggestion.type === 'consolidate' || suggestion.type === 'new-token') {
                    let baseTokenValue = suggestion.value;
                    if (!baseTokenValue && suggestion.tokensToAlias?.length) {
                        baseTokenValue = tokens.find(t => t.path === suggestion.tokensToAlias![0])?.value;
                    }

                    if (baseTokenValue) {
                        allTokensToUpdate.push({
                            name: suggestion.newName.split('.').pop() || suggestion.newName,
                            path: suggestion.newName,
                            type: 'color',
                            value: baseTokenValue,
                            status: 'published'
                        });
                    }

                    if (suggestion.tokensToAlias) {
                        for (const path of suggestion.tokensToAlias) {
                            const token = tokens.find(t => t.path === path);
                            if (token) {
                                allTokensToUpdate.push({
                                    ...token,
                                    ref: `{${suggestion.newName}}`
                                });
                            }
                        }
                    }
                }
            }

            if (allTokensToUpdate.length > 0) {
                await batchSaveTokens(allTokensToUpdate);
                toast.dismiss();
                toast.success(`Applied ${suggestions.length} suggestions successfully`);
            }
        } catch (error) {
            toast.dismiss();
            toast.error("Failed to apply all suggestions");
        }
    };

    if (!designSystemId && !propDesignSystem) {
        return (
            <div className="flex flex-col items-center justify-center h-full p-12 text-center space-y-4">
                <div className="p-4 bg-muted rounded-full">
                    <LayoutDashboard className="h-12 w-12 text-muted-foreground" />
                </div>
                <h2 className="text-2xl font-bold">No Design System Selected</h2>
                <p className="text-muted-foreground max-w-sm">
                    Please select or generate a design system first to manage its tokens.
                </p>
            </div>
        );
    }

    if (isLoadingDS) {
        return <DashboardSkeleton />;
    }

    return (
        <div className="flex flex-col h-screen max-h-screen overflow-hidden bg-background">
            <header className="flex items-center justify-between px-6 py-4 border-b bg-card/50 backdrop-blur-md sticky top-0 z-30">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-primary/10 rounded-lg">
                        <LayoutDashboard className="h-6 w-6 text-primary" />
                    </div>
                    <div>
                        <h1 className="text-xl font-bold tracking-tight">Token Management</h1>
                        <p className="text-xs text-muted-foreground font-medium">Configure and organize your design system foundations</p>
                    </div>
                </div>

                <div className="flex items-center gap-2">
                    <ErrorBoundary variant="mini">
                        {designSystemId && (
                            <BrandSwitcher designSystemId={designSystemId} />
                        )}
                        <div className="h-6 w-px bg-border/40 mx-2" />
                        {designSystem && (
                            <ExportButton designSystem={designSystem} tokens={tokens} />
                        )}
                    </ErrorBoundary>
                    <div className="flex bg-muted/30 p-1 rounded-lg border border-border/50">
                        <Button
                            variant={activeTab === "tokens" ? "secondary" : "ghost"}
                            size="sm"
                            className="h-8 gap-2 px-3 text-xs"
                            onClick={() => setActiveTab("tokens")}
                        >
                            <LayoutDashboard className="h-3.5 w-3.5" />
                            Tokens
                        </Button>
                        <Button
                            variant={activeTab === "sandbox" ? "secondary" : "ghost"}
                            size="sm"
                            className="h-8 gap-2 px-3 text-xs"
                            onClick={() => setActiveTab("sandbox")}
                        >
                            <Split className="h-3.5 w-3.5" />
                            Sandbox
                        </Button>
                        <Button
                            variant={activeTab === "history" ? "secondary" : "ghost"}
                            size="sm"
                            className="h-8 gap-2 px-3 text-xs"
                            onClick={() => setActiveTab("history")}
                        >
                            <History className="h-3.5 w-3.5" />
                            History
                        </Button>
                        <Button
                            variant={activeTab === "governance" ? "secondary" : "ghost"}
                            size="sm"
                            className="h-8 gap-2 px-3 text-xs"
                            onClick={() => setActiveTab("governance")}
                        >
                            <Shield className="h-3.5 w-3.5" />
                            Governance
                        </Button>
                        <Button
                            variant={activeTab === "copilot" ? "secondary" : "ghost"}
                            size="sm"
                            className="h-8 gap-2 px-3 text-xs"
                            onClick={() => setActiveTab("copilot")}
                        >
                            <Sparkles className="h-3.5 w-3.5" />
                            Copilot
                        </Button>
                    </div>
                </div>
            </header>

            <div className="flex-1 flex overflow-hidden p-6 gap-6">
                <ErrorBoundary variant="component">
                    {activeTab === "history" ? (
                        designSystemId ? (
                            <div className="w-full animate-in fade-in slide-in-from-left duration-300">
                                <VersionManager designSystemId={designSystemId} />
                            </div>
                        ) : (
                            <div className="flex items-center justify-center h-full text-muted-foreground">
                                Save the design system to view version history
                            </div>
                        )
                    ) : activeTab === "sandbox" ? (
                        isLoadingTokens ? <SandboxSkeleton /> : (
                            <div className="w-full h-full animate-in fade-in slide-in-from-bottom-4 duration-500">
                                <TokenCompareSandbox
                                    allTokens={tokens}
                                    brands={brands}
                                    onTokenClick={(path) => {
                                        const token = tokens.find(t => t.path === path);
                                        if (token) {
                                            setEditingToken(token);
                                            setActiveTab("tokens");
                                        }
                                    }}
                                />
                            </div>
                        )
                    ) : activeTab === "governance" ? (
                        designSystemId ? (
                            <div className="w-full h-full animate-in fade-in slide-in-from-right duration-500 overflow-y-auto pr-2">
                                <GovernanceDashboard
                                    tokens={tokens}
                                    designSystemId={designSystemId}
                                    onRestore={restoreToken}
                                    onPermanentDelete={permanentlyDeleteToken}
                                    onApplyAISuggestion={handleApplyAISuggestion}
                                    onApplyAllSuggestions={handleApplyAllSuggestions}
                                    onTokenClick={(path) => {
                                        const token = tokens.find(t => t.path === path);
                                        if (token) {
                                            setEditingToken(token);
                                            setActiveTab("tokens");
                                        }
                                    }}
                                />
                            </div>
                        ) : (
                            <div className="flex items-center justify-center h-full text-muted-foreground">
                                Save the design system to manage governance
                            </div>
                        )
                    ) : activeTab === "copilot" ? (
                        designSystemId ? (
                            <div className="w-full h-full animate-in fade-in slide-in-from-right duration-500 overflow-y-auto pr-2">
                                <SemanticCopilot
                                    designSystemId={designSystemId}
                                    tokens={tokens}
                                    onRefresh={() => { }}
                                />
                            </div>
                        ) : (
                            <div className="flex items-center justify-center h-full text-muted-foreground">
                                Save the design system to use Copilot
                            </div>
                        )
                    ) : (
                        <>
                            <div className={`transition-all duration-300 flex flex-col gap-6 ${editingToken || isCreating ? 'w-1/2' : 'w-full'}`}>
                                <div className="flex items-center justify-between bg-card/10 p-2 rounded-lg border border-border/50">
                                    <span className="text-[10px] font-bold uppercase tracking-wider text-muted-foreground ml-2">Visual Editors</span>
                                    <Button
                                        variant={showSpacingGrid ? "secondary" : "ghost"}
                                        size="sm"
                                        className="h-8 gap-2 text-xs"
                                        onClick={() => setShowSpacingGrid(!showSpacingGrid)}
                                    >
                                        <Layers className="h-3.5 w-3.5" />
                                        {showSpacingGrid ? "Hide Grid" : "Spacing Scale"}
                                    </Button>
                                </div>

                                {showSpacingGrid && (
                                    <div className="animate-in fade-in slide-in-from-top-4 duration-500">
                                        <SpacingGrid
                                            tokens={tokens
                                                .filter(t => t.type === 'spacing' || t.type === 'dimension')
                                                .map(t => ({ path: t.path, value: t.value as string }))
                                            }
                                            onSelect={(path: string) => {
                                                const token = tokens.find(t => t.path === path);
                                                if (token) setEditingToken(token);
                                            }}
                                        />
                                    </div>
                                )}

                                {isLoadingTokens ? (
                                    <TokenListSkeleton />
                                ) : (
                                    <TokenList
                                        tokens={localTokens}
                                        onEdit={setEditingToken}
                                        onDelete={handleDelete}
                                        onAdd={() => setIsCreating(true)}
                                        onReorder={handleReorder}
                                    />
                                )}
                            </div>

                            {(editingToken || isCreating) && (
                                <div className="w-1/2 animate-in slide-in-from-right duration-300">
                                    <TokenEditor
                                        token={editingToken || undefined}
                                        allTokens={tokens}
                                        onSave={handleSave}
                                        onCancel={() => {
                                            setEditingToken(null);
                                            setIsCreating(false);
                                        }}
                                    />
                                </div>
                            )}
                        </>
                    )}
                </ErrorBoundary>
            </div>
        </div>
    );
}

import * as React from 'react';
import { cn } from '@/lib/utils';

/**
 * Focus ring props
 */
export interface FocusRingProps {
    /** Children to wrap */
    children: React.ReactNode;
    /** Color of the focus ring */
    color?: string;
    /** Intensity of the focus ring (0-1) */
    intensity?: number;
    /** Offset from the element */
    offset?: number;
    /** Blur radius */
    blur?: number;
    /** Inset focus ring */
    inset?: boolean;
    /** Additional class name */
    className?: string;
}

/**
 * Focus ring component with CSS-only implementation for better type compatibility
 */
export const FocusRing = React.forwardRef<HTMLDivElement, FocusRingProps>(
    (
        {
            children,
            className,
            color = 'var(--primary)',
            // intensity = 0.4, // Unused
            offset = 2,
            blur = 4,
            inset = false,
        },
        ref
    ) => {
        const [isFocused, setIsFocused] = React.useState(false);

        return (
            <div
                ref={ref}
                className={cn("relative inline-flex", className)}
                onFocus={() => setIsFocused(true)}
                onBlur={() => setIsFocused(false)}
            >
                <style>{`
          .focus-ring-${offset}-${blur}:focus {
            outline: none;
          }
