/**
 * Accessible focus management hook
 */
export function useAccessibleFocus<T extends HTMLElement>(options: {
    onFocus?: () => void;
    onBlur?: () => void;
    skipFocus?: boolean;
} = {}) {
    const { onFocus, onBlur, skipFocus = false } = options;
    const elementRef = React.useRef<T>(null);
    const [isFocused, setIsFocused] = React.useState(false);

    const handleFocus = React.useCallback(() => {
        if (skipFocus) return;
        setIsFocused(true);
        onFocus?.();
    }, [onFocus, skipFocus]);

    const handleBlur = React.useCallback(() => {
        setIsFocused(false);
        onBlur?.();
    }, [onBlur]);

    const focus = React.useCallback(() => {
        elementRef.current?.focus();
    }, []);

    return {
        ref: elementRef,
        isFocused,
        focus,
        handlers: {
            onFocus: handleFocus,
            onBlur: handleBlur,
        },
    };
}

/**
 * Announce message to screen readers
 */
export function useLiveRegion(options: {
    politeness?: 'polite' | 'assertive' | 'off';
    clearAfter?: number;
} = {}) {
    const { politeness = 'polite', clearAfter = 1000 } = options;
    const [message, setMessage] = React.useState<string | null>(null);
    const timeoutRef = React.useRef<ReturnType<typeof setTimeout>>();

    const announce = React.useCallback((msg: string) => {
        setMessage(msg);

        if (timeoutRef.current) {
            clearTimeout(timeoutRef.current);
        }

        if (clearAfter > 0) {
            timeoutRef.current = setTimeout(() => {
                setMessage(null);
            }, clearAfter);
        }
    }, [clearAfter]);

    const clear = React.useCallback(() => {
        setMessage(null);
        if (timeoutRef.current) {
            clearTimeout(timeoutRef.current);
        }
    }, []);

    React.useEffect(() => {
        return () => {
            if (timeoutRef.current) {
                clearTimeout(timeoutRef.current);
            }
        };
    }, []);

    return {
        announce,
        clear,
        message,
        liveRegionProps: {
            'aria-live': politeness,
            'aria-atomic': true,
            className: "sr-only",
        } as React.HTMLAttributes<HTMLDivElement>,
    };
}

