    translateX = 0,
    translateY = 0,
    translateZ = 0,
    rotateX = 0,
    rotateY = 0,
    rotateZ = 0,
    ...rest
}: {
    as?: React.ElementType;
    children: React.ReactNode;
    className?: string;
    translateX?: number | string;
    translateY?: number | string;
    translateZ?: number | string;
    rotateX?: number | string;
    rotateY?: number | string;
    rotateZ?: number | string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}) => {
    const ref = useRef<HTMLDivElement>(null);
    const [isMouseEntered] = useMouseEnter();

    useEffect(() => {
        const handleAnimations = () => {
            if (!ref.current) return;
            if (isMouseEntered) {
                ref.current.style.transform = `translateX(${translateX}px) translateY(${translateY}px) translateZ(${translateZ}px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) rotateZ(${rotateZ}deg)`;
            } else {
                ref.current.style.transform = `translateX(0px) translateY(0px) translateZ(0px) rotateX(0deg) rotateY(0deg) rotateZ(0deg)`;
            }
        };
        handleAnimations();
    }, [isMouseEntered, translateX, translateY, translateZ, rotateX, rotateY, rotateZ]);

    return (
        <Tag
            ref={ref}
            className={cn("w-fit transition-transform duration-200 ease-linear", className)}
            {...rest}
        >
            {children}
        </Tag>
    );
};

// Create a hook to use the context
export const useMouseEnter = () => {
    const context = useContext(MouseEnterContext);
    if (context === undefined) {
        throw new Error("useMouseEnter must be used within a MouseEnterProvider");
    }
    return context;
};

import React, { useState } from 'react';
import { motion, useMotionValue, useSpring, Variants } from 'framer-motion';
import { cn } from '@/lib/utils';
import { useReducedMotion } from '@/hooks/useReducedMotion';

// Enhanced hover effect with multiple visual feedback
interface EnhancedHoverProps {
    children: React.ReactNode;
    className?: string;
    scale?: number;
    lift?: number;
    glow?: boolean;
    glowColor?: string;
    borderGlow?: boolean;
    magnetic?: boolean;
    magneticStrength?: number;
}

export function EnhancedHover({
    children,
    className,
    scale = 1.02,
    lift = -2,
    glow = false,
    glowColor = 'hsl(var(--primary) / 0.3)',
    borderGlow = false,
    magnetic = false,
    magneticStrength = 0.3,
}: EnhancedHoverProps) {
    const prefersReducedMotion = useReducedMotion();
    const [isHovered, setIsHovered] = useState(false);

    // Magnetic effect values
    const mouseX = useMotionValue(0);
    const mouseY = useMotionValue(0);

    const springX = useSpring(mouseX, { stiffness: 300, damping: 30 });
    const springY = useSpring(mouseY, { stiffness: 300, damping: 30 });

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!magnetic || prefersReducedMotion) return;

        const rect = e.currentTarget.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;

        mouseX.set((e.clientX - centerX) * magneticStrength);
        mouseY.set((e.clientY - centerY) * magneticStrength);
    };

    const handleMouseLeave = () => {
        if (!magnetic || prefersReducedMotion) return;
        mouseX.set(0);
        mouseY.set(0);
    };

    if (prefersReducedMotion) {
        return (
            <div className={className}>
                {children}
            </div>
        );
    }

    return (
        <motion.div
            className={cn('relative cursor-pointer', className)}
            style={{
                x: magnetic ? springX : 0,
                y: magnetic ? springY : 0,
            }}
            whileHover={{
                scale,
                y: lift,
                transition: {
                    type: 'spring',
                    stiffness: 400,
                    damping: 17,
                },
            }}
            onMouseMove={handleMouseMove}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => {
                setIsHovered(false);
                handleMouseLeave();
            }}
        >
            {children}

            {/* Glow effect */}
            {glow && isHovered && (
                <motion.div
                    className="absolute inset-0 rounded-lg blur-xl -z-10"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    style={{
                        background: glowColor,
                    }}
                />
            )}

            {/* Border glow effect */}
            {borderGlow && isHovered && (
                <motion.div
                    className="absolute inset-0 rounded-lg"
                    initial={{ opacity: 0, boxShadow: 'none' }}
                    animate={{
                        opacity: 1,
                        boxShadow: `0 0 20px ${glowColor}`,
                    }}
                    exit={{ opacity: 0, boxShadow: 'none' }}
                    style={{
                        filter: 'blur(1px)',
                    }}
                />
            )}
        </motion.div>
    );
}

// Enhanced button with multiple interaction states
interface EnhancedButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
    variant?: 'primary' | 'secondary' | 'outline' | 'ghost';
    size?: 'sm' | 'md' | 'lg';
    loading?: boolean;
    success?: boolean;
    error?: boolean;
    magnetic?: boolean;
    glow?: boolean;
}

export function EnhancedButton({
    children,
    className,
    variant = 'primary',
    size = 'md',
    loading = false,
    success = false,
    error = false,
    magnetic = false,
    glow = false,
    disabled,
    ...props
}: EnhancedButtonProps) {


    const baseClasses = 'relative inline-flex items-center justify-center rounded-lg font-medium transition-all focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50';

    const variantClasses = {
        primary: 'bg-primary text-primary-foreground hover:bg-primary/90 shadow-sm',
        secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        outline: 'border border-input bg-background hover:bg-accent hover:text-accent-foreground',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
    };

    const sizeClasses = {
        sm: 'h-9 px-3 text-sm',
        md: 'h-10 px-4 py-2',
        lg: 'h-11 px-8',
    };

    const buttonVariants: Variants = {
        idle: {},
        loading: {
            scale: [1, 0.98, 1],
            transition: {
                duration: 1.5,
                repeat: Infinity,
                ease: 'easeInOut',
            },
        },
        success: {
            scale: [1, 1.05, 1],
            backgroundColor: 'hsl(var(--success))',
            transition: { duration: 0.3 },
        },
        error: {
            scale: [1, 0.95, 1.05, 1],
            backgroundColor: 'hsl(var(--destructive))',
            transition: { duration: 0.4 },
        },
    };

    const getCurrentVariant = () => {
        if (error) return 'error';
        if (success) return 'success';
        if (loading) return 'loading';
        return 'idle';
    };

    return (
        <EnhancedHover
            scale={magnetic ? 1.02 : 1.01}
            lift={magnetic ? -1 : 0}
            glow={glow}
            magnetic={magnetic}
            className={cn(
                baseClasses,
                variantClasses[variant],
                sizeClasses[size],
                className
            )}
        >
            <motion.button
                variants={buttonVariants}
                animate={getCurrentVariant()}
                disabled={disabled || loading}
                {...(props as any)}
            >
                {loading && (
                    <motion.div
                        className="mr-2 h-4 w-4 animate-spin rounded-full border-2 border-current border-t-transparent"
                        animate={{ rotate: 360 }}
                        transition={{
                            duration: 1,
                            repeat: Infinity,
                            ease: 'linear',
                        }}
                    />
                )}

                {success && !loading && (
                    <motion.svg
                        className="mr-2 h-4 w-4"
                        fill="none"
                        viewBox="0 0 24 24"
                        initial={{ scale: 0, rotate: -180 }}
                        animate={{ scale: 1, rotate: 0 }}
                        transition={{ type: 'spring', stiffness: 500, damping: 25 }}
                    >
                        <path
                            stroke="currentColor"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            d="M20 6L9 17L4 12"
                        />
                    </motion.svg>
                )}

                {error && !loading && !success && (
                    <motion.svg
                        className="mr-2 h-4 w-4"
                        fill="none"
                        viewBox="0 0 24 24"
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        transition={{ type: 'spring', stiffness: 500, damping: 25 }}
                    >
                        <path
                            stroke="currentColor"
                            strokeWidth={2}
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            d="M18 6L6 18M6 6l12 12"
                        />
                    </motion.svg>
                )}

                <span className={loading ? 'opacity-70' : ''}>
                    {children}
                </span>
            </motion.button>
        </EnhancedHover>
    );
}

// Enhanced card with hover effects
interface EnhancedCardProps {
    children: React.ReactNode;
    className?: string;
    hover?: boolean;
    glow?: boolean;
    lift?: boolean;
}

export function EnhancedCard({
    children,
    className,
    hover = true,
    glow = false,
    lift = true,
}: EnhancedCardProps) {
    return (
        <EnhancedHover
            scale={hover ? 1.02 : 1}
            lift={lift ? -4 : 0}
            glow={glow}
            className={cn(
                'rounded-lg border bg-card text-card-foreground shadow-sm transition-all',
                hover && 'cursor-pointer',
                className
            )}
        >
            {children}
        </EnhancedHover>
    );
}

import * as React from "react";
import * as LabelPrimitive from "@radix-ui/react-label";
import { Slot } from "@radix-ui/react-slot";
import { Controller, ControllerProps, FieldPath, FieldValues, FormProvider, useFormContext } from "react-hook-form";

import { cn } from "@/lib/utils";
import { Label } from "@/components/ui/label";

const Form = FormProvider;

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
> = {
  name: TName;
};

const FormFieldContext = React.createContext<FormFieldContextValue>({} as FormFieldContextValue);

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  );
};

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext);
  const itemContext = React.useContext(FormItemContext);
  const { getFieldState, formState } = useFormContext();

  const fieldState = getFieldState(fieldContext.name, formState);

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>");
  }

  const { id } = itemContext;

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  };
};

type FormItemContextValue = {
  id: string;
};

const FormItemContext = React.createContext<FormItemContextValue>({} as FormItemContextValue);

const FormItem = React.forwardRef<HTMLDivElement, React.HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => {
    const id = React.useId();

    return (
      <FormItemContext.Provider value={{ id }}>
        <div ref={ref} className={cn("space-y-2", className)} {...props} />
      </FormItemContext.Provider>
    );
  },
);
FormItem.displayName = "FormItem";

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField();

  return <Label ref={ref} className={cn(error && "text-destructive", className)} htmlFor={formItemId} {...props} />;
});
FormLabel.displayName = "FormLabel";

const FormControl = React.forwardRef<React.ElementRef<typeof Slot>, React.ComponentPropsWithoutRef<typeof Slot>>(
  ({ ...props }, ref) => {
    const { error, formItemId, formDescriptionId, formMessageId } = useFormField();

    return (
      <Slot
        ref={ref}
        id={formItemId}
        aria-describedby={!error ? `${formDescriptionId}` : `${formDescriptionId} ${formMessageId}`}
        aria-invalid={!!error}
        {...props}
      />
    );
  },
);
FormControl.displayName = "FormControl";

const FormDescription = React.forwardRef<HTMLParagraphElement, React.HTMLAttributes<HTMLParagraphElement>>(
  ({ className, ...props }, ref) => {
    const { formDescriptionId } = useFormField();

    return <p ref={ref} id={formDescriptionId} className={cn("text-sm text-muted-foreground", className)} {...props} />;
  },
);
FormDescription.displayName = "FormDescription";

const FormMessage = React.forwardRef<HTMLParagraphElement, React.HTMLAttributes<HTMLParagraphElement>>(
  ({ className, children, ...props }, ref) => {
    const { error, formMessageId } = useFormField();
    const body = error ? String(error?.message) : children;

    if (!body) {
      return null;
    }

    return (
      <p ref={ref} id={formMessageId} className={cn("text-sm font-medium text-destructive", className)} {...props}>
        {body}
      </p>
    );
  },
);
FormMessage.displayName = "FormMessage";

export { useFormField, Form, FormItem, FormLabel, FormControl, FormDescription, FormMessage, FormField };

