// Glass card component
export const GlassCard = React.forwardRef<HTMLDivElement, GlassCardProps>(
  (
    {
      children,
      className,
      elevation = 'medium',
      hoverEffect = false,
      childrenClassName,
    },
    ref
  ) => {
    const elevations = {
      low: 'shadow-lg',
