// CSS glassmorphism utilities
export const glassUtilities = `
  /* Glass effect utilities */
  .glass {
    @apply bg-white/10 backdrop-blur-xl border border-white/20 shadow-xl;
  }
  
  .glass-sm {
    @apply bg-white/5 backdrop-blur-md border border-white/10;
  }
  
  .glass-lg {
    @apply bg-white/20 backdrop-blur-2xl border border-white/30 shadow-2xl;
  }
  
  .glass-hover {
    @apply transition-all duration-300;
  }
  
  .glass-hover:hover {
    @apply bg-white/20 border-white/30;
  }
  
  /* Animated gradient border */
  .gradient-border {
    @apply relative p-[1px] rounded-2xl overflow-hidden;
    background: linear-gradient(90deg, #ff0080, #7928ca, #ff0080);
    background-size: 200% 100%;
    animation: gradient-slide 3s linear infinite;
  }
  
  @keyframes gradient-slide {
    0% { background-position: 0% 0%; }
    100% { background-position: 200% 0%; }
  }
  
  /* Noise texture */
  .noise-overlay {
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E");
    opacity: 0.03;
    pointer-events: none;
  }
  
  /* Shimmer effect */
  .shimmer {
    @apply relative overflow-hidden;
  }
  
  .shimmer::after {
    content: '';
    @apply absolute inset-0;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transform: translateX(-100%);
    animation: shimmer-slide 1.5s infinite;
  }
  
  @keyframes shimmer-slide {
    100% { transform: translateX(100%); }
  }
  
  /* Glow effect */
  .glow {
    @apply relative;
  }
  
  .glow::before {
    content: '';
    @apply absolute inset-0 rounded-full;
    background: var(--glow-color, var(--primary));
    filter: blur(var(--glow-blur, 40px));
    opacity: var(--glow-opacity, 0.5);
  }
`;

import * as React from "react";
import { cn } from "@/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";

const spinnerVariants = cva(
    "animate-spin rounded-full border-2 border-current border-t-transparent",
    {
        variants: {
            size: {
                sm: "h-4 w-4",
                default: "h-6 w-6",
                lg: "h-8 w-8",
                xl: "h-12 w-12",
            },
            variant: {
                default: "text-primary",
                secondary: "text-secondary-foreground",
                muted: "text-muted-foreground",
                destructive: "text-destructive",
            },
        },
        defaultVariants: {
            size: "default",
            variant: "default",
        },
    }
);

export interface LoadingSpinnerProps
    extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof spinnerVariants> {
    /** Text to display next to the spinner */
    label?: string;
    /** Position of the label relative to spinner */
    labelPosition?: "right" | "bottom";
}

/**
 * Loading spinner component for indicating loading states
 * 
 * @example
 * <LoadingSpinner />
 * <LoadingSpinner size="lg" label="Loading..." />
 * <LoadingSpinner variant="muted" labelPosition="bottom" />
 */
const LoadingSpinner = React.forwardRef<HTMLDivElement, LoadingSpinnerProps>(
    ({ className, size, variant, label, labelPosition = "right", ...props }, ref) => {
        const isVertical = labelPosition === "bottom";

        return (
            <div
                ref={ref}
                role="status"
                aria-label={label || "Loading"}
                className={cn(
                    "inline-flex items-center gap-2",
                    isVertical && "flex-col",
                    className
                )}
                {...props}
            >
                <div
                    className={cn(spinnerVariants({ size, variant }))}
                    aria-hidden="true"
                />
                {label ? (
                    <span className={cn(
                        "text-sm",
                        variant === "muted" && "text-muted-foreground",
                        variant === "destructive" && "text-destructive"
                    )}>
                        {label}
                    </span>
                ) : (
                    <span className="sr-only">Loading</span>
                )}
            </div>
        );
    }
);
LoadingSpinner.displayName = "LoadingSpinner";

export { LoadingSpinner, spinnerVariants };

