import { animate, AnimationPlaybackControls } from 'framer-motion';

/**
 * Animation batching utilities for coordinating complex animation sequences
 */

export interface AnimationStep {
    element: HTMLElement | null;
    keyframes: any;
    options?: {
        duration?: number;
        ease?: any; // Framer motion easing type is complex, using any to allow custom and standard types
        delay?: number;
        type?: 'spring' | 'tween' | 'inertia';
        stiffness?: number;
        damping?: number;
    };
}

export interface AnimationSequence {
    steps: AnimationStep[];
    onComplete?: () => void;
    onStart?: () => void;
}

/**
 * Execute a sequence of animations in order
 */
export async function animateSequence(sequence: AnimationSequence): Promise<void> {
    const { steps, onStart, onComplete } = sequence;

    onStart?.();

    for (const step of steps) {
        if (!step.element) continue;

        await animate(
            step.element,
            step.keyframes,
            {
                duration: 0.3,
                ease: [0.4, 0, 0.2, 1] as any,
                ...step.options,
            }
        );
    }

    onComplete?.();
}

/**
 * Execute multiple animations simultaneously
 */
export function animateParallel(steps: AnimationStep[]): AnimationPlaybackControls[] {
    return steps
        .filter(step => step.element)
        .map(step =>
            animate(
                step.element!,
                step.keyframes,
                {
                    duration: 0.3,
                    ease: 'easeOut',
                    ...step.options,
                }
            )
        );
}

/**
 * Staggered animation for lists or grids
 */
export async function animateStaggered(
    elements: (HTMLElement | null)[],
    keyframes: any,
    options: {
        staggerDelay?: number;
        baseOptions?: AnimationStep['options'];
    } = {}
): Promise<void> {
    const { staggerDelay = 0.05, baseOptions = {} } = options;

    const animations = elements
        .filter(el => el)
        .map((element, index) =>
            animate(
                element!,
                keyframes,
                {
                    duration: 0.3,
                    ease: 'easeOut' as any,
                    delay: index * staggerDelay,
                    ...baseOptions,
                }
            )
        );

    await Promise.all(animations.map(animation => animation.then(() => { })));
}

/**
 * Complex form submission animation sequence
 */
export const formAnimations = {
    async submitForm(formElement: HTMLElement, buttonElement: HTMLElement) {
        const sequence: AnimationSequence = {
            steps: [
                {
                    element: buttonElement,
                    keyframes: { scale: 0.95 },
                    options: { duration: 0.1 },
                },
                {
                    element: buttonElement,
                    keyframes: { scale: 1 },
                    options: { duration: 0.2, type: 'spring', stiffness: 500 },
                },
            ],
            onStart: () => {
                // Disable form interactions
                formElement.style.pointerEvents = 'none';
            },
            onComplete: () => {
                // Re-enable form interactions
                formElement.style.pointerEvents = 'auto';
            },
        };

        await animateSequence(sequence);
    },

    async showSuccess(buttonElement: HTMLElement, successIcon: HTMLElement) {
        await animateParallel([
            {
                element: buttonElement,
                keyframes: { backgroundColor: 'hsl(var(--success))' },
                options: { duration: 0.3 },
            },
            {
                element: successIcon,
                keyframes: { scale: [0, 1.2, 1], rotate: [0, 180, 360] },
                options: { duration: 0.5, type: 'spring', stiffness: 500 },
            },
        ]);
    },

    async showError(buttonElement: HTMLElement, errorIcon: HTMLElement) {
        await animateParallel([
            {
                element: buttonElement,
                keyframes: {
                    backgroundColor: 'hsl(var(--destructive))',
                    x: [0, -5, 5, -5, 5, 0]
                },
                options: { duration: 0.6 },
            },
            {
                element: errorIcon,
                keyframes: { scale: [0, 1], rotate: [0, 90] },
                options: { duration: 0.3, type: 'spring' },
            },
        ]);
    },
};

/**
 * Page transition animations
 */
export const pageAnimations = {
    async fadeIn(contentElement: HTMLElement) {
        await animate(
            contentElement,
            { opacity: [0, 1], y: [20, 0] },
            { duration: 0.5, ease: 'easeOut' }
        );
    },

    async fadeOut(contentElement: HTMLElement) {
        await animate(
            contentElement,
            { opacity: [1, 0], y: [0, -20] },
            { duration: 0.3, ease: 'easeIn' }
        );
    },

    async slideIn(contentElement: HTMLElement, direction: 'left' | 'right' | 'up' | 'down' = 'right') {
        const directions = {
            left: { x: [-100, 0], y: 0 },
            right: { x: [100, 0], y: 0 },
            up: { x: 0, y: [-100, 0] },
            down: { x: 0, y: [100, 0] },
        };

        await animate(
            contentElement,
            {
                opacity: [0, 1],
                ...directions[direction],
            },
            { duration: 0.5, ease: 'easeOut' }
        );
    },
};

/**
 * Loading state animations
 */
export const loadingAnimations = {
    async showSkeleton(skeletonElements: HTMLElement[]) {
        await animateStaggered(
            skeletonElements,
            { opacity: [0, 1] },
            { staggerDelay: 0.1 }
        );
    },

    async hideSkeleton(skeletonElements: HTMLElement[]) {
        await animateStaggered(
            skeletonElements,
            { opacity: [1, 0] },
            { staggerDelay: 0.05 }
        );
    },

    async shimmerEffect(shimmerElement: HTMLElement) {
        await animate(
            shimmerElement,
            { x: ['-100%', '100%'] },
            {
                duration: 1.5,
                ease: 'linear',
                repeat: Infinity,
            }
        );
    },
};

/**
 * Utility to cancel all running animations
 */
export function cancelAnimations(controls: AnimationPlaybackControls[]) {
    controls.forEach(control => control.stop());
}

/**
 * Performance-aware animation executor
 */
export function createPerformanceAwareAnimator() {
    let isAnimating = false;
    const animationQueue: (() => Promise<void>)[] = [];

    const executeAnimation = async (animationFn: () => Promise<void>) => {
        if (isAnimating) {
            animationQueue.push(animationFn);
            return;
        }

        isAnimating = true;
        try {
            await animationFn();
        } finally {
            isAnimating = false;

            // Execute next animation in queue
            const nextAnimation = animationQueue.shift();
            if (nextAnimation) {
                executeAnimation(nextAnimation);
            }
        }
    };

    return {
        animate: executeAnimation,
        clearQueue: () => animationQueue.length = 0,
        get isAnimating() { return isAnimating; },
        get queueLength() { return animationQueue.length; },
    };
}

import { supabase } from "@/integrations/supabase/client";
import { monitor } from "./monitoring";

export type AuditAction = "CREATE" | "UPDATE" | "DELETE" | "PUBLISH" | "ARCHIVE" | "RESTORE";
export type EntityType = "TOKEN" | "BRAND" | "GROUP" | "SYSTEM";

export interface AuditLogEntry {
    design_system_id: string;
    action: AuditAction;
    entity_type: EntityType;
    entity_id?: string;
    old_value?: Record<string, unknown> | null;
    new_value?: Record<string, unknown> | null;
    metadata?: Record<string, unknown> | null;
}

/**
 * Records an action in the design audit logs.
 */
export const recordAuditLog = async (entry: AuditLogEntry) => {
    monitor.info(`[Audit Log] ${entry.action} on ${entry.entity_type}`, {
        entity_id: entry.entity_id,
        summary: entry.metadata?.summary
    });

    try {
        const { error } = await supabase
            .from("audit_logs" as any)
            .insert([
                {
                    design_system_id: entry.design_system_id,
                    action: entry.action,
                    entity_type: entry.entity_type,
                    entity_id: entry.entity_id,
                    old_value: entry.old_value,
                    new_value: entry.new_value,
                    metadata: entry.metadata,
                },
            ]);

        if (error) {
            monitor.warn("Audit log insert failed", { error: error.message });
            return false;
        }
        return true;
    } catch (err) {
        monitor.error("Audit log exception", err as Error);
        return false;
    }
};

import { GeneratedDesignSystem } from "@/types/designSystem";

export class ReactNativeExporter {
    static export(ds: GeneratedDesignSystem): string {
        const theme = {
            colors: { ...ds.colors },
            spacing: { ...ds.spacing },
            borderRadius: { ...ds.borderRadius },
            typography: this.formatTypography(ds.typography),
        };

        return `
import { StyleSheet } from 'react-native';

export const theme = ${JSON.stringify(theme, null, 2)};

export const useTheme = () => theme;

// Utility for creating styles with theme info
export const createStyles = (styleBuilder) => StyleSheet.create(styleBuilder(theme));
        `.trim();
    }

    private static formatTypography(typography: GeneratedDesignSystem['typography']) {
        const result: Record<string, any> = {};

        // Convert web typography to RN styles
        Object.entries(typography.sizes).forEach(([key, value]) => {
            result[key] = {
                fontSize: parseInt(String(value).replace('px', '').replace('rem', '')) * 16, // Rough conversion
                fontFamily: typography.fontFamily.body
            };
        });

        return result;
    }
}

import * as htmlToImage from 'html-to-image';
import { GeneratedDesignSystem } from "@/types/designSystem";
import { DesignToken } from "@/types/tokens";

/**
 * PDF Exporter
 */
export async function exportToPDF(ds: GeneratedDesignSystem, tokens: DesignToken[]): Promise<Blob> {
    const { jsPDF } = await import("jspdf");
    const doc = new jsPDF();
    const margin = 20;
    let y = margin;

    // Title
    doc.setFontSize(22);
    doc.setFont("helvetica", "bold");
    doc.text(ds.name, margin, y);
    y += 10;

    // Subtitle
    doc.setFontSize(10);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(100, 100, 100);
    doc.text("Design System Specification", margin, y);
    y += 15;

    // Description
    doc.setFontSize(12);
    doc.setTextColor(0, 0, 0);
    const desc = doc.splitTextToSize(ds.description || "A comprehensive design system created with DesignForge.", 170);
    doc.text(desc, margin, y);
    y += desc.length * 7 + 10;

    // Colors Section
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("Color Palette", margin, y);
    y += 10;

    const colorTokens = tokens.filter(t => (t as any).token_type === 'color' || t.type === 'color');
    doc.setFontSize(10);
    doc.setFont("helvetica", "normal");

    for (const color of colorTokens) {
        if (y > 270) {
            doc.addPage();
            y = margin;
        }

        // Draw color swatch
        doc.setDrawColor(200, 200, 200);
        doc.setFillColor(String(color.value));
        doc.rect(margin, y - 5, 8, 8, 'FD');

        doc.text(`${color.name} (${color.path})`, margin + 12, y);
        doc.setFont("courier", "normal");
        doc.text(String(color.value), margin + 110, y);
        doc.setFont("helvetica", "normal");

        y += 10;
    }

    y += 10;

    // Typography
    if (y > 250) {
        doc.addPage();
        y = margin;
    }

    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("Typography", margin, y);
    y += 10;

    const typographyData = [
        ["Font (Heading)", ds.typography.fontFamily.heading],
        ["Font (Body)", ds.typography.fontFamily.body],
        ["Scale (Base)", ds.typography.sizes.base]
    ];

    doc.setFontSize(10);
    doc.setFont("helvetica", "normal");
    typographyData.forEach(([label, value]) => {
        doc.text(label, margin, y);
        doc.text(value, margin + 40, y);
        y += 7;
    });

    return doc.output('blob');
}

